import { Suspense } from "react";
import { notFound, redirect } from "next/navigation";
import { getServerSession } from "next-auth";
import { authOptions } from "@/lib/next auth/options";
import { Role } from "@/prisma/generated/prisma/enums";
import { OwnerAttendanceClient } from "@/components/dashboard/attendance/OwnerAttendanceClient";
import { EmployeeLeaveClient } from "@/components/dashboard/attendance/EmployeeLeaveClient";
import {
  getLeaveRequests,
  getUserLeaveRequests,
} from "@/app/actions/leave-request";
import { getDailyAttendance } from "@/app/actions/attendance";
import { Loader2 } from "lucide-react";
import { prisma } from "@/prisma/prisma";

export default async function AttendancePage({
  params,
  searchParams,
}: {
  params: Promise<{ businessSlug: string }>;
  searchParams: Promise<{ date?: string }>;
}) {
  const { businessSlug } = await params;
  const { date: dateParam } = await searchParams;
  const session = await getServerSession(authOptions);

  if (!session?.user) {
    redirect("/auth/login");
  }

  // Verify access to this business
  if (session.user.businessSlug !== businessSlug) {
    // Or handle if they are owner of multiple? But schema implies 1:1 or specific relation.
    // For now strict check.
    redirect("/app");
  }

  const business = await prisma.business.findUnique({
    where: { slug: businessSlug },
  });

  if (!business) {
    notFound();
  }

  const role = session.user.role;

  if (role === Role.OWNER) {
    const requestsResult = await getLeaveRequests(business.id);
    const requests = requestsResult.success ? requestsResult.data || [] : [];

    // Parse date or default to today
    const date = dateParam ? new Date(dateParam) : new Date();
    // Validate date?

    const attendanceResult = await getDailyAttendance(business.id, date);
    const dailyAttendance = attendanceResult.success
      ? attendanceResult.data || []
      : [];

    return (
      <OwnerAttendanceClient
        businessId={business.id}
        businessSlug={businessSlug}
        leaveRequests={requests}
        initialDailyAttendance={dailyAttendance}
        currentDate={date}
      />
    );
  } else if (role === Role.EMPLOYEE) {
    const employee = await prisma.employee.findUnique({
      where: { user_id: session.user.id },
    });

    if (!employee) {
      return <div>Employee profile not found.</div>;
    }

    const requestsResult = await getUserLeaveRequests(employee.id);
    const requests = requestsResult.success ? requestsResult.data || [] : [];

    return (
      <EmployeeLeaveClient
        employeeId={employee.id}
        businessId={business.id}
        businessSlug={businessSlug}
        requests={requests}
      />
    );
  } else {
    return <div>Access Denied</div>;
  }
}

"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import { toast } from "sonner";
import { Loader2, Save, Copy, Check } from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { PageHeader } from "@/components/dashboard/PageHeader";

import {
  updateBusinessHours,
  BusinessHourInput,
} from "@/lib/server actions/business-hours";

const DAYS = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
];

interface BusinessHoursClientProps {
  initialHours: BusinessHourInput[];
  categories: string[];
  businessSlug: string;
}

const DEFAULT_HOURS = Array.from({ length: 7 }).map((_, i) => ({
  day_of_week: i,
  open_time: "09:00",
  close_time: "18:00",
  is_closed: i === 0 || i === 6 ? true : false,
  category: "GENERAL",
}));

export function BusinessHoursClient({
  initialHours,
  categories,
  businessSlug,
}: BusinessHoursClientProps) {
  const router = useRouter();
  const [isSaving, setIsSaving] = useState(false);
  const [activeCategory, setActiveCategory] = useState("GENERAL");

  const [hours, setHours] = useState<BusinessHourInput[]>(() => {
    if (initialHours.length === 0) return DEFAULT_HOURS;
    return initialHours.map((h) => ({
      day_of_week: h.day_of_week,
      open_time: h.open_time,
      close_time: h.close_time,
      is_closed: h.is_closed,
      category: h.category,
    }));
  });

  const handleTimeChange = (
    day: number,
    field: "open_time" | "close_time",
    value: string,
  ) => {
    setHours((prev) => {
      const existing = prev.find(
        (h) => h.day_of_week === day && h.category === activeCategory,
      );
      if (existing) {
        return prev.map((h) =>
          h.day_of_week === day && h.category === activeCategory
            ? { ...h, [field]: value }
            : h,
        );
      } else {
        return [
          ...prev,
          {
            day_of_week: day,
            open_time: field === "open_time" ? value : "18:00",
            close_time: field === "close_time" ? value : "09:00",
            is_closed: false,
            category: activeCategory,
          },
        ];
      }
    });
  };

  const toggleClosed = (day: number) => {
    setHours((prev) => {
      const existing = prev.find(
        (h) => h.day_of_week === day && h.category === activeCategory,
      );
      if (existing) {
        return prev.map((h) =>
          h.day_of_week === day && h.category === activeCategory
            ? { ...h, is_closed: !h.is_closed }
            : h,
        );
      } else {
        return [
          ...prev,
          {
            day_of_week: day,
            open_time: "09:00",
            close_time: "18:00",
            is_closed: true,
            category: activeCategory,
          },
        ];
      }
    });
  };

  const copyToAllDays = (sourceDay: number) => {
    const source = hours.find(
      (h) => h.day_of_week === sourceDay && h.category === activeCategory,
    );
    if (!source) return;

    setHours((prev) => {
      const others = prev.filter((h) => h.category !== activeCategory);
      const newEntries = Array.from({ length: 7 }).map((_, i) => ({
        ...source,
        day_of_week: i,
      }));
      return [...others, ...newEntries];
    });
    toast.success("Schedule copied to all days");
  };

  const saveChanges = async () => {
    setIsSaving(true);
    try {
      await updateBusinessHours(businessSlug, hours);
      toast.success("Business hours saved");
      router.refresh();
    } catch (error) {
      toast.error("Failed to save changes");
    } finally {
      setIsSaving(false);
    }
  };

  const getHourForDay = (day: number) => {
    return (
      hours.find(
        (h) => h.day_of_week === day && h.category === activeCategory,
      ) || {
        day_of_week: day,
        open_time: "09:00",
        close_time: "18:00",
        is_closed: true,
        category: activeCategory,
      }
    );
  };

  const displayCategories = [
    "GENERAL",
    ...categories.filter((c) => c !== "GENERAL"),
  ];

  return (
    <div className="flex flex-col p-4 md:p-8 bg-zinc-50/50 min-h-screen">
      <section className="flex-1 flex flex-col max-w-4xl mx-auto w-full">
        <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4 mb-8">
          <PageHeader
            title="Business Hours"
            description="Configure your operating hours for different service categories."
            className="mb-0"
          />
          <Button
            onClick={saveChanges}
            disabled={isSaving}
            className="min-w-[140px] bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg shadow-emerald-500/20 rounded-xl"
          >
            {isSaving ? (
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
            ) : (
              <Save className="mr-2 h-4 w-4" />
            )}
            Save Changes
          </Button>
        </div>

        <Tabs
          value={activeCategory}
          onValueChange={setActiveCategory}
          className="w-full"
        >
          <TabsList className="flex flex-wrap h-auto w-full justify-start gap-2 bg-transparent p-0 mb-6">
            {displayCategories.map((category) => (
              <TabsTrigger
                key={category}
                value={category}
                className="data-[state=active]:bg-emerald-600 data-[state=active]:text-white border border-zinc-200 bg-white text-zinc-600 hover:bg-zinc-50 px-6 py-2.5 rounded-full transition-all"
              >
                {category === "GENERAL" ? "General Hours" : category}
              </TabsTrigger>
            ))}
          </TabsList>

          {displayCategories.map((category) => (
            <TabsContent key={category} value={category} className="mt-0">
              <Card className="rounded-3xl border-zinc-200 shadow-sm overflow-hidden">
                <CardHeader className="bg-white border-b border-zinc-100">
                  <CardTitle className="text-xl font-bold text-zinc-900">
                    {category === "GENERAL"
                      ? "General Operating Hours"
                      : `${category} Hours`}
                  </CardTitle>
                  <CardDescription className="text-zinc-500">
                    Set the weekly schedule for{" "}
                    {category === "GENERAL" ? "general services" : category}.
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4 p-6 bg-white">
                  {DAYS.map((dayName, index) => {
                    const hour = getHourForDay(index);
                    return (
                      <div
                        key={index}
                        className={`flex flex-col sm:flex-row sm:items-center gap-4 p-4 rounded-xl border transition-all ${
                          hour.is_closed
                            ? "bg-zinc-50/50 border-zinc-100"
                            : "bg-white border-zinc-200 shadow-sm hover:border-emerald-200/50 hover:shadow-emerald-500/5"
                        }`}
                      >
                        <div className="w-40 flex items-center gap-3">
                          <Switch
                            checked={!hour.is_closed}
                            onCheckedChange={() => toggleClosed(index)}
                            className="data-[state=checked]:bg-emerald-600"
                          />
                          <span
                            className={`font-medium ${
                              hour.is_closed ? "text-zinc-400" : "text-zinc-700"
                            }`}
                          >
                            {dayName}
                          </span>
                        </div>

                        <div className="flex-1 flex items-center gap-4">
                          {hour.is_closed ? (
                            <div className="text-sm text-zinc-400 italic px-2 flex items-center gap-2">
                              <span className="w-2 h-2 rounded-full bg-zinc-300"></span>
                              Closed
                            </div>
                          ) : (
                            <>
                              <div className="flex items-center gap-3">
                                <Select
                                  value={hour.open_time}
                                  onValueChange={(v) =>
                                    handleTimeChange(index, "open_time", v)
                                  }
                                >
                                  <SelectTrigger className="w-[110px] rounded-lg border-zinc-200 focus:ring-emerald-500">
                                    <SelectValue />
                                  </SelectTrigger>
                                  <SelectContent>
                                    {generateTimeOptions().map((t) => (
                                      <SelectItem key={t} value={t}>
                                        {t}
                                      </SelectItem>
                                    ))}
                                  </SelectContent>
                                </Select>
                                <span className="text-zinc-400 font-medium">
                                  -
                                </span>
                                <Select
                                  value={hour.close_time}
                                  onValueChange={(v) =>
                                    handleTimeChange(index, "close_time", v)
                                  }
                                >
                                  <SelectTrigger className="w-[110px] rounded-lg border-zinc-200 focus:ring-emerald-500">
                                    <SelectValue />
                                  </SelectTrigger>
                                  <SelectContent>
                                    {generateTimeOptions().map((t) => (
                                      <SelectItem key={t} value={t}>
                                        {t}
                                      </SelectItem>
                                    ))}
                                  </SelectContent>
                                </Select>
                              </div>

                              <Button
                                variant="ghost"
                                size="sm"
                                className="ml-auto text-zinc-400 hover:text-emerald-600 hover:bg-emerald-50 transition-colors"
                                onClick={() => copyToAllDays(index)}
                                title="Copy to all days"
                              >
                                <Copy className="h-4 w-4" />
                              </Button>
                            </>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </CardContent>
              </Card>
            </TabsContent>
          ))}
        </Tabs>
      </section>
    </div>
  );
}

function generateTimeOptions() {
  const times = [];
  for (let i = 0; i < 24; i++) {
    const hour = i.toString().padStart(2, "0");
    times.push(`${hour}:00`);
    times.push(`${hour}:30`);
  }
  return times;
}

"use client";

import { useState, useTransition } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { toast } from "sonner";
import { ImageUpload } from "@/components/ui/image-upload";
import LocationInputGroup from "@/components/business/LocationInputGroup";
import { updateBusinessAction } from "@/lib/server actions/business-settings";
import { Loader2 } from "lucide-react";

interface BusinessSettingsFormProps {
  business: {
    slug: string;
    name: string;
    initials: string | null;
    description: string | null;
    imageUrl: string | null;
    latitude: number | null;
    longitude: number | null;
  };
}

export function BusinessSettingsForm({ business }: BusinessSettingsFormProps) {
  const [isPending, startTransition] = useTransition();

  async function onSubmit(formData: FormData) {
    startTransition(async () => {
      const result = await updateBusinessAction(business.slug, formData);
      if (result.success) {
        toast.success("Business settings saved successfully");
      } else {
        toast.error(result.error || "Something went wrong");
      }
    });
  }

  return (
    <form action={onSubmit} className="space-y-6">
      <Card className="rounded-3xl border-zinc-200/60 shadow-sm overflow-hidden">
        <CardHeader className="bg-white border-b border-zinc-100 p-6">
          <CardTitle className="text-xl font-bold text-zinc-900">
            General Information
          </CardTitle>
          <CardDescription className="text-zinc-500">
            Basic details about your business.
          </CardDescription>
        </CardHeader>
        <CardContent className="p-6 space-y-6 bg-white">
          <div className="grid gap-6 sm:grid-cols-2">
            <div className="space-y-2">
              <Label htmlFor="name" className="text-zinc-700">
                Business Name
              </Label>
              <Input
                id="name"
                name="name"
                defaultValue={business.name}
                placeholder="e.g. BeautyFeel"
                className="rounded-xl border-zinc-200 focus-visible:ring-emerald-500"
                required
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="initials" className="text-zinc-700">
                Business Initials
              </Label>
              <Input
                id="initials"
                name="initials"
                defaultValue={business.initials || ""}
                placeholder="e.g. BF"
                maxLength={2}
                required
                className="rounded-xl border-zinc-200 focus-visible:ring-emerald-500"
              />
              <p className="text-[0.8rem] text-zinc-400">
                Used as prefix for voucher codes (e.g. BF-123456)
              </p>
            </div>
          </div>

          <div className="space-y-3">
            <Label className="text-zinc-700">Business Image</Label>
            <div className="space-y-2">
              <input
                type="hidden"
                name="existingImageUrl"
                defaultValue={business.imageUrl || ""}
              />
              <ImageUpload name="imageFile" defaultValue={business.imageUrl} />
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="description" className="text-zinc-700">
              Description
            </Label>
            <Textarea
              id="description"
              name="description"
              defaultValue={business.description || ""}
              placeholder="Tell customers about your business..."
              className="rounded-xl border-zinc-200 focus-visible:ring-emerald-500 min-h-[120px] resize-none"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="slug" className="text-zinc-700">
              Business Slug
            </Label>
            <Input
              disabled
              defaultValue={business.slug}
              className="bg-zinc-50 border-zinc-200 rounded-xl text-zinc-500"
            />
            <p className="text-[0.8rem] text-zinc-400">
              This is your unique identifier used in the URL. Only admins can
              change this.
            </p>
          </div>
        </CardContent>
      </Card>

      <Card className="rounded-3xl border-zinc-200/60 shadow-sm overflow-hidden">
        <CardHeader className="bg-white border-b border-zinc-100 p-6">
          <CardTitle className="text-xl font-bold text-zinc-900">
            Location
          </CardTitle>
          <CardDescription className="text-zinc-500">
            Pin your business location on the map. This helps customers find
            you.
          </CardDescription>
        </CardHeader>
        <CardContent className="p-0">
          <div className="p-6 bg-white">
            <LocationInputGroup
              initialLat={business.latitude}
              initialLng={business.longitude}
              businessSlug={business.slug}
            />
          </div>
        </CardContent>
      </Card>

      <div className="flex justify-end pt-4">
        <Button
          type="submit"
          size="lg"
          disabled={isPending}
          className="bg-emerald-600 hover:bg-emerald-700 text-white rounded-xl shadow-lg shadow-emerald-500/20 px-8 min-w-[140px]"
        >
          {isPending ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Saving...
            </>
          ) : (
            "Save Changes"
          )}
        </Button>
      </div>
    </form>
  );
}

import { prisma } from "@/prisma/prisma";
import { notFound } from "next/navigation";
import { CustomersClient } from "@/components/dashboard/customers/CustomersClient";

export default async function CustomersPage({
  params,
  searchParams,
}: {
  params: Promise<{ businessSlug: string }>;
  searchParams: Promise<{ [key: string]: string | string[] | undefined }>;
}) {
  const { businessSlug } = await params;

  const business = await prisma.business.findUnique({
    where: { slug: businessSlug },
  });

  if (!business) {
    notFound();
  }

  // Fetch customers
  // In a real app we might want pagination here, but for now fetch all (or limit to recently active)
  const customers = await prisma.customer.findMany({
    where: {
      business_id: business.id,
    },
    orderBy: {
      name: "asc",
    },
  });

  return <CustomersClient customers={customers} businessSlug={businessSlug} />;
}

"use client";

import { useState, useMemo, useOptimistic, useTransition } from "react";
import { useRouter } from "next/navigation";
import {
  Employee,
  User,
  EmployeeAttendance,
  Payslip,
} from "@/prisma/generated/prisma/client";
import { PageHeader } from "@/components/dashboard/PageHeader";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Search, Plus, Users, RefreshCcw } from "lucide-react";
import {
  createEmployeeAction,
  updateEmployeeAction,
  deleteEmployeeAction,
  resetEmployeePasswordAction,
} from "@/lib/server actions/employees";
import { toast } from "sonner";
import { EmployeeList } from "./EmployeeList";

type EmployeeWithDetails = Employee & {
  user: User;
  attendance: EmployeeAttendance[];
  payslips: Payslip[];
  _count: {
    served_services: number;
  };
};

interface EmployeesPageClientProps {
  employees: EmployeeWithDetails[];
  businessSlug: string;
  categories: string[];
}

interface EmployeeFormData {
  name: string;
  email: string;
  daily_rate: string;
  commission_percentage: string;
  specialties: string[];
}

const initialFormData: EmployeeFormData = {
  name: "",
  email: "",
  daily_rate: "",
  commission_percentage: "",
  specialties: [],
};

type OptimisticAction =
  | {
      type: "update";
      employeeId: number;
      data: {
        name: string;
        email: string;
        daily_rate: number;
        commission_percentage: number;
        specialties: string[];
      };
    }
  | { type: "delete"; employeeId: number }
  | { type: "add"; employee: EmployeeWithDetails };

export function EmployeesPageClient({
  employees,
  businessSlug,
  categories,
}: EmployeesPageClientProps) {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isResetPasswordDialogOpen, setIsResetPasswordDialogOpen] =
    useState(false);
  const [selectedEmployee, setSelectedEmployee] =
    useState<EmployeeWithDetails | null>(null);
  const [formData, setFormData] = useState<EmployeeFormData>(initialFormData);
  const [newPassword, setNewPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isPending, startTransition] = useTransition();

  // Optimistic state for instant UI updates
  const [optimisticEmployees, addOptimisticUpdate] = useOptimistic(
    employees,
    (state: EmployeeWithDetails[], action: OptimisticAction) => {
      switch (action.type) {
        case "update":
          return state.map((emp) =>
            emp.id === action.employeeId
              ? {
                  ...emp,
                  daily_rate: action.data.daily_rate,
                  commission_percentage: action.data.commission_percentage,
                  specialties: action.data.specialties,
                  user: {
                    ...emp.user,
                    name: action.data.name,
                    email: action.data.email,
                  },
                }
              : emp,
          );
        case "delete":
          return state.filter((emp) => emp.id !== action.employeeId);
        default:
          return state;
      }
    },
  );

  const filteredEmployees = useMemo(() => {
    return optimisticEmployees.filter((emp) => {
      const matchesSearch =
        emp.user.name.toLowerCase().includes(search.toLowerCase()) ||
        emp.user.email.toLowerCase().includes(search.toLowerCase());
      return matchesSearch;
    });
  }, [optimisticEmployees, search]);

  const handleAddEmployee = async () => {
    if (!formData.name || !formData.email) {
      toast.error("Please fill in required fields");
      return;
    }

    setIsLoading(true);
    const result = await createEmployeeAction({
      name: formData.name,
      email: formData.email,
      daily_rate: parseFloat(formData.daily_rate) || 0,
      commission_percentage: parseFloat(formData.commission_percentage) || 0,
      specialties: formData.specialties,
    });

    setIsLoading(false);
    if (result.success) {
      if ("warning" in result && result.warning) {
        toast.warning(result.warning);
      } else {
        toast.success("Employee created. Temporary password sent via email.");
      }
      setIsAddDialogOpen(false);
      setFormData(initialFormData);
      router.refresh();
    } else {
      toast.error(result.error || "Failed to create employee");
    }
  };

  const handleEditEmployee = async () => {
    if (!selectedEmployee || !formData.name || !formData.email) {
      toast.error("Please fill in required fields");
      return;
    }

    const updateData = {
      name: formData.name,
      email: formData.email,
      daily_rate: parseFloat(formData.daily_rate) || 0,
      commission_percentage: parseFloat(formData.commission_percentage) || 0,
      specialties: formData.specialties,
    };

    // Close dialog immediately for better UX
    setIsEditDialogOpen(false);

    // Apply optimistic update and call server action
    startTransition(async () => {
      // Apply optimistic update
      addOptimisticUpdate({
        type: "update",
        employeeId: selectedEmployee.id,
        data: updateData,
      });

      const result = await updateEmployeeAction(
        selectedEmployee.id,
        updateData,
      );

      if (result.success) {
        toast.success("Employee updated successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to update employee");
        // Refresh to revert to server state on error
        router.refresh();
      }

      setSelectedEmployee(null);
      setFormData(initialFormData);
    });
  };

  const handleDeleteEmployee = async (employeeId: number) => {
    // Apply optimistic update and call server action
    startTransition(async () => {
      // Optimistically remove from UI
      addOptimisticUpdate({
        type: "delete",
        employeeId,
      });

      const result = await deleteEmployeeAction(employeeId);
      if (result.success) {
        toast.success("Employee deleted successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to delete employee");
        // Refresh to revert to server state on error
        router.refresh();
      }
    });
  };

  const handleResetPassword = async () => {
    if (!selectedEmployee || !newPassword) {
      toast.error("Please enter a new password");
      return;
    }

    if (newPassword.length < 6) {
      toast.error("Password must be at least 6 characters");
      return;
    }

    setIsLoading(true);
    const result = await resetEmployeePasswordAction(
      selectedEmployee.id,
      newPassword,
    );

    setIsLoading(false);
    if (result.success) {
      toast.success("Password reset successfully");
      setIsResetPasswordDialogOpen(false);
      setSelectedEmployee(null);
      setNewPassword("");
    } else {
      toast.error(result.error || "Failed to reset password");
    }
  };

  const openEditDialog = (employee: EmployeeWithDetails) => {
    setSelectedEmployee(employee);
    setFormData({
      name: employee.user.name,
      email: employee.user.email,
      daily_rate: employee.daily_rate.toString(),
      commission_percentage: employee.commission_percentage.toString(),
      specialties: employee.specialties ?? [],
    });
    setIsEditDialogOpen(true);
  };

  const openResetPasswordDialog = (employee: EmployeeWithDetails) => {
    setSelectedEmployee(employee);
    setNewPassword("");
    setIsResetPasswordDialogOpen(true);
  };

  return (
    <div className="flex flex-col p-4 md:p-8 bg-zinc-50/50 min-h-screen">
      <section className="flex-1 flex flex-col max-w-7xl mx-auto w-full">
        <PageHeader
          title="Employees"
          description="Manage your team members and their information"
          className="mb-8"
        >
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button className="shadow-lg shadow-emerald-500/20 bg-emerald-600 hover:bg-emerald-700 text-white transition-all active:scale-95 rounded-full px-6">
                <Plus className="h-4 w-4 mr-2" strokeWidth={2.5} />
                <span className="font-semibold">Add Employee</span>
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[500px] rounded-3xl p-0 gap-0 overflow-hidden">
              <DialogHeader className="p-6 pb-2">
                <DialogTitle className="text-xl font-bold text-zinc-900">
                  Add New Employee
                </DialogTitle>
                <DialogDescription className="text-zinc-500">
                  Create a new employee account. They will receive a temporary
                  password via email and be required to change it.
                </DialogDescription>
              </DialogHeader>
              <div className="px-6 py-4">
                <EmployeeForm
                  formData={formData}
                  setFormData={setFormData}
                  categories={categories}
                />
              </div>
              <DialogFooter className="p-6 pt-2 bg-zinc-50/50 border-t border-zinc-100">
                <Button
                  variant="outline"
                  onClick={() => setIsAddDialogOpen(false)}
                  className="rounded-xl"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleAddEmployee}
                  disabled={isLoading}
                  className="rounded-xl bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg shadow-emerald-500/20"
                >
                  {isLoading ? "Creating..." : "Create Employee"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </PageHeader>

        <div className="flex flex-col sm:flex-row gap-4 mb-8 items-start sm:items-center justify-between">
          <div className="relative flex-1 w-full max-w-md">
            <Search className="absolute left-3.5 top-3 h-4 w-4 text-zinc-400" />
            <Input
              placeholder="Search employees..."
              className="pl-10 h-10 rounded-xl border-zinc-200 bg-white shadow-sm focus-visible:ring-emerald-500"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>

          <Button
            variant="outline"
            size="sm"
            className="h-10 gap-2 rounded-xl border-zinc-200 bg-white text-zinc-600 hidden md:flex"
            onClick={() => router.refresh()}
          >
            <RefreshCcw className="h-4 w-4" />
            <span>Refresh</span>
          </Button>
        </div>

        {/* Content */}
        <div className="flex-1">
          {filteredEmployees.length === 0 ? (
            <div className="bg-white rounded-3xl border border-zinc-200/60 shadow-sm p-12 flex flex-col items-center justify-center text-center">
              <div className="h-12 w-12 bg-zinc-50 rounded-full flex items-center justify-center mb-4">
                <Users className="h-6 w-6 text-zinc-300" />
              </div>
              <h3 className="text-lg font-semibold text-zinc-900 mb-1">
                No employees found
              </h3>
              <p className="text-zinc-500 max-w-sm mb-6">
                {employees.length === 0
                  ? "Get started by adding your first team member to manage services."
                  : "Try adjusting your search terms."}
              </p>
              {employees.length === 0 && (
                <Button
                  onClick={() => setIsAddDialogOpen(true)}
                  className="rounded-full bg-emerald-600 hover:bg-emerald-700 text-white"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Add Your First Employee
                </Button>
              )}
            </div>
          ) : (
            <EmployeeList
              employees={filteredEmployees}
              onEdit={openEditDialog}
              onDelete={handleDeleteEmployee}
              onResetPassword={openResetPasswordDialog}
            />
