              >
                Cancel
              </Button>
              <Button
                onClick={handleResetPassword}
                disabled={isLoading}
                className="rounded-xl bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg shadow-emerald-500/20"
              >
                {isLoading ? "Resetting..." : "Reset Password"}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </section>
    </div>
  );
}

// Employee Form Component
function EmployeeForm({
  formData,
  setFormData,
  categories,
}: {
  formData: EmployeeFormData;
  setFormData: (data: EmployeeFormData) => void;
  categories: string[];
}) {
  const isGeneralist = formData.specialties.length === 0;

  const toggleSpecialty = (category: string) => {
    const exists = formData.specialties.includes(category);
    const updated = exists
      ? formData.specialties.filter((item) => item !== category)
      : [...formData.specialties, category];
    setFormData({ ...formData, specialties: updated });
  };

  return (
    <div className="grid gap-4">
      <div className="grid gap-2">
        <Label htmlFor="name">
          Full Name <span className="text-red-500">*</span>
        </Label>
        <Input
          id="name"
          placeholder="e.g., John Doe"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          className="h-10 rounded-xl"
        />
      </div>

      <div className="grid gap-2">
        <Label htmlFor="email">
          Email Address <span className="text-red-500">*</span>
        </Label>
        <Input
          id="email"
          type="email"
          placeholder="john@example.com"
          value={formData.email}
          onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          className="h-10 rounded-xl"
        />
      </div>

      <div className="grid gap-2">
        <Label>Specialties</Label>
        <div className="flex flex-wrap gap-2">
          <Button
            type="button"
            size="sm"
            variant={isGeneralist ? "default" : "outline"}
            onClick={() => setFormData({ ...formData, specialties: [] })}
            className="rounded-full"
          >
            Generalist (All)
          </Button>
          {categories.map((category) => {
            const isSelected = formData.specialties.includes(category);
            return (
              <Button
                key={category}
                type="button"
                size="sm"
                variant={isSelected ? "default" : "outline"}
                onClick={() => toggleSpecialty(category)}
                className="rounded-full capitalize"
              >
                {category}
              </Button>
            );
          })}
        </div>
        <p className="text-xs text-muted-foreground">
          Select specialties or choose Generalist to allow all services.
        </p>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div className="grid gap-2">
          <Label htmlFor="daily_rate">Daily Rate (PHP)</Label>
          <Input
            id="daily_rate"
            type="number"
            min="0"
            step="0.01"
            placeholder="0.00"
            value={formData.daily_rate}
            onChange={(e) =>
              setFormData({ ...formData, daily_rate: e.target.value })
            }
            className="h-10 rounded-xl"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="commission">Commission (%)</Label>
          <Input
            id="commission"
            type="number"
            min="0"
            max="100"
            step="0.1"
            placeholder="0"
            value={formData.commission_percentage}
            onChange={(e) =>
              setFormData({
                ...formData,
                commission_percentage: e.target.value,
              })
            }
            className="h-10 rounded-xl"
          />
        </div>
      </div>
    </div>
  );
}

import { AppSidebar } from "@/components/dashboard/AppSidebar";
import {
  SidebarInset,
  SidebarProvider,
  SidebarTrigger,
} from "@/components/ui/sidebar";
import { SidebarSkeleton } from "@/components/dashboard/SidebarSkeleton";
import React from "react";
import { Metadata } from "next";
import { prisma } from "@/prisma/prisma";
import { getServerSession } from "next-auth";
import { authOptions } from "@/lib/next auth/options";

import { connection } from "next/server";

type Props = {
  children: React.ReactNode;
  modal: React.ReactNode;
  params: Promise<{ businessSlug: string }>;
};

export async function generateMetadata({ params }: Props): Promise<Metadata> {
  const { businessSlug } = await params;

  const business = await prisma.business.findUnique({
    where: { slug: businessSlug },
    select: { name: true },
  });

  return {
    title: business?.name || "Dashboard",
    robots: {
      index: false,
      follow: false,
    },
  };
}

async function AppLayoutContent({
  children,
  modal,
}: {
  children: React.ReactNode;
  modal: React.ReactNode;
}) {
  await connection();
  const session = await getServerSession(authOptions);
  const isEmployee = session?.user?.role === "EMPLOYEE";

  if (isEmployee) {
    return (
      <div className="bg-background min-h-screen ">
        {children}
        {modal}
      </div>
    );
  }

  return (
    <div className="bg-background">
      <SidebarProvider>
        <React.Suspense fallback={<SidebarSkeleton />}>
          <AppSidebar />
        </React.Suspense>
        <SidebarInset>
          <header className="flex h-14 items-center gap-2 border-b bg-white px-4 lg:hidden">
            <SidebarTrigger className="h-8 w-8" />
            <span className="font-semibold">Menu</span>
          </header>
          <div className="flex-1 min-h-0 overflow-hidden">
            {children}
            {modal}
          </div>
        </SidebarInset>
      </SidebarProvider>
    </div>
  );
}

export default function AppLayout({
  children,
  modal,
}: {
  children: React.ReactNode;
  modal: React.ReactNode;
}) {
  return (
    <React.Suspense fallback={<SidebarSkeleton />}>
      <AppLayoutContent children={children} modal={modal} />
    </React.Suspense>
  );
}

