"use client";

import { useState, useEffect, useMemo, memo, useCallback } from "react";
import { ServiceItem } from "./ServiceItem";
import { Service, ServicePackage } from "@/prisma/generated/prisma/client";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { toast } from "sonner";
import {
  createPackageAction,
  updatePackageAction,
} from "@/lib/server actions/packages";
import { Clock, PhilippinePeso, Search } from "lucide-react";
import { cn } from "@/lib/utils";

type ServicePackageWithItems = ServicePackage & {
  items: {
    service: Service;
    custom_price: number;
    service_id: number;
  }[];
};

interface PackageFormProps {
  services: Service[];
  categories: string[];
  businessSlug: string;
  initialData?: ServicePackageWithItems;
  onSuccess: () => void;
}

const CategorySelector = memo(
  ({
    value,
    categories,
    onChange,
  }: {
    value: string;
    categories: string[];
    onChange: (value: string) => void;
  }) => {
    return (
      <Select value={value} onValueChange={onChange}>
        <SelectTrigger>
          <SelectValue placeholder="Select category" />
        </SelectTrigger>
        <SelectContent>
          {categories.map((cat) => (
            <SelectItem key={cat} value={cat}>
              {cat}
            </SelectItem>
          ))}
          <SelectItem value="new">+ Create New Category</SelectItem>
        </SelectContent>
      </Select>
    );
  },
);
CategorySelector.displayName = "CategorySelector";

export function PackageForm({
  services,
  categories,
  businessSlug,
  initialData,
  onSuccess,
}: PackageFormProps) {
  const [formData, setFormData] = useState({
    name: initialData?.name || "",
    description: initialData?.description || "",
    category: initialData?.category || "",
  });

  const [selectedItems, setSelectedItems] = useState<Map<number, number>>(
    () => {
      if (initialData?.items) {
        const map = new Map();
        initialData.items.forEach((item) => {
          map.set(item.service_id, item.custom_price);
        });
        return map;
      }
      return new Map();
    },
  );

  const [serviceSearch, setServiceSearch] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [newCategory, setNewCategory] = useState("");

  const filteredServices = useMemo(() => {
    return services.filter(
      (s) =>
        s.name.toLowerCase().includes(serviceSearch.toLowerCase()) ||
        s.category.toLowerCase().includes(serviceSearch.toLowerCase()),
    );
  }, [services, serviceSearch]);

  const totalValue = useMemo(() => {
    let sum = 0;
    selectedItems.forEach((price) => {
      sum += price;
    });
    return sum;
  }, [selectedItems]);

  const originalTotalValue = useMemo(() => {
    let sum = 0;
    selectedItems.forEach((_, serviceId) => {
      const service = services.find((s) => s.id === serviceId);
      if (service) {
        sum += service.price;
      }
    });
    return sum;
  }, [selectedItems, services]);

  const totalDuration = useMemo(() => {
    let duration = 0;
    selectedItems.forEach((_, serviceId) => {
      const service = services.find((s) => s.id === serviceId);
      if (service) {
        duration += service.duration || 0;
      }
    });
    return duration;
  }, [selectedItems, services]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.category) {
      toast.error("Please fill in required fields");
      return;
    }

    if (selectedItems.size === 0) {
      toast.error("Please select at least one service");
      return;
    }

    setIsLoading(true);

    const category = newCategory || formData.category;
    const items = Array.from(selectedItems.entries()).map(
      ([serviceId, price]) => ({
        serviceId,
        customPrice: price,
      }),
    );

    try {
      let result;

      if (initialData) {
        result = await updatePackageAction(initialData.id, {
          name: formData.name,
          description: formData.description || undefined,
          price: totalValue,
          duration: totalDuration,
          category,
          items,
        });
      } else {
        result = await createPackageAction({
          name: formData.name,
          description: formData.description || undefined,
          price: totalValue,
          duration: totalDuration,
          category,
          businessSlug,
          items,
        });
      }

      if (result.success) {
        toast.success(
          `Package ${initialData ? "updated" : "created"} successfully`,
        );
        onSuccess();
      } else {
        toast.error(result.error || "Failed to save package");
      }
    } catch (error) {
      toast.error("An unexpected error occurred");
    } finally {
      setIsLoading(false);
    }
  };

  const toggleService = useCallback((service: Service) => {
    setSelectedItems((prev) => {
      const newMap = new Map(prev);
      if (newMap.has(service.id)) {
        newMap.delete(service.id);
      } else {
        newMap.set(service.id, service.price);
      }
      return newMap;
    });
  }, []);

  const updateItemPrice = useCallback((serviceId: number, price: number) => {
    setSelectedItems((prev) => {
      const newMap = new Map(prev);
      if (newMap.has(serviceId)) {
        newMap.set(serviceId, price);
      }
      return newMap;
    });
  }, []);

  const handleCategoryChange = useMemo(
    () => (value: string) => {
      if (value === "new") {
        setFormData((prev) => ({ ...prev, category: "new" }));
      } else {
        setFormData((prev) => ({ ...prev, category: value }));
        setNewCategory("");
      }
    },
    [],
  );

  return (
    <form onSubmit={handleSubmit} className="flex flex-col h-[600px] md:h-auto">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 flex-1 overflow-y-auto px-1">
        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="name">
              Package Name <span className="text-destructive">*</span>
            </Label>
            <Input
              id="name"
              placeholder="e.g. Summer Spa Special"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              placeholder="Describe what's included..."
              value={formData.description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              rows={3}
              className="resize-none"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="category">
              Category <span className="text-destructive">*</span>
            </Label>
            <CategorySelector
              value={formData.category}
              categories={categories}
              onChange={handleCategoryChange}
            />
            {(formData.category === "new" || categories.length === 0) && (
              <Input
                placeholder="Enter new category name"
                value={newCategory}
                onChange={(e) => {
                  setNewCategory(e.target.value);
                  setFormData({ ...formData, category: "new" });
                }}
                className="mt-2"
              />
            )}
          </div>

          <div className="p-4 bg-zinc-50 rounded-lg border space-y-3">
            <Label>Package Summary</Label>
            <div className="space-y-2">
              <div className="flex justify-between items-center text-sm">
                <span className="text-muted-foreground">
                  Included Services:
                </span>
                <span className="font-medium">{selectedItems.size}</span>
              </div>
              <div className="flex justify-between items-center text-sm">
                <span className="text-muted-foreground">Original Value:</span>
                <span className="font-medium">
                  {originalTotalValue.toLocaleString()}
                </span>
              </div>
              <div className="flex justify-between items-center text-lg font-bold pt-2 border-t">
                <span>Total Package Price:</span>
                <span className="text-primary">
                  {totalValue.toLocaleString()}
                </span>
              </div>
              {originalTotalValue > totalValue && (
                <div className="flex justify-end pt-1">
                  <Badge
                    variant="outline"
                    className="border-green-200 bg-green-50 text-green-700"
                  >
                    Save {(originalTotalValue - totalValue).toLocaleString()}
                  </Badge>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex flex-col h-[400px] md:h-full border rounded-lg overflow-hidden bg-zinc-50/50">
          <div className="p-3 border-b bg-white space-y-2">
            <div className="flex justify-between items-center">
              <Label>Select Services Included</Label>
              <span className="text-xs text-muted-foreground font-medium">
                Customize prices below
              </span>
            </div>
            <div className="relative">
              <Search className="absolute left-2.5 top-1/2 -translate-y-1/2 h-3.5 w-3.5 text-muted-foreground" />
              <Input
                placeholder="Search services..."
                className="h-8 pl-8 text-xs bg-zinc-50"
                value={serviceSearch}
                onChange={(e) => setServiceSearch(e.target.value)}
              />
            </div>
          </div>

          <div className="flex-1 bg-white max-h-[50vh] overflow-y-auto custom-scrollbar">
            <div className="divide-y">
              {filteredServices.length === 0 ? (
                <div className="p-8 text-center text-sm text-muted-foreground">
                  No services found
                </div>
              ) : (
                filteredServices.map((service) => (
                  <ServiceItem
                    key={service.id}
                    service={service}
                    isSelected={selectedItems.has(service.id)}
                    customPrice={selectedItems.get(service.id)}
                    onToggle={toggleService}
                    onPriceUpdate={updateItemPrice}
                  />
                ))
              )}
            </div>
          </div>

          <div className="p-3 border-t bg-white flex justify-between items-center text-xs text-muted-foreground">
            <span>Total Duration:</span>
            <span className="font-medium text-foreground">
              {totalDuration} mins
            </span>
          </div>
        </div>
      </div>

      <div className="flex justify-end gap-3 pt-6 mt-2 border-t">
        <Button
          type="button"
          variant="outline"
          onClick={() => onSuccess()}
          disabled={isLoading}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          disabled={isLoading}
          className="bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg shadow-emerald-500/20"
        >
          {isLoading
            ? "Saving..."
            : initialData
              ? "Update Package"
              : "Create Package"}
        </Button>
      </div>
    </form>
  );
}

import { ServicePackage, Service } from "@/prisma/generated/prisma/client";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Clock, MoreHorizontal, Pencil, Trash2, Package } from "lucide-react";
import { formatCurrency } from "@/lib/utils";
import { cn } from "@/lib/utils";
import { Card, CardContent } from "@/components/ui/card";

type ServicePackageWithItems = ServicePackage & {
  items: {
    service: Service;
    custom_price: number;
    service_id: number;
  }[];
};

interface PackageListProps {
  packages: ServicePackageWithItems[];
  onEdit: (pkg: ServicePackageWithItems) => void;
  onDelete: (pkgId: number) => void;
}

export function PackageList({ packages, onEdit, onDelete }: PackageListProps) {
  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      Hair: "bg-purple-100 text-purple-700 border-purple-200",
      Nails: "bg-pink-100 text-pink-700 border-pink-200",
      Facial: "bg-green-100 text-green-700 border-green-200",
      Massage: "bg-blue-100 text-blue-700 border-blue-200",
      Makeup: "bg-orange-100 text-orange-700 border-orange-200",
      default: "bg-zinc-100 text-zinc-700 border-zinc-200",
    };
    return colors[category] || colors.default;
  };

  return (
    <>
      {/* Mobile Card View */}
      <div className="md:hidden grid grid-cols-1 gap-4">
        {packages.map((pkg) => (
          <div
            key={pkg.id}
            className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-3 transition-all active:scale-[0.99] active:bg-zinc-50/50"
          >
            <div className="flex justify-between items-start gap-4">
              <div className="flex-1 min-w-0">
                <div className="flex items-center gap-2 mb-1">
                  <h3 className="font-semibold text-zinc-900 truncate">
                    {pkg.name}
                  </h3>
                  <Badge
                    variant="secondary"
                    className={cn(
                      "text-[10px] px-1.5 py-0 h-5 font-medium border-0",
                      getCategoryColor(pkg.category),
                    )}
                  >
                    {pkg.category}
                  </Badge>
                </div>
                {pkg.description && (
                  <p className="text-xs text-zinc-500 line-clamp-2 mb-2">
                    {pkg.description}
                  </p>
                )}
                <div className="flex flex-wrap gap-1">
                  {pkg.items.slice(0, 3).map((item) => (
                    <Badge
                      key={item.service_id}
                      variant="outline"
                      className="text-[10px] h-5 font-normal bg-zinc-50/50"
                    >
                      {item.service.name}
                    </Badge>
                  ))}
                  {pkg.items.length > 3 && (
                    <Badge
                      variant="outline"
                      className="text-[10px] h-5 font-normal bg-zinc-50/50 text-zinc-500"
                    >
                      +{pkg.items.length - 3} more
                    </Badge>
                  )}
                </div>
              </div>

              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="-mr-2 h-8 w-8 text-zinc-400 hover:text-zinc-600"
                  >
                    <MoreHorizontal className="h-4 w-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent
                  align="end"
                  className="w-[160px] rounded-xl"
                >
                  <DropdownMenuItem onClick={() => onEdit(pkg)}>
                    <Pencil className="mr-2 h-3.5 w-3.5" />
                    Edit
                  </DropdownMenuItem>
                  <DropdownMenuItem
                    className="text-red-600 focus:text-red-700"
                    onClick={() => onDelete(pkg.id)}
                  >
                    <Trash2 className="mr-2 h-3.5 w-3.5" />
                    Delete
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>

            <div className="flex items-center justify-between pt-2 border-t border-zinc-50 mt-1">
              <div className="flex items-center gap-1.5 text-zinc-500">
                <Clock className="h-3.5 w-3.5" />
                <span className="text-xs font-medium">
                  {pkg.duration ? `${pkg.duration} mins` : ""}
                </span>
              </div>
              <span className="font-bold text-zinc-900">
                {formatCurrency(pkg.price)}
              </span>
            </div>
          </div>
        ))}
      </div>

      {/* Desktop Table View */}
      <div className="hidden md:block rounded-3xl border border-zinc-100 overflow-hidden shadow-sm bg-white">
        <Table>
          <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
            <TableRow className="hover:bg-transparent border-zinc-100">
              <TableHead className="w-[250px] font-semibold text-zinc-500 pl-6 h-12">
                Package Name
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Included Services
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Category
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Duration
              </TableHead>
              <TableHead className="text-right font-semibold text-zinc-500 h-12">
                Price
              </TableHead>
              <TableHead className="text-right w-[100px] font-semibold text-zinc-500 pr-6 h-12">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {packages.map((pkg) => (
              <TableRow
                key={pkg.id}
                className="hover:bg-zinc-50/50 transition-colors border-zinc-100 group"
              >
                <TableCell className="pl-6 py-4">
                  <div className="flex flex-col gap-1">
                    <span className="font-semibold text-zinc-900">
                      {pkg.name}
                    </span>
                    {pkg.description && (
                      <span className="text-xs text-zinc-500 line-clamp-1 max-w-[200px]">
                        {pkg.description}
                      </span>
                    )}
                  </div>
                </TableCell>
                <TableCell className="py-4">
                  <div className="flex flex-wrap gap-1 max-w-[300px]">
                    {pkg.items.slice(0, 3).map((item) => (
                      <Badge
                        key={item.service_id}
                        variant="outline"
                        className="text-[10px] h-5 font-normal bg-white"
                      >
                        {item.service.name}
                      </Badge>
                    ))}
                    {pkg.items.length > 3 && (
                      <Badge
                        variant="outline"
                        className="text-[10px] h-5 font-normal text-zinc-500 bg-zinc-50"
                      >
                        +{pkg.items.length - 3} more
                      </Badge>
                    )}
                  </div>
                </TableCell>
                <TableCell className="py-4">
                  <Badge
                    variant="secondary"
                    className={cn(
                      "font-medium border shadow-sm px-2.5 py-0.5 rounded-full",
                      getCategoryColor(pkg.category),
                    )}
                  >
                    {pkg.category}
                  </Badge>
                </TableCell>
                <TableCell className="py-4">
                  <div className="flex items-center gap-1.5 text-zinc-500">
                    <Clock className="h-3.5 w-3.5 text-zinc-400" />
                    <span className="text-sm font-medium">
                      {pkg.duration} min
                    </span>
                  </div>
                </TableCell>
                <TableCell className="text-right font-bold text-zinc-900 py-4">
                  {formatCurrency(pkg.price)}
                </TableCell>
                <TableCell className="text-right pr-6 py-4">
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="ghost"
                        size="icon"
                        className="h-8 w-8 text-zinc-400 opacity-0 group-hover:opacity-100 transition-all hover:bg-zinc-100 hover:text-zinc-600"
                      >
                        <MoreHorizontal className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent
                      align="end"
                      className="w-[160px] rounded-xl border-zinc-100 shadow-lg"
                    >
                      <DropdownMenuItem onClick={() => onEdit(pkg)}>
                        <Pencil className="mr-2 h-3.5 w-3.5 text-zinc-500" />
                        Edit
                      </DropdownMenuItem>
                      <DropdownMenuItem
                        className="text-red-600 focus:text-red-700 focus:bg-red-50"
                        onClick={() => onDelete(pkg.id)}
                      >
                        <Trash2 className="mr-2 h-3.5 w-3.5" />
                        Delete
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </>
  );
}

"use client";

import { useState, useMemo, useOptimistic, useTransition } from "react";
import { useRouter } from "next/navigation";
import { Service, ServicePackage } from "@/prisma/generated/prisma/client";
import { PageHeader } from "@/components/dashboard/PageHeader";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Empty,
  EmptyHeader,
  EmptyMedia,
  EmptyTitle,
  EmptyDescription,
} from "@/components/ui/empty";
import { Search, Plus, Package, RefreshCcw, Filter } from "lucide-react";
import { deletePackageAction } from "@/lib/server actions/packages";
import { toast } from "sonner";
import { PackageForm } from "./PackageForm";
import { PackageList } from "./PackageList";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

type ServicePackageWithItems = ServicePackage & {
  items: {
    service: Service;
    custom_price: number;
    service_id: number;
  }[];
};

interface PackagesPageClientProps {
  packages: ServicePackageWithItems[];
  services: Service[];
  categories: string[];
  businessSlug: string;
}

type OptimisticAction =
  | { type: "delete"; packageId: number }
  | { type: "update"; packageId: number; data: ServicePackageWithItems }
  | { type: "create"; data: ServicePackageWithItems };

export function PackagesPageClient({
  packages,
  services,
  categories,
  businessSlug,
}: PackagesPageClientProps) {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("ALL");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingPackage, setEditingPackage] =
    useState<ServicePackageWithItems | null>(null);
  const [packageToDelete, setPackageToDelete] = useState<number | null>(null);
  const [isPending, startTransition] = useTransition();

  const [optimisticPackages, addOptimisticUpdate] = useOptimistic(
    packages,
    (state: ServicePackageWithItems[], action: OptimisticAction) => {
      switch (action.type) {
        case "delete":
          return state.filter((pkg) => pkg.id !== action.packageId);
        case "update":
          return state.map((pkg) =>
            pkg.id === action.packageId ? action.data : pkg,
          );
        case "create":
          return [...state, action.data];
        default:
          return state;
      }
    },
  );

  const filteredPackages = useMemo(() => {
    return optimisticPackages.filter((pkg) => {
      const matchesSearch = pkg.name
        .toLowerCase()
        .includes(search.toLowerCase());
      const matchesCategory =
        categoryFilter === "ALL" || pkg.category === categoryFilter;

      return matchesSearch && matchesCategory;
    });
  }, [optimisticPackages, search, categoryFilter]);

  const handleDeletePackage = async (packageId: number) => {
    startTransition(async () => {
      addOptimisticUpdate({ type: "delete", packageId });
      const result = await deletePackageAction(packageId);
      if (result.success) {
        toast.success("Package deleted successfully");
      } else {
        toast.error("Failed to delete package");
        router.refresh();
      }
    });
  };

  const handleEditClick = (pkg: ServicePackageWithItems) => {
    setEditingPackage(pkg);
  };

  const closeEditDialog = () => {
    setEditingPackage(null);
  };

  return (
    <div className="flex flex-col p-4 md:p-8 bg-zinc-50/50 min-h-screen">
      <section className="flex-1 flex flex-col max-w-7xl mx-auto w-full">
        <PageHeader
          title="Packages"
          description="Bundle services together for special pricing"
          className="mb-8"
        >
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button className="shadow-lg shadow-emerald-500/20 bg-emerald-600 hover:bg-emerald-700 text-white transition-all active:scale-95 rounded-full px-6">
                <Plus className="h-4 w-4 mr-2" strokeWidth={2.5} />
                <span className="font-semibold">Add Package</span>
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-[95%] md:max-w-[800px] max-h-[90vh] flex flex-col overflow-hidden p-0 rounded-3xl">
              <DialogHeader className="p-6 pb-2">
                <DialogTitle className="text-xl font-bold text-zinc-900">
                  Create New Package
                </DialogTitle>
                <DialogDescription className="text-zinc-500">
                  Bundle multiple services together for a special price.
                </DialogDescription>
              </DialogHeader>
              <div className="flex-1 overflow-y-auto p-6 pt-0">
                <PackageForm
                  services={services}
                  categories={categories}
                  businessSlug={businessSlug}
                  onSuccess={() => {
                    setIsAddDialogOpen(false);
                    router.refresh();
                  }}
                />
              </div>
            </DialogContent>
          </Dialog>
        </PageHeader>

        <div className="flex flex-col sm:flex-row gap-4 mb-8 items-start sm:items-center justify-between">
          <div className="flex flex-col sm:flex-row gap-3 w-full sm:w-auto flex-1 max-w-2xl">
            <div className="relative flex-1">
              <Search className="absolute left-3.5 top-3 h-4 w-4 text-zinc-400" />
              <Input
                placeholder="Search packages..."
                className="pl-10 h-10 rounded-xl border-zinc-200 bg-white shadow-sm focus-visible:ring-emerald-500"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-full sm:w-[180px] h-10 rounded-xl border-zinc-200 bg-white shadow-sm">
                <div className="flex items-center gap-2 text-zinc-600">
                  <Filter className="h-4 w-4 opacity-50" />
                  <span className="truncate">
                    {categoryFilter === "ALL"
                      ? "All Categories"
                      : categoryFilter}
                  </span>
                </div>
              </SelectTrigger>
              <SelectContent className="rounded-xl">
                <SelectItem value="ALL">All Categories</SelectItem>
                {categories.map((cat) => (
                  <SelectItem key={cat} value={cat}>
                    {cat}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <Button
            variant="outline"
            size="sm"
            className="h-10 gap-2 rounded-xl border-zinc-200 bg-white text-zinc-600 hidden md:flex"
            onClick={() => router.refresh()}
          >
            <RefreshCcw className="h-4 w-4" />
            <span>Refresh</span>
          </Button>
        </div>

        <div className="flex-1">
          {filteredPackages.length === 0 ? (
            <div className="bg-white rounded-3xl border border-zinc-200/60 shadow-sm p-12 flex flex-col items-center justify-center text-center">
              <div className="h-12 w-12 bg-zinc-50 rounded-full flex items-center justify-center mb-4">
                <Package className="h-6 w-6 text-zinc-300" />
              </div>
              <h3 className="text-lg font-semibold text-zinc-900 mb-1">
                No packages found
              </h3>
              <p className="text-zinc-500 max-w-sm mb-6">
                {packages.length === 0
                  ? "Create your first service package to generate more revenue."
                  : "Try adjusting your search or filters."}
              </p>
              {packages.length === 0 && (
                <Button
                  onClick={() => setIsAddDialogOpen(true)}
                  className="rounded-full bg-emerald-600 hover:bg-emerald-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Add Package
                </Button>
              )}
            </div>
          ) : (
            <PackageList
              packages={filteredPackages}
              onEdit={handleEditClick}
              onDelete={(id) => {
                // Using AlertDialog in PackageList would require passing specific handler or UI
                // Ideally we want the deletion confirmation inside the list or handle it here with a state
                // For now, let's keep it simple and just set a state to show confirmation dialog if needed,
                // or better yet, move the specific logic to the list item if complex.
                // Actually, let's re-use the delete logic but we need to know WHICH one to delete.
                // The PackageList calls this with an ID.
                // We can show a confirmation dialog here or trust the user clicked delete.
                // Since the previous implementation had a dialog inside the row,
                // and I haven't moved the dialog logic to PackageList fully (just the trigger),
                // let's wrap the logic or component.
                // WAIT: PackageList impl has ONCLICK handler for delete.
                // It's better to manage the deletion state here to keep PackageList pure if possible,
                // OR put the AlertDialog inside PackageList.
                // Current PackageList has a simple onClick.
                // Let's create a state for delete confirmation here.
                setPackageToDelete(id);
              }}
            />
          )}
        </div>

        <Dialog open={!!editingPackage} onOpenChange={closeEditDialog}>
          <DialogContent className="max-w-[95%] md:max-w-[800px] max-h-[90vh] flex flex-col overflow-hidden p-0 rounded-3xl">
            <DialogHeader className="p-6 pb-2">
              <DialogTitle className="text-xl font-bold text-zinc-900">
                Edit Package
              </DialogTitle>
              <DialogDescription className="text-zinc-500">
                Update package details and included services.
              </DialogDescription>
            </DialogHeader>
            <div className="flex-1 overflow-y-auto p-6 pt-0">
              {editingPackage && (
                <PackageForm
                  services={services}
                  categories={categories}
                  businessSlug={businessSlug}
                  initialData={editingPackage}
                  onSuccess={() => {
                    closeEditDialog();
                    router.refresh();
                  }}
                />
              )}
            </div>
          </DialogContent>
        </Dialog>

        <AlertDialog
          open={!!packageToDelete}
          onOpenChange={(open) => !open && setPackageToDelete(null)}
        >
          <AlertDialogContent className="rounded-2xl">
            <AlertDialogHeader>
              <AlertDialogTitle>Delete Package?</AlertDialogTitle>
              <AlertDialogDescription>
                This will permanently delete this package. This action cannot be
                undone.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancel</AlertDialogCancel>
              <AlertDialogAction
                className="bg-destructive text-destructive-foreground hover:bg-destructive/90 rounded-lg"
                onClick={() =>
                  packageToDelete && handleDeletePackage(packageToDelete)
                }
              >
                Delete
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </section>
    </div>
  );
}

import { useState } from "react";
import { addDays, addWeeks, addMonths, format } from "date-fns";
import { Service } from "@/prisma/generated/prisma/client";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus, Trash2, Clock, Sparkles } from "lucide-react";
import { Separator } from "@/components/ui/separator";

export interface ServiceFlowData {
  suggested_service_id: string;
  delay_duration: string;
  delay_unit: "DAYS" | "WEEKS" | "MONTHS";
  type: "REQUIRED" | "SUGGESTED";
}

export interface ServiceFormData {
  name: string;
  description: string;
  price: string;
  duration: string;
  category: string;
  flows: ServiceFlowData[];
}

interface ServiceFormProps {
  formData: ServiceFormData;
  setFormData: (data: ServiceFormData) => void;
  categories: string[];
  newCategory: string;
  setNewCategory: (value: string) => void;
  services: Service[];
}

export function ServiceForm({
  formData,
  setFormData,
  categories,
  newCategory,
  setNewCategory,
  services,
}: ServiceFormProps) {
  const [showNewCategory, setShowNewCategory] = useState(false);

  const getEstimatedDate = (
    duration: string,
    unit: "DAYS" | "WEEKS" | "MONTHS",
  ) => {
    const num = parseInt(duration);
    if (isNaN(num)) return "";
    const today = new Date();
    let futureDate = today;
    if (unit === "DAYS") futureDate = addDays(today, num);
    if (unit === "WEEKS") futureDate = addWeeks(today, num);
    if (unit === "MONTHS") futureDate = addMonths(today, num);
    return format(futureDate, "MMM dd, yyyy");
  };

  const addFlow = () => {
    setFormData({
      ...formData,
      flows: [
        ...formData.flows,
        {
          suggested_service_id: "",
          delay_duration: "1",
          delay_unit: "WEEKS",
          type: "SUGGESTED",
        },
      ],
    });
  };

  const removeFlow = (index: number) => {
    const newFlows = [...formData.flows];
    newFlows.splice(index, 1);
    setFormData({ ...formData, flows: newFlows });
  };

  const updateFlow = (index: number, field: string, value: any) => {
    const newFlows = [...formData.flows];
    newFlows[index] = { ...newFlows[index], [field]: value };
    setFormData({ ...formData, flows: newFlows });
  };

  return (
    <div className="grid gap-6 py-4 max-h-[60vh] overflow-y-auto px-1 pr-2">
      <div className="space-y-4">
        <h3 className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
          <span className="bg-emerald-100 text-emerald-700 w-5 h-5 rounded-full flex items-center justify-center text-xs">
            1
          </span>
          Basic Details
        </h3>
        <div className="grid gap-3">
          <div className="grid gap-1.5">
            <Label htmlFor="name" className="text-xs font-medium text-zinc-600">
              Service Name <span className="text-red-500">*</span>
            </Label>
            <Input
              id="name"
              placeholder="e.g., Haircut, Manicure"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
              className="h-9 rounded-lg border-zinc-200 focus:border-emerald-500 focus:ring-emerald-500/20"
            />
          </div>

          <div className="grid gap-1.5">
            <Label
              htmlFor="description"
              className="text-xs font-medium text-zinc-600"
            >
              Description
            </Label>
            <Textarea
              id="description"
              placeholder="Brief description..."
              value={formData.description}
              onChange={(e) =>
                setFormData({ ...formData, description: e.target.value })
              }
              rows={2}
              className="resize-none rounded-lg border-zinc-200 focus:border-emerald-500 focus:ring-emerald-500/20 text-sm"
            />
          </div>
        </div>
      </div>

      <Separator className="bg-zinc-100" />

      <div className="space-y-4">
        <h3 className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
          <span className="bg-emerald-100 text-emerald-700 w-5 h-5 rounded-full flex items-center justify-center text-xs">
            2
          </span>
          Pricing & Category
        </h3>
        <div className="grid grid-cols-2 gap-4">
          <div className="grid gap-1.5">
            <Label
              htmlFor="price"
              className="text-xs font-medium text-zinc-600"
            >
              Price () <span className="text-red-500">*</span>
            </Label>
            <Input
              id="price"
              type="number"
              min="0"
              step="0.01"
              placeholder="0.00"
              value={formData.price}
              onChange={(e) =>
                setFormData({ ...formData, price: e.target.value })
              }
              className="h-9 rounded-lg border-zinc-200"
            />
          </div>
          <div className="grid gap-1.5">
            <Label
              htmlFor="duration"
              className="text-xs font-medium text-zinc-600"
            >
              Duration (mins)
            </Label>
            <Input
              id="duration"
              type="number"
              min="0"
              placeholder="e.g., 30"
              value={formData.duration}
              onChange={(e) =>
                setFormData({ ...formData, duration: e.target.value })
              }
              className="h-9 rounded-lg border-zinc-200"
            />
          </div>
        </div>

        <div className="grid gap-1.5">
          <Label className="text-xs font-medium text-zinc-600">
            Category <span className="text-red-500">*</span>
          </Label>
          {showNewCategory ? (
            <div className="flex gap-2">
              <Input
                placeholder="New category..."
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                className="h-9 rounded-lg border-zinc-200"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                onClick={() => {
                  setShowNewCategory(false);
                  setNewCategory("");
                }}
                className="h-9 px-3 text-zinc-500"
              >
                Cancel
              </Button>
            </div>
          ) : (
            <div className="flex gap-2">
              <Select
                value={formData.category}
                onValueChange={(value) =>
                  setFormData({ ...formData, category: value })
                }
              >
                <SelectTrigger className="flex-1 h-9 rounded-lg border-zinc-200">
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((cat) => (
                    <SelectItem key={cat} value={cat}>
                      {cat}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Button
                type="button"
                variant="outline"
                size="icon"
                onClick={() => setShowNewCategory(true)}
                title="Add new category"
                className="h-9 w-9 rounded-lg border-zinc-200"
              >
                <Plus className="h-4 w-4" />
              </Button>
            </div>
          )}
        </div>
      </div>

      <Separator className="bg-zinc-100" />

      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <Label className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
            <span className="bg-emerald-100 text-emerald-700 w-5 h-5 rounded-full flex items-center justify-center text-xs">
              3
            </span>
            Service Flows
          </Label>
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={addFlow}
            className="h-7 text-xs rounded-lg border-dashed border-emerald-300 text-emerald-700 bg-emerald-50 hover:bg-emerald-100"
          >
            <Plus className="h-3 w-3 mr-1.5" />
            Add Step
          </Button>
        </div>

        {formData.flows.length === 0 ? (
          <div className="text-center py-6 bg-zinc-50 rounded-xl border border-dashed border-zinc-200">
            <div className="flex justify-center mb-2">
              <Sparkles className="h-5 w-5 text-zinc-300" />
            </div>
            <p className="text-xs font-medium text-zinc-500">
              Automate next steps
            </p>
          </div>
        ) : (
          <div className="space-y-4 pl-1">
            {formData.flows.map((flow, index) => (
              <div key={index} className="relative pl-6 pb-2">
                {/* Timeline Line */}
                <div className="absolute left-[9px] top-6 bottom-0 w-px bg-emerald-100 last:bottom-auto last:h-full" />

                {/* Timeline Dot */}
                <div className="absolute left-0 top-1.5 h-5 w-5 rounded-full bg-emerald-50 border border-emerald-200 flex items-center justify-center text-emerald-600 z-10">
                  <Clock className="h-3 w-3" />
                </div>

                <div className="bg-white rounded-xl border border-zinc-200 shadow-sm p-3 relative group hover:border-emerald-200 transition-colors">
                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="absolute top-2 right-2 h-6 w-6 text-zinc-300 hover:text-red-500 hover:bg-red-50 transition-all opacity-0 group-hover:opacity-100"
                    onClick={() => removeFlow(index)}
                  >
                    <Trash2 className="h-3 w-3" />
                  </Button>

                  <div className="flex flex-wrap items-center gap-2 text-sm mb-3 pr-6">
                    <span className="text-zinc-500 font-medium">Wait</span>
                    <Input
                      type="number"
                      min="0"
                      value={flow.delay_duration}
                      onChange={(e) =>
                        updateFlow(index, "delay_duration", e.target.value)
                      }
                      className="w-16 h-7 text-center rounded-md border-zinc-200"
                    />
                    <Select
                      value={flow.delay_unit}
                      onValueChange={(value) =>
                        updateFlow(index, "delay_unit", value)
                      }
                    >
                      <SelectTrigger className="w-24 h-7 rounded-md border-zinc-200">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="DAYS">Day(s)</SelectItem>
                        <SelectItem value="WEEKS">Week(s)</SelectItem>
                        <SelectItem value="MONTHS">Month(s)</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="bg-zinc-50/50 p-2.5 rounded-lg border border-zinc-100">
                    <div className="flex flex-col gap-2">
                      <div className="flex items-center gap-2 text-xs font-medium text-zinc-500">
                        <span>Then</span>
                        <Select
                          value={flow.type}
                          onValueChange={(value) =>
                            updateFlow(index, "type", value)
                          }
                        >
                          <SelectTrigger className="w-28 h-6 text-xs border-zinc-200 bg-white">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="SUGGESTED">Suggest</SelectItem>
                            <SelectItem value="REQUIRED">Require</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <Select
                        value={flow.suggested_service_id}
                        onValueChange={(value) =>
                          updateFlow(index, "suggested_service_id", value)
                        }
                      >
                        <SelectTrigger className="w-full h-8 bg-white border-zinc-200">
                          <SelectValue placeholder="Select service..." />
                        </SelectTrigger>
                        <SelectContent>
                          {services.map((s) => (
                            <SelectItem key={s.id} value={s.id.toString()}>
                              {s.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  {flow.delay_duration && (
                    <div className="mt-2 text-[10px] text-zinc-400 font-medium flex items-center gap-1">
                      <Clock className="w-3 h-3" />
                      est.{" "}
                      {getEstimatedDate(flow.delay_duration, flow.delay_unit)}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

"use client";

import { useState, useMemo, useOptimistic, useTransition } from "react";
import { useRouter } from "next/navigation";
import { Service } from "@/prisma/generated/prisma/client";
import { PageHeader } from "@/components/dashboard/PageHeader";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Empty,
  EmptyHeader,
  EmptyMedia,
  EmptyTitle,
  EmptyDescription,
  EmptyContent,
} from "@/components/ui/empty";
import { Search, Plus, Package, Filter, RefreshCcw } from "lucide-react";
import {
  createServiceAction,
  updateServiceAction,
  deleteServiceAction,
} from "@/lib/server actions/services";
import { toast } from "sonner";
import { ServiceList } from "./ServiceList";
import { ServiceForm, ServiceFormData, ServiceFlowData } from "./ServiceForm";

interface ServicesPageClientProps {
  services: Service[];
  categories: string[];
  businessSlug: string;
}

const initialFormData: ServiceFormData = {
  name: "",
  description: "",
  price: "",
  duration: "",
  category: "",
  flows: [],
};

type OptimisticAction =
  | {
      type: "update";
      serviceId: number;
      data: {
        name: string;
        description?: string;
        price: number;
        duration?: number;
        category: string;
      };
    }
  | { type: "delete"; serviceId: number };

export function ServicesPageClient({
  services,
  categories,
  businessSlug,
}: ServicesPageClientProps) {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("ALL");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [selectedService, setSelectedService] = useState<Service | null>(null);
  const [formData, setFormData] = useState<ServiceFormData>(initialFormData);
  const [isLoading, setIsLoading] = useState(false);
  const [newCategory, setNewCategory] = useState("");
  const [isPending, startTransition] = useTransition();

  // Optimistic state for instant UI updates
  const [optimisticServices, addOptimisticUpdate] = useOptimistic(
    services,
    (state: Service[], action: OptimisticAction) => {
      switch (action.type) {
        case "update":
          return state.map((service) =>
            service.id === action.serviceId
              ? {
                  ...service,
                  name: action.data.name,
                  description: action.data.description || null,
                  price: action.data.price,
                  duration: action.data.duration || null,
                  category: action.data.category,
                }
              : service,
          );
        case "delete":
          return state.filter((service) => service.id !== action.serviceId);
        default:
          return state;
      }
    },
  );

  const filteredServices = useMemo(() => {
    return optimisticServices.filter((service) => {
      const matchesSearch =
        service.name.toLowerCase().includes(search.toLowerCase()) ||
        service.description?.toLowerCase().includes(search.toLowerCase()) ||
        service.category.toLowerCase().includes(search.toLowerCase());

      const matchesCategory =
        categoryFilter === "ALL" || service.category === categoryFilter;

      return matchesSearch && matchesCategory;
    });
  }, [optimisticServices, search, categoryFilter]);

  const handleAddService = async () => {
    if (!formData.name || !formData.price || !formData.category) {
      toast.error("Please fill in required fields");
      return;
    }

    setIsLoading(true);
    const result = await createServiceAction({
      name: formData.name,
      description: formData.description || undefined,
      price: parseFloat(formData.price),
      duration: formData.duration ? parseInt(formData.duration) : undefined,
      category: newCategory || formData.category,
      flows: formData.flows
        .filter((f) => f.suggested_service_id)
        .map((f) => ({
          suggested_service_id: parseInt(f.suggested_service_id),
          delay_duration: parseInt(f.delay_duration),
          delay_unit: f.delay_unit,
          type: f.type,
        })),
    });

    setIsLoading(false);
    if (result.success) {
      toast.success("Service created successfully");
      setIsAddDialogOpen(false);
      setFormData(initialFormData);
      setNewCategory("");
      router.refresh();
    } else {
      toast.error(result.error || "Failed to create service");
    }
  };

  const handleEditService = async () => {
    if (!selectedService || !formData.name || !formData.price) {
      toast.error("Please fill in required fields");
      return;
    }

    const updateData = {
      name: formData.name,
      description: formData.description || undefined,
      price: parseFloat(formData.price),
      duration: formData.duration ? parseInt(formData.duration) : undefined,
      category: newCategory || formData.category,
      flows: formData.flows
        .filter((f) => f.suggested_service_id)
        .map((f) => ({
          suggested_service_id: parseInt(f.suggested_service_id),
          delay_duration: parseInt(f.delay_duration),
          delay_unit: f.delay_unit,
          type: f.type,
        })),
    };

    // Close dialog immediately for better UX
    setIsEditDialogOpen(false);

    // Apply optimistic update and call server action
    startTransition(async () => {
      // Apply optimistic update
      addOptimisticUpdate({
        type: "update",
        serviceId: selectedService.id,
        data: updateData,
      });

      const result = await updateServiceAction(selectedService.id, updateData);

      if (result.success) {
        toast.success("Service updated successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to update service");
        // Refresh to revert to server state on error
        router.refresh();
      }

      setSelectedService(null);
      setFormData(initialFormData);
      setNewCategory("");
    });
  };

  const handleDeleteService = async (serviceId: number) => {
    // Apply optimistic update and call server action
    startTransition(async () => {
      // Optimistically remove from UI
      addOptimisticUpdate({
        type: "delete",
        serviceId,
      });

      const result = await deleteServiceAction(serviceId);
      if (result.success) {
        toast.success("Service deleted successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to delete service");
        // Refresh to revert to server state on error
        router.refresh();
      }
    });
  };

  const openEditDialog = (service: Service) => {
    setSelectedService(service);
    setFormData({
      name: service.name,
      description: service.description || "",
      price: service.price.toString(),
      duration: service.duration?.toString() || "",
      category: service.category,
      flows:
        (service as any).flow_triggers?.map((f: any) => ({
          suggested_service_id: f.suggested_service_id.toString(),
          delay_duration: f.delay_duration.toString(),
          delay_unit: f.delay_unit,
          type: f.type,
        })) || [],
    });
    setIsEditDialogOpen(true);
  };

  return (
    <div className="flex flex-col p-4 md:p-8 bg-zinc-50/50 min-h-screen">
      <section className="flex-1 flex flex-col max-w-7xl mx-auto w-full">
        <PageHeader
          title="Services"
          description="Manage your service catalog"
          className="mb-8"
        >
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button className="shadow-lg shadow-emerald-500/20 bg-emerald-600 hover:bg-emerald-700 text-white transition-all active:scale-95 rounded-full px-6">
                <Plus className="h-4 w-4 mr-2" strokeWidth={2.5} />
                <span className="font-semibold">Add Service</span>
              </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[550px] rounded-3xl p-0 gap-0 overflow-hidden">
              <DialogHeader className="p-6 pb-2">
                <DialogTitle className="text-xl font-bold text-zinc-900">
                  Add New Service
                </DialogTitle>
                <DialogDescription className="text-zinc-500">
                  Create a new service offering for your clients.
                </DialogDescription>
              </DialogHeader>
              <div className="px-6 py-2">
                <ServiceForm
                  formData={formData}
                  setFormData={setFormData}
                  categories={categories}
                  newCategory={newCategory}
                  setNewCategory={setNewCategory}
                  services={services}
                />
              </div>
              <DialogFooter className="p-6 pt-2 bg-zinc-50/50">
                <Button
                  variant="outline"
                  onClick={() => setIsAddDialogOpen(false)}
                  className="rounded-xl border-zinc-200"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleAddService}
                  disabled={isLoading}
                  className="bg-emerald-600 hover:bg-emerald-700 text-white rounded-xl shadow-lg shadow-emerald-500/20"
                >
                  {isLoading ? "Creating..." : "Create Service"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </PageHeader>

        <div className="flex flex-col sm:flex-row gap-4 mb-8 items-start sm:items-center justify-between">
          <div className="flex flex-col sm:flex-row gap-3 w-full sm:w-auto flex-1 max-w-2xl">
            <div className="relative flex-1">
              <Search className="absolute left-3.5 top-3 h-4 w-4 text-zinc-400" />
              <Input
                placeholder="Search services..."
                className="pl-10 h-10 rounded-xl border-zinc-200 bg-white shadow-sm focus-visible:ring-emerald-500"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-full sm:w-[180px] h-10 rounded-xl border-zinc-200 bg-white shadow-sm">
                <div className="flex items-center gap-2 text-zinc-600">
                  <Filter className="h-4 w-4 opacity-50" />
                  <span className="truncate">
                    {categoryFilter === "ALL"
                      ? "All Categories"
                      : categoryFilter}
                  </span>
                </div>
              </SelectTrigger>
              <SelectContent className="rounded-xl">
                <SelectItem value="ALL">All Categories</SelectItem>
                {categories.map((cat) => (
                  <SelectItem key={cat} value={cat}>
                    {cat}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <Button
            variant="outline"
            size="sm"
            className="h-10 gap-2 rounded-xl border-zinc-200 bg-white text-zinc-600 hidden md:flex"
            onClick={() => router.refresh()}
          >
            <RefreshCcw className="h-4 w-4" />
            <span>Refresh</span>
          </Button>
        </div>

        {/* Content */}
        <div className="flex-1">
          {filteredServices.length === 0 ? (
            <div className="bg-white rounded-3xl border border-zinc-200/60 shadow-sm p-12 flex flex-col items-center justify-center text-center">
              <div className="h-12 w-12 bg-zinc-50 rounded-full flex items-center justify-center mb-4">
                <Package className="h-6 w-6 text-zinc-300" />
              </div>
              <h3 className="text-lg font-semibold text-zinc-900 mb-1">
                No services found
              </h3>
              <p className="text-zinc-500 max-w-sm mb-6">
                {services.length === 0
                  ? "Get started by adding your first service offering."
                  : "Try adjusting your search or filters to find what you're looking for."}
              </p>
              {services.length === 0 && (
                <Button
                  onClick={() => setIsAddDialogOpen(true)}
                  className="rounded-full bg-emerald-600 hover:bg-emerald-700"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Add Service
                </Button>
              )}
            </div>
          ) : (
            <ServiceList
              services={filteredServices}
              onEdit={openEditDialog}
              onDelete={handleDeleteService}
            />
          )}
        </div>

        <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
          <DialogContent className="sm:max-w-[550px] rounded-3xl p-0 gap-0 overflow-hidden">
            <DialogHeader className="p-6 pb-2">
              <DialogTitle className="text-xl font-bold text-zinc-900">
                Edit Service
              </DialogTitle>
              <DialogDescription className="text-zinc-500">
                Update the details for this service.
              </DialogDescription>
            </DialogHeader>
            <div className="px-6 py-2">
              <ServiceForm
                formData={formData}
                setFormData={setFormData}
                categories={categories}
                newCategory={newCategory}
                setNewCategory={setNewCategory}
                services={services}
              />
            </div>
            <DialogFooter className="p-6 pt-2 bg-zinc-50/50">
              <Button
                variant="outline"
                onClick={() => setIsEditDialogOpen(false)}
                className="rounded-xl border-zinc-200"
              >
                Cancel
              </Button>
              <Button
                onClick={handleEditService}
                disabled={isLoading}
