"use client";

import { useCallback, useEffect, useMemo, useState } from "react";
import type { MouseEvent } from "react";
import { createPortal } from "react-dom";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { CheckCircle2, Clock3, XCircle } from "lucide-react";

export interface QrPaymentPanelProps {
  qrImage: string;
  amountLabel: string;
  status: "pending" | "paid" | "failed" | "expired";
  expiresAt?: string;
  onClose: () => void;
  onRetry?: () => void;
}

const getImageSrc = (image: string) =>
  image.startsWith("data:") ? image : `data:image/png;base64,${image}`;

export default function QrPaymentPanel({
  qrImage,
  amountLabel,
  status,
  expiresAt,
  onClose,
  onRetry,
}: QrPaymentPanelProps) {
  const [remaining, setRemaining] = useState<string | null>(null);
  const portalRoot = typeof document !== "undefined" ? document.body : null;

  useEffect(() => {
    if (!expiresAt) return;

    const expiryTime = new Date(expiresAt).getTime();
    const update = () => {
      const diff = expiryTime - Date.now();
      if (diff <= 0) {
        setRemaining("Expired");
        return;
      }
      const minutes = Math.floor(diff / 60000);
      const seconds = Math.floor((diff % 60000) / 1000);
      setRemaining(`${minutes}:${seconds.toString().padStart(2, "0")}`);
    };

    update();
    const interval = setInterval(update, 1000);
    return () => clearInterval(interval);
  }, [expiresAt]);

  const statusCopy = useMemo(() => {
    switch (status) {
      case "paid":
        return "Payment received. Finalizing your booking...";
      case "failed":
        return "Payment failed. Please try again.";
      case "expired":
        return "QR code expired. Please generate a new one.";
      default:
        return "Scan with any QR PH app to complete payment.";
    }
  }, [status]);

  const statusIcon = useMemo(() => {
    if (status === "paid") {
      return <CheckCircle2 className="h-5 w-5 text-emerald-600" />;
    }
    if (status === "failed" || status === "expired") {
      return <XCircle className="h-5 w-5 text-rose-500" />;
    }
    return <Clock3 className="h-5 w-5 text-amber-500" />;
  }, [status]);

  const handleDismiss = useCallback(() => {
    onClose();
  }, [onClose]);

  const handleBackdropClick = useCallback(
    (event: MouseEvent<HTMLDivElement>) => {
      if (event.target === event.currentTarget) {
        onClose();
      }
    },
    [onClose],
  );

  if (!portalRoot) return null;

  return createPortal(
    <div
      className="fixed inset-0 z-[70] flex items-center justify-center bg-black/40 px-4 py-10 backdrop-blur-sm pointer-events-auto"
      onClick={handleBackdropClick}
      role="dialog"
      aria-modal="true"
    >
      <Card className="w-full max-w-md rounded-2xl border border-black/10 bg-white/95 p-6 shadow-xl pointer-events-auto">
        <div className="space-y-4 text-center">
          <div className="inline-flex items-center justify-center rounded-full bg-amber-50 px-3 py-1 text-xs font-semibold text-amber-700">
            QR Payment
          </div>
          <div className="flex items-center justify-center gap-2 text-sm font-medium text-muted-foreground">
            {statusIcon}
            <span>{statusCopy}</span>
          </div>
          <div className="text-sm text-muted-foreground">
            Amount to pay: <span className="font-semibold">{amountLabel}</span>
          </div>
          <div className="mx-auto w-56 rounded-2xl border border-black/10 bg-white p-3 shadow-sm">
            <img
              src={getImageSrc(qrImage)}
              alt="QR payment code"
              className={cn(
                "w-full",
                status === "paid" && "opacity-60 grayscale",
              )}
            />
          </div>
          {expiresAt && (
            <p className="text-xs text-muted-foreground">
              Expires in{" "}
              <span className="font-semibold">
                {remaining || "calculating..."}
              </span>
            </p>
          )}
          <div className="space-y-2">
            {status === "failed" || status === "expired" ? (
              <Button type="button" onClick={onRetry} className="w-full">
                Try again
              </Button>
            ) : null}
            <Button
              type="button"
              variant="outline"
              onClick={handleDismiss}
              className="w-full"
            >
              Back to booking
            </Button>
          </div>
          <p className="text-[11px] text-muted-foreground">
            Booking confirmation appears after payment is verified.
          </p>
        </div>
      </Card>
    </div>,
    portalRoot,
  );
}

"use client";

import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { TimeSlot } from "@/lib/server actions/availability";
import { Clock, Users, Info } from "lucide-react";

interface TimeSlotPickerProps {
  slots: TimeSlot[];
  value?: Date;
  onChange: (time: Date) => void;
  isLoading?: boolean;
  category?: string;
  businessHours?: { open_time: string; close_time: string } | null;
  disabled?: boolean;
}

export default function TimeSlotPicker({
  slots,
  value,
  onChange,
  isLoading = false,
  category,
  businessHours,
  disabled = false,
}: TimeSlotPickerProps) {
  if (isLoading) {
    return (
      <div className="grid grid-cols-3 sm:grid-cols-4 gap-2">
        {Array.from({ length: 8 }).map((_, i) => (
          <div key={i} className="h-16 rounded-md bg-muted animate-pulse" />
        ))}
      </div>
    );
  }

  if (disabled) {
    return (
      <div className="text-center py-8 text-muted-foreground">
        <Clock className="h-8 w-8 mx-auto mb-2 opacity-50" />
        <p>Select services to view available slots</p>
        <p className="text-sm">Time slots are disabled until then</p>
      </div>
    );
  }

  if (slots.length === 0) {
    return (
      <div className="text-center py-8 text-muted-foreground">
        <Clock className="h-8 w-8 mx-auto mb-2 opacity-50" />
        <p>No available slots for the selected services</p>
        <p className="text-sm">
          Try selecting a different day or removing a time-restricted service
        </p>
      </div>
    );
  }

  const formatTime = (time: string) => {
    const [hours, minutes] = time.split(":");
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? "PM" : "AM";
    const displayHour = hour > 12 ? hour - 12 : hour === 0 ? 12 : hour;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  return (
    <div className="space-y-3">
      {businessHours && category && (
        <div className="flex items-center gap-2 p-2.5 rounded-lg bg-emerald-50/50 border border-emerald-200/60">
          <Info className="h-3.5 w-3.5 text-emerald-600 shrink-0" />
          <p className="text-xs text-emerald-800">
            <span className="font-semibold capitalize">{category}</span> hours:{" "}
            {formatTime(businessHours.open_time)} -{" "}
            {formatTime(businessHours.close_time)}
          </p>
        </div>
      )}
      <div className="grid grid-cols-3 sm:grid-cols-4 gap-3 max-h-[300px] overflow-y-auto p-1">
        {slots.map((slot) => {
          const isSelected =
            value && slot.startTime.getTime() === value.getTime();
          const availabilityLevel =
            slot.availableEmployeeCount >= 3
              ? "high"
              : slot.availableEmployeeCount >= 1
                ? "medium"
                : "none";

          return (
            <Button
              key={slot.startTime.toISOString()}
              type="button"
              variant={isSelected ? "default" : "outline"}
              disabled={!slot.available}
              onClick={() => onChange(slot.startTime)}
              className={cn(
                "h-auto py-3 px-2 flex flex-col items-center gap-1.5 transition-all duration-200",
                !slot.available && "opacity-40 cursor-not-allowed bg-muted/50",
                isSelected
                  ? "shadow-md ring-2 ring-primary/20 ring-offset-1 scale-[1.02]"
                  : "hover:border-primary/50 hover:bg-primary/5 bg-card",
                availabilityLevel === "high" &&
                  !isSelected &&
                  "border-green-500/30 hover:border-green-500/60 hover:bg-green-50/10",
              )}
            >
              <span
                className={cn(
                  "font-bold text-sm tracking-tight",
                  isSelected ? "text-primary-foreground" : "text-foreground",
                )}
              >
                {new Date(slot.startTime).toLocaleTimeString("en-US", {
                  timeZone: "Asia/Manila",
                  hour: "numeric",
                  minute: "2-digit",
                  hour12: true,
                })}
              </span>
              <span
                className={cn(
                  "text-[10px] uppercase tracking-wider font-medium flex items-center gap-1",
                  isSelected
                    ? "text-primary-foreground/80"
                    : availabilityLevel === "high"
                      ? "text-green-600"
                      : "text-muted-foreground",
                )}
              >
                {slot.availableEmployeeCount}{" "}
                {slot.availableEmployeeCount === 1 ? "Staff" : "Staffs"}
              </span>
            </Button>
          );
        })}
      </div>
    </div>
  );
}

