"use client";

import { useState, useOptimistic, useTransition } from "react";
import { format, addDays, subDays, isSameDay } from "date-fns";
import {
  ChevronLeft,
  ChevronRight,
  Loader2,
  RefreshCw,
  Calendar as CalendarIcon,
  Clock,
  MapPin,
  User,
} from "lucide-react";
import { toast } from "sonner";
import { useRouter } from "next/navigation";
import {
  Employee,
  EmployeeAttendance,
  AttendanceStatus,
} from "@/prisma/generated/prisma/client";

import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";

import { Button } from "@/components/ui/button";
import { Calendar } from "@/components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  getDailyAttendance,
  updateAttendanceStatus,
} from "@/app/actions/attendance";

import { EmployeeAttendanceHistoryDialog } from "./EmployeeAttendanceHistoryDialog";

type AttendanceData = {
  employee: Employee & { user: { name: string } };
  attendance: EmployeeAttendance | null;
};

interface DailyAttendanceTabProps {
  businessId: string;
  initialData: AttendanceData[];
  currentDate: Date;
}

export function DailyAttendanceTab({
  businessId,
  initialData,
  currentDate,
}: DailyAttendanceTabProps) {
  const router = useRouter();
  const [isPending, startTransition] = useTransition();
  const [selectedEmployee, setSelectedEmployee] = useState<
    (Employee & { user: { name: string } }) | null
  >(null);

  // Optimistic data handling
  const [optimisticData, addOptimisticUpdate] = useOptimistic(
    initialData,
    (state, update: { employeeId: number; status: AttendanceStatus }) => {
      return state.map((item) => {
        if (item.employee.id === update.employeeId) {
          // Create optimistic attendance or update existing
          const baseAttendance = item.attendance || {
            id: 0,
            employee_id: update.employeeId,
            date: currentDate,
            time_in: null,
            time_out: null,
            location_verified: false,
            latitude: null,
            longitude: null,
            created_at: new Date(),
            updated_at: new Date(),
          };

          return {
            ...item,
            attendance: {
              ...baseAttendance,
              status: update.status,
            } as EmployeeAttendance,
          };
        }
        return item;
      });
    },
  );

  const handleDateChange = (newDate: Date) => {
    startTransition(() => {
      // Use ISO string for consistent parsing
      const dateString = newDate.toISOString();
      router.push(`?date=${dateString}`);
    });
  };

  const refreshData = () => {
    startTransition(() => {
      router.refresh();
    });
  };

  const handleStatusChange = async (
    employeeId: number,
    newStatus: AttendanceStatus,
  ) => {
    // Optimistic update
    addOptimisticUpdate({ employeeId, status: newStatus });

    const result = await updateAttendanceStatus(
      employeeId,
      currentDate,
      newStatus,
    );
    if (!result.success) {
      toast.error("Failed to update status");
      router.refresh(); // Revert on failure by refreshing
    }
  };

  // Summary logic
  const summary = {
    PRESENT: optimisticData.filter((d) => d.attendance?.status === "PRESENT")
      .length,
    LATE: optimisticData.filter((d) => d.attendance?.status === "LATE").length,
    ABSENT: optimisticData.filter((d) => d.attendance?.status === "ABSENT")
      .length,
    LEAVE: optimisticData.filter((d) => d.attendance?.status === "LEAVE")
      .length,
    OFF: optimisticData.filter((d) => d.attendance?.status === "OFF").length,
  };

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const getAvatarColor = (name: string) => {
    const colors = [
      "bg-blue-500",
      "bg-emerald-500",
      "bg-purple-500",
      "bg-orange-500",
      "bg-pink-500",
      "bg-cyan-500",
      "bg-indigo-500",
      "bg-teal-500",
    ];
    const index = name.charCodeAt(0) % colors.length;
    return colors[index];
  };

  return (
    <div className="space-y-6">
      <EmployeeAttendanceHistoryDialog
        employee={selectedEmployee}
        open={!!selectedEmployee}
        onOpenChange={(open) => !open && setSelectedEmployee(null)}
      />

      {/* Date Navigator */}
      <div className="flex flex-col sm:flex-row items-center justify-between gap-4 bg-white p-1.5 rounded-2xl border border-zinc-200 shadow-[0px_2px_3px_-1px_rgba(0,0,0,0.1),0px_1px_0px_0px_rgba(25,28,33,0.02),0px_0px_0px_1px_rgba(25,28,33,0.08)]">
        <div className="flex items-center gap-1 p-1 bg-zinc-100/80 rounded-xl border border-zinc-200/50">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDateChange(subDays(currentDate, 1))}
            disabled={isPending}
            className="h-8 w-8 rounded-lg hover:bg-white hover:shadow-sm text-zinc-600 transition-all"
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>

          <Popover>
            <PopoverTrigger asChild>
              <Button
                variant="ghost"
                className={cn(
                  "h-8 px-3 text-sm font-medium text-zinc-700 hover:bg-white hover:shadow-sm rounded-lg transition-all border border-transparent hover:border-zinc-200/50",
                  !currentDate && "text-muted-foreground",
                )}
              >
                <CalendarIcon className="mr-2 h-4 w-4 text-zinc-500" />
                {currentDate ? (
                  format(currentDate, "MMMM do, yyyy")
                ) : (
                  <span>Pick a date</span>
                )}
              </Button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0" align="start">
              <Calendar
                mode="single"
                selected={currentDate}
                onSelect={(date) => date && handleDateChange(date)}
                initialFocus
              />
            </PopoverContent>
          </Popover>

          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDateChange(addDays(currentDate, 1))}
            disabled={isPending}
            className="h-8 w-8 rounded-lg hover:bg-white hover:shadow-sm text-zinc-600 transition-all"
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>

        <div className="flex items-center gap-2 pr-2">
          {!isSameDay(currentDate, new Date()) && (
            <Button
              variant="outline"
              size="sm"
              onClick={() => handleDateChange(new Date())}
              disabled={isPending}
              className="h-8 text-xs font-medium rounded-lg border-zinc-200 bg-white hover:bg-zinc-50 hover:text-zinc-900"
            >
              Go to Today
            </Button>
          )}
          <Button
            variant="ghost"
            size="icon"
            onClick={refreshData}
            disabled={isPending}
            className="h-8 w-8 rounded-lg text-zinc-400 hover:text-zinc-700 hover:bg-zinc-100 transition-colors"
          >
            <RefreshCw
              className={`h-4 w-4 ${isPending ? "animate-spin" : ""}`}
            />
          </Button>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-5">
        <SummaryCard
          title="Present"
          count={summary.PRESENT}
          indicatorColor="bg-emerald-500"
        />
        <SummaryCard
          title="Late"
          count={summary.LATE}
          indicatorColor="bg-amber-500"
        />
        <SummaryCard
          title="Absent"
          count={summary.ABSENT}
          indicatorColor="bg-rose-500"
        />
        <SummaryCard
          title="On Leave"
          count={summary.LEAVE}
          indicatorColor="bg-blue-500"
        />
        <SummaryCard
          title="Off"
          count={summary.OFF}
          indicatorColor="bg-zinc-400"
        />
      </div>

      {/* Mobile Card View */}
      <div className="md:hidden grid grid-cols-1 gap-4">
        {optimisticData.length === 0 ? (
          <div className="text-center py-12 bg-white rounded-2xl border border-zinc-100">
            <User className="h-12 w-12 text-zinc-300 mx-auto mb-3" />
            <h3 className="text-lg font-medium text-zinc-900">
              No employees found
            </h3>
            <p className="text-zinc-500">
              Try selecting a different date or refreshing.
            </p>
          </div>
        ) : (
          optimisticData.map(({ employee, attendance }) => (
            <div
              key={employee.id}
              className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-4"
            >
              <div className="flex justify-between items-start">
                <div
                  className="flex items-center gap-3 cursor-pointer hover:opacity-80 transition-opacity"
                  onClick={() => setSelectedEmployee(employee)}
                >
                  <Avatar
                    className={`${getAvatarColor(employee.user.name)} h-10 w-10 text-white shadow-sm ring-2 ring-white`}
                  >
                    <AvatarFallback className="bg-transparent font-medium">
                      {getInitials(employee.user.name)}
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex flex-col">
                    <span className="font-semibold text-zinc-900">
                      {employee.user.name}
                    </span>
                    <div
                      className="flex items-center gap-1.5 mt-1"
                      onClick={(e) => e.stopPropagation()}
                    >
                      {/* Dropdown Logic */}
                    </div>
                  </div>
                </div>
              </div>

              <div className="pl-[52px] -mt-2 mb-2">
                <div
                  className="flex items-center gap-1.5 mt-1"
                  onClick={(e) => e.stopPropagation()}
                >
                  <Select
                    defaultValue={attendance?.status || "ABSENT"}
                    value={attendance?.status || "ABSENT"}
                    onValueChange={(val) =>
                      handleStatusChange(employee.id, val as AttendanceStatus)
                    }
                  >
                    <SelectTrigger className="h-7 w-[110px] text-xs bg-zinc-50 border-zinc-200">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="PRESENT">Present</SelectItem>
                      <SelectItem value="LATE">Late</SelectItem>
                      <SelectItem value="ABSENT">Absent</SelectItem>
                      <SelectItem value="LEAVE">On Leave</SelectItem>
                      <SelectItem value="OFF">Day Off</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-3 py-3 border-t border-zinc-50">
                <div className="flex flex-col gap-1">
                  <span className="text-xs text-zinc-400 font-medium uppercase tracking-wider flex items-center gap-1">
                    <Clock className="h-3 w-3" /> Time In
                  </span>
                  <span className="text-sm font-medium text-zinc-900">
                    {attendance?.time_in
                      ? format(new Date(attendance.time_in), "hh:mm a")
                      : "-"}
                  </span>
                </div>
                <div className="flex flex-col gap-1">
                  <span className="text-xs text-zinc-400 font-medium uppercase tracking-wider flex items-center gap-1">
                    <Clock className="h-3 w-3" /> Time Out
                  </span>
                  <span className="text-sm font-medium text-zinc-900">
                    {attendance?.time_out
                      ? format(new Date(attendance.time_out), "hh:mm a")
                      : "-"}
                  </span>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Desktop Table View */}
      <div className="hidden md:block rounded-3xl border border-zinc-100 overflow-hidden shadow-sm bg-white">
        <Table>
          <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
            <TableRow className="hover:bg-transparent border-zinc-100">
              <TableHead className="pl-6 h-12 font-semibold text-zinc-500">
                Employee
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Time In
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Time Out
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500 w-[200px]">
                Status
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {optimisticData.length === 0 ? (
              <TableRow>
                <TableCell colSpan={4} className="h-24 text-center">
                  No employees found.
                </TableCell>
              </TableRow>
            ) : (
              optimisticData.map(({ employee, attendance }) => (
                <TableRow
                  key={employee.id}
                  className="hover:bg-zinc-50/50 transition-colors border-zinc-100 group"
                >
                  <TableCell
                    className="pl-6 py-4 cursor-pointer"
                    onClick={() => setSelectedEmployee(employee)}
                  >
                    <div className="flex items-center gap-3 group-hover:translate-x-1 transition-transform duration-200">
                      <Avatar
                        className={`${getAvatarColor(employee.user.name)} h-9 w-9 text-white shadow-sm ring-2 ring-white`}
                      >
                        <AvatarFallback className="bg-transparent font-medium">
                          {getInitials(employee.user.name)}
                        </AvatarFallback>
                      </Avatar>
                      <span className="font-medium text-zinc-900 group-hover:text-blue-600 transition-colors">
                        {employee.user.name}
                      </span>
                    </div>
                  </TableCell>
                  <TableCell className="py-4 text-zinc-600">
                    {attendance?.time_in ? (
                      <span className="bg-zinc-100 text-zinc-700 px-2 py-1 rounded-md text-xs font-medium font-mono">
                        {format(new Date(attendance.time_in), "hh:mm a")}
                      </span>
                    ) : (
                      "-"
                    )}
                  </TableCell>
                  <TableCell className="py-4 text-zinc-600">
                    {attendance?.time_out ? (
                      <span className="bg-zinc-100 text-zinc-700 px-2 py-1 rounded-md text-xs font-medium font-mono">
                        {format(new Date(attendance.time_out), "hh:mm a")}
                      </span>
                    ) : (
                      "-"
                    )}
                  </TableCell>
                  <TableCell className="py-4">
                    <Select
                      defaultValue={attendance?.status || "ABSENT"}
                      value={attendance?.status || "ABSENT"}
                      onValueChange={(val) =>
                        handleStatusChange(employee.id, val as AttendanceStatus)
                      }
                    >
                      <SelectTrigger className="w-[140px] border-zinc-200 shadow-sm">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="PRESENT">Present</SelectItem>
                        <SelectItem value="LATE">Late</SelectItem>
                        <SelectItem value="ABSENT">Absent</SelectItem>
                        <SelectItem value="LEAVE">On Leave</SelectItem>
                        <SelectItem value="OFF">Day Off</SelectItem>
                      </SelectContent>
                    </Select>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}

function SummaryCard({
  title,
  count,
  indicatorColor,
}: {
  title: string;
  count: number;
  indicatorColor: string;
}) {
  return (
    <div className="bg-white rounded-2xl border border-zinc-100 shadow-sm p-5 hover:border-emerald-100 transition-colors duration-300">
      <div className="flex items-center gap-3 mb-2">
        <div className={`w-1.5 h-1.5 rounded-full ${indicatorColor}`} />
        <span className="text-sm font-medium text-zinc-500 uppercase tracking-wide">
          {title}
        </span>
      </div>
      <div className="flex items-baseline gap-2 pl-4">
        <span className="text-3xl font-bold text-zinc-900">{count}</span>
      </div>
    </div>
  );
}

"use client";

import { LeaveRequest } from "@/prisma/generated/prisma/client";
import { RequestLeaveDialog } from "./RequestLeaveDialog";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { format } from "date-fns";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { cancelLeaveRequest } from "@/app/actions/leave-request";
import { toast } from "sonner";
import { useState } from "react";
import { Loader2 } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

interface EmployeeLeaveClientProps {
  employeeId: number;
  businessId: string;
  businessSlug: string; // Added prop
  requests: LeaveRequest[];
}

export function EmployeeLeaveClient({
  employeeId,
  businessId,
  businessSlug,
  requests,
}: EmployeeLeaveClientProps) {
  const [cancellingId, setCancellingId] = useState<number | null>(null);

  const getStatusColor = (status: string) => {
    switch (status) {
      case "APPROVED":
        return "bg-green-500 hover:bg-green-600";
      case "REJECTED":
        return "bg-red-500 hover:bg-red-600";
      default:
        return "bg-yellow-500 hover:bg-yellow-600";
    }
  };

  const handleCancel = async (id: number) => {
    try {
      setCancellingId(id);
      const result = await cancelLeaveRequest(id);
      if (result.success) {
        toast.success("Leave request cancelled");
      } else {
        toast.error(result.error || "Failed to cancel request");
      }
    } catch (error) {
      toast.error("An error occurred");
    } finally {
      setCancellingId(null);
    }
  };

  return (
    <Dialog>
      <DialogTrigger asChild>
        <Button variant="outline" className="w-full md:w-auto">
          Manage Leave Requests
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-[95vw] md:max-w-5xl!  h-[80vh] flex flex-col p-0">
        <DialogHeader className="p-6 pb-2">
          <DialogTitle>Leave Requests</DialogTitle>
          <DialogDescription>
            Manage your leave requests and view their status.
          </DialogDescription>
        </DialogHeader>

        <div className="flex-1 overflow-y-auto p-6 pt-2 space-y-6">
          <div className="flex justify-end">
            <RequestLeaveDialog
              employeeId={employeeId}
              businessId={businessId}
              businessSlug={businessSlug}
            />
          </div>

          <Card className="border-0 shadow-none">
            <CardHeader className="px-0 pt-0">
              <CardTitle>My Requests</CardTitle>
              <CardDescription>History of your leave requests.</CardDescription>
            </CardHeader>
            <CardContent className="px-0">
              {requests.length === 0 ? (
                <div className="text-center py-10 text-muted-foreground border rounded-lg border-dashed">
                  No leave requests found.
                </div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Dates</TableHead>
                      <TableHead>Type</TableHead>
                      <TableHead>Reason</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Admin Comment</TableHead>
                      <TableHead className="text-right">Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {requests.map((request) => (
                      <TableRow key={request.id}>
                        <TableCell>
                          <div className="flex flex-col">
                            <span>
                              {format(
                                new Date(request.start_date),
                                "MMM d, yyyy",
                              )}
                            </span>
                            <span className="text-xs text-muted-foreground">
                              to{" "}
                              {format(
                                new Date(request.end_date),
                                "MMM d, yyyy",
                              )}
                            </span>
                          </div>
                        </TableCell>
                        <TableCell>
                          <Badge variant="outline">{request.type}</Badge>
                        </TableCell>
                        <TableCell
                          className="max-w-[200px] truncate"
                          title={request.reason}
                        >
                          {request.reason}
                        </TableCell>
                        <TableCell>
                          <Badge
                            className={getStatusColor(request.status)}
                            variant="secondary"
                          >
                            {request.status}
                          </Badge>
                        </TableCell>
                        <TableCell className="max-w-[200px] truncate">
                          {request.admin_comment || "-"}
                        </TableCell>
                        <TableCell className="text-right">
                          {request.status === "PENDING" && (
                            <AlertDialog>
                              <AlertDialogTrigger asChild>
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  className="text-red-600 hover:text-red-700 hover:bg-red-50"
                                  disabled={cancellingId === request.id}
                                >
                                  {cancellingId === request.id ? (
                                    <Loader2 className="h-4 w-4 animate-spin" />
                                  ) : (
                                    "Cancel"
                                  )}
                                </Button>
                              </AlertDialogTrigger>
                              <AlertDialogContent>
                                <AlertDialogHeader>
                                  <AlertDialogTitle>
                                    Cancel Leave Request
                                  </AlertDialogTitle>
                                  <AlertDialogDescription>
                                    Are you sure you want to cancel this leave
                                    request? This action cannot be undone.
                                  </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                  <AlertDialogCancel>
                                    Keep Request
                                  </AlertDialogCancel>
                                  <AlertDialogAction
                                    onClick={() => handleCancel(request.id)}
                                    className="bg-red-600 hover:bg-red-700"
                                  >
                                    Yes, Cancel Request
                                  </AlertDialogAction>
                                </AlertDialogFooter>
                              </AlertDialogContent>
                            </AlertDialog>
                          )}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import {
  LeaveRequest,
  Employee,
  User,
  LeaveRequestStatus,
} from "@/prisma/generated/prisma/client";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { format } from "date-fns";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { LeaveActionDialog } from "./LeaveActionDialog";
import { User as UserIcon, Calendar, MessageSquare, Clock } from "lucide-react";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";

// Helper functions for avatars
const getInitials = (name: string) => {
  return name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2);
};

const getAvatarColor = (name: string) => {
  const colors = [
    "bg-blue-500",
    "bg-emerald-500",
    "bg-purple-500",
    "bg-orange-500",
    "bg-pink-500",
    "bg-cyan-500",
    "bg-indigo-500",
    "bg-teal-500",
  ];
  const index = name.charCodeAt(0) % colors.length;
  return colors[index];
};

type LeaveRequestWithEmployee = LeaveRequest & {
  employee: Employee & {
    user: User;
  };
};

interface LeaveRequestsTabProps {
  requests: LeaveRequestWithEmployee[];
  params: { businessSlug: string };
}

export function LeaveRequestsTab({ requests }: LeaveRequestsTabProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case "APPROVED":
        return "bg-green-500 hover:bg-green-600";
      case "REJECTED":
        return "bg-red-500 hover:bg-red-600";
      default:
        return "bg-yellow-500 hover:bg-yellow-600";
    }
  };

  return (
    <div className="space-y-6">
      {/* Mobile Card View */}
      <div className="md:hidden grid grid-cols-1 gap-4">
        {requests.length === 0 ? (
          <div className="text-center py-12 bg-white rounded-2xl border border-zinc-100">
            <UserIcon className="h-12 w-12 text-zinc-300 mx-auto mb-3" />
            <h3 className="text-lg font-medium text-zinc-900">
              No requests found
            </h3>
            <p className="text-zinc-500">
              There are no pending leave requests.
            </p>
          </div>
        ) : (
          requests.map((request) => (
            <div
              key={request.id}
              className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-4"
            >
              <div className="flex justify-between items-start">
                <div className="flex items-center gap-3">
                  <Avatar
                    className={`${getAvatarColor(request.employee.user.name)} h-10 w-10 text-white shadow-sm ring-2 ring-white`}
                  >
                    <AvatarFallback className="bg-transparent font-medium">
                      {getInitials(request.employee.user.name)}
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex flex-col">
                    <span className="font-semibold text-zinc-900">
                      {request.employee.user.name}
                    </span>
                    <Badge
                      className={`w-fit mt-1 border-0 ${getStatusColor(request.status)}`}
                    >
                      {request.status}
                    </Badge>
                  </div>
                </div>
              </div>

              <div className="space-y-3 pt-2 border-t border-zinc-50">
                <div className="flex items-start gap-2 text-sm text-zinc-600">
                  <Calendar className="h-4 w-4 mt-0.5 text-zinc-400" />
                  <div className="flex flex-col">
                    <span className="text-xs text-zinc-400 font-medium uppercase">
                      Dates
                    </span>
                    <span className="font-medium text-zinc-900">
                      {format(new Date(request.start_date), "MMM d, yyyy")} -{" "}
                      {format(new Date(request.end_date), "MMM d, yyyy")}
                    </span>
                  </div>
                </div>

                <div className="flex items-start gap-2 text-sm text-zinc-600">
                  <MessageSquare className="h-4 w-4 mt-0.5 text-zinc-400" />
                  <div className="flex flex-col">
                    <span className="text-xs text-zinc-400 font-medium uppercase">
                      Reason
                    </span>
                    <span>{request.reason}</span>
                  </div>
                </div>
              </div>

              {request.status === "PENDING" && (
                <div className="grid grid-cols-2 gap-3 pt-2 border-t border-zinc-50">
                  <LeaveActionDialog
                    requestId={request.id}
                    action="REJECT"
                    trigger={
                      <Button
                        variant="outline"
                        className="w-full border-red-200 text-red-600 hover:bg-red-50 hover:text-red-700"
                      >
                        Reject
                      </Button>
                    }
                  />
                  <LeaveActionDialog
                    requestId={request.id}
                    action="APPROVE"
                    trigger={
                      <Button className="w-full bg-emerald-600 hover:bg-emerald-700">
                        Approve
                      </Button>
                    }
                  />
                </div>
              )}
            </div>
          ))
        )}
      </div>

      {/* Desktop Table View */}
      <div className="hidden md:block rounded-3xl border border-zinc-100 overflow-hidden shadow-sm bg-white">
        <Table>
          <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
            <TableRow className="hover:bg-transparent border-zinc-100">
              <TableHead className="pl-6 h-12 font-semibold text-zinc-500">
                Employee
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Dates
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Reason
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Status
              </TableHead>
              <TableHead className="h-12 font-semibold text-zinc-500">
                Admin Comment
              </TableHead>
              <TableHead className="text-right pr-6 h-12 font-semibold text-zinc-500">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {requests.length === 0 ? (
              <TableRow>
                <TableCell
                  colSpan={6}
                  className="text-center py-12 text-muted-foreground"
                >
                  <div className="flex flex-col items-center gap-2">
                    <UserIcon className="h-8 w-8 text-zinc-300" />
                    <p>No leave requests found.</p>
                  </div>
                </TableCell>
              </TableRow>
            ) : (
              requests.map((request) => (
                <TableRow
                  key={request.id}
                  className="hover:bg-zinc-50/50 transition-colors border-zinc-100"
                >
                  <TableCell className="pl-6 py-4">
                    <div className="flex items-center gap-3">
                      <Avatar
                        className={`${getAvatarColor(request.employee.user.name)} h-9 w-9 text-white shadow-sm ring-2 ring-white`}
                      >
                        <AvatarFallback className="bg-transparent font-medium">
                          {getInitials(request.employee.user.name)}
                        </AvatarFallback>
                      </Avatar>
                      <span className="font-medium text-zinc-900">
                        {request.employee.user.name}
                      </span>
                    </div>
                  </TableCell>
                  <TableCell className="py-4">
                    <div className="flex flex-col text-sm">
                      <span className="font-medium text-zinc-700">
                        {format(new Date(request.start_date), "MMM d, yyyy")}
                      </span>
                      <span className="text-zinc-400 text-xs">
                        to {format(new Date(request.end_date), "MMM d, yyyy")}
                      </span>
                    </div>
                  </TableCell>
                  <TableCell className="py-4 max-w-[200px] truncate text-zinc-600">
                    {request.reason}
                  </TableCell>
                  <TableCell className="py-4">
                    <Badge
                      className={`border-0 ${getStatusColor(request.status)}`}
                    >
                      {request.status}
                    </Badge>
                  </TableCell>
                  <TableCell className="py-4 text-zinc-500 text-sm">
                    {request.admin_comment || "-"}
                  </TableCell>
                  <TableCell className="text-right pr-6 py-4">
                    {request.status === "PENDING" && (
                      <div className="flex justify-end gap-2">
                        <LeaveActionDialog
                          requestId={request.id}
                          action="REJECT"
                          trigger={
                            <Button
                              size="sm"
                              variant="outline"
                              className="text-red-600 border-red-200 hover:bg-red-50"
                            >
                              Reject
                            </Button>
                          }
                        />
                        <LeaveActionDialog
                          requestId={request.id}
                          action="APPROVE"
                          trigger={
                            <Button
                              size="sm"
                              className="bg-emerald-600 hover:bg-emerald-700 text-white"
                            >
                              Approve
                            </Button>
                          }
                        />
                      </div>
                    )}
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}

"use client";

import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import { format } from "date-fns";
import { CalendarIcon, Loader2 } from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Calendar } from "@/components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { cn } from "@/lib/utils";
import { Textarea } from "@/components/ui/textarea";
import { createLeaveRequest } from "@/app/actions/leave-request";
import { toast } from "sonner";
import { LeaveType } from "@/prisma/generated/prisma/enums";

const formSchema = z.object({
  dateRange: z.object({
    from: z.date(),
    to: z.date(),
  }),
  reason: z.string().min(3, "Reason must be at least 3 characters"),
  type: z.nativeEnum(LeaveType),
});

interface RequestLeaveDialogProps {
  employeeId: number;
  businessId: string;
  businessSlug: string; // Added prop
  onSuccess?: () => void;
}

export function RequestLeaveDialog({
  employeeId,
  businessId,
  businessSlug,
  onSuccess,
}: RequestLeaveDialogProps) {
  const [open, setOpen] = useState(false);

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      reason: "",
      type: LeaveType.VACATION,
    },
  });

  const onSubmit = async (values: z.infer<typeof formSchema>) => {
    try {
      const result = await createLeaveRequest({
        employee_id: employeeId,
        business_id: businessId,
        start_date: values.dateRange.from,
        end_date: values.dateRange.to,
        reason: values.reason,
        type: values.type,
        businessSlug: businessSlug, // Passed slug
      });

      if (result.success) {
        toast.success("Leave request submitted");
        setOpen(false);
        form.reset();
        if (onSuccess) onSuccess();
      } else {
        toast.error(result.error || "Failed to submit request");
      }
    } catch (error) {
      console.error(error);
      toast.error("An error occurred");
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button>Request Leave</Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Request Leave</DialogTitle>
          <DialogDescription>
            Select the dates, type, and provide a reason for your leave.
          </DialogDescription>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="type"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Leave Type</FormLabel>
                  <Select
                    onValueChange={field.onChange}
                    defaultValue={field.value}
                  >
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select a leave type" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {Object.values(LeaveType).map((type) => (
                        <SelectItem key={type} value={type}>
                          {type.charAt(0) + type.slice(1).toLowerCase()}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="dateRange"
              render={({ field }) => (
                <FormItem className="flex flex-col">
                  <FormLabel>Dates</FormLabel>
                  <Popover>
                    <PopoverTrigger asChild>
                      <FormControl>
                        <Button
                          variant={"outline"}
                          className={cn(
                            "w-full pl-3 text-left font-normal",
                            !field.value && "text-muted-foreground",
                          )}
                        >
                          {field.value?.from ? (
                            field.value.to ? (
                              <>
                                {format(field.value.from, "LLL dd, y")} -{" "}
                                {format(field.value.to, "LLL dd, y")}
                              </>
                            ) : (
                              format(field.value.from, "LLL dd, y")
                            )
                          ) : (
                            <span>Pick a date range</span>
                          )}
                          <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                        </Button>
                      </FormControl>
                    </PopoverTrigger>
                    <PopoverContent className="w-auto p-0" align="start">
                      <Calendar
                        mode="range"
                        selected={field.value}
                        onSelect={field.onChange}
                        disabled={
                          (date) => date < new Date() // Disable past dates
                        }
                        initialFocus
                      />
                    </PopoverContent>
                  </Popover>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="reason"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Reason</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="e.g., Family vacation, Sick leave"
                      className="resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <DialogFooter>
              <Button type="submit" disabled={form.formState.isSubmitting}>
                {form.formState.isSubmitting && (
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                )}
                Submit Request
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { useState, useMemo, useOptimistic, useTransition } from "react";
import { useRouter } from "next/navigation";
import { Customer } from "@/prisma/generated/prisma/client";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import {
  Search,
  Plus,
  Pencil,
  Trash2,
  Users,
  MoreHorizontal,
  Mail,
  Phone,
  Calendar,
} from "lucide-react";
import {
  createCustomer,
  updateCustomer,
  deleteCustomer,
  getCustomerHistory,
  type CustomerHistoryData,
} from "@/lib/server actions/customer";
import { toast } from "sonner";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { CustomerForm, CustomerFormData } from "./CustomerForm";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useQuery } from "@tanstack/react-query";
import { formatPH } from "@/lib/date-utils";

interface CustomersClientProps {
  customers: Customer[];
  businessSlug: string;
}

const initialFormData: CustomerFormData = {
  name: "",
  email: "",
  phone: "",
};

type OptimisticAction =
  | { type: "add"; customer: Customer }
  | { type: "update"; customer: Customer }
  | { type: "delete"; id: string };

export function CustomersClient({
  customers,
  businessSlug,
}: CustomersClientProps) {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(
    null,
  );
  const [isHistoryDialogOpen, setIsHistoryDialogOpen] = useState(false);
  const [historyCustomer, setHistoryCustomer] = useState<Customer | null>(null);
  const [formData, setFormData] = useState<CustomerFormData>(initialFormData);
  const [isLoading, setIsLoading] = useState(false);
  const [isPending, startTransition] = useTransition();

  const [optimisticCustomers, addOptimisticUpdate] = useOptimistic(
    customers,
    (state: Customer[], action: OptimisticAction) => {
      switch (action.type) {
        case "add":
          return [...state, action.customer];
        case "update":
          return state.map((c) =>
            c.id === action.customer.id ? action.customer : c,
          );
        case "delete":
          return state.filter((c) => c.id !== action.id);
        default:
          return state;
      }
    },
  );

  const filteredCustomers = useMemo(() => {
    return optimisticCustomers.filter((customer) => {
      const searchLower = search.toLowerCase();
      return (
        customer.name.toLowerCase().includes(searchLower) ||
        (customer.email && customer.email.toLowerCase().includes(searchLower))
      );
    });
  }, [optimisticCustomers, search]);

  const { data: historyResult, isFetching: isHistoryLoading } = useQuery({
    queryKey: ["customerHistory", historyCustomer?.id],
    queryFn: () =>
      historyCustomer
        ? getCustomerHistory(historyCustomer.id)
        : Promise.resolve(null),
    enabled: !!historyCustomer && isHistoryDialogOpen,
    staleTime: 1000 * 60 * 5,
    gcTime: 1000 * 60 * 30,
    refetchOnWindowFocus: false,
  });

  const handleAddCustomer = async () => {
    if (!formData.name) {
      toast.error("Please enter a customer name");
      return;
    }

    setIsLoading(true);
    // Optimistic update
    const tempId = `temp-${Date.now()}`;
    const newCustomer = {
      id: tempId,
      name: formData.name,
      email: formData.email || null,
      phone: formData.phone || null,
      business_id: "", // placeholder
      created_at: new Date(),
      updated_at: new Date(),
    } as Customer;

    startTransition(async () => {
      addOptimisticUpdate({ type: "add", customer: newCustomer });
      const result = await createCustomer({
        businessSlug,
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
      });

      setIsLoading(false);
      if (result.success) {
        toast.success("Customer created successfully");
        setIsAddDialogOpen(false);
        setFormData(initialFormData);
        router.refresh();
      } else {
        toast.error(result.error || "Failed to create customer");
        router.refresh(); // revert optimistic
      }
    });
  };

  const handleEditCustomer = async () => {
    if (!selectedCustomer || !formData.name) {
      toast.error("Please enter a customer name");
      return;
    }

    const updatedCustomer = {
      ...selectedCustomer,
      name: formData.name,
      email: formData.email || null,
      phone: formData.phone || null,
    };

    setIsEditDialogOpen(false);
    startTransition(async () => {
      addOptimisticUpdate({ type: "update", customer: updatedCustomer });
      const result = await updateCustomer(selectedCustomer.id, {
        businessSlug,
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
      });

      if (result.success) {
        toast.success("Customer updated successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to update customer");
        router.refresh(); // revert
      }
      setSelectedCustomer(null);
      setFormData(initialFormData);
    });
  };

  const handleDeleteCustomer = async (id: string) => {
    startTransition(async () => {
      addOptimisticUpdate({ type: "delete", id });
      const result = await deleteCustomer(id);

      if (result.success) {
        toast.success("Customer deleted successfully");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to delete customer");
        router.refresh(); // revert
      }
    });
  };

  const openEditDialog = (customer: Customer) => {
    setSelectedCustomer(customer);
    setFormData({
      name: customer.name,
      email: customer.email || "",
      phone: customer.phone || "",
    });
    setIsEditDialogOpen(true);
  };

  const openHistoryDialog = (customer: Customer) => {
    setHistoryCustomer(customer);
    setIsHistoryDialogOpen(true);
  };

  const closeHistoryDialog = (open: boolean) => {
    setIsHistoryDialogOpen(open);
    if (!open) {
      setHistoryCustomer(null);
    }
  };

  const historyData: CustomerHistoryData | null = historyResult?.success
    ? historyResult.data
    : null;

  const pendingFlows = useMemo(() => {
    if (!historyData) return [];
    return historyData.flowStatus.filter((flow) => flow.status === "PENDING");
  }, [historyData]);

  const nextFlowDue = useMemo(() => {
    if (!pendingFlows.length) return null;
    return pendingFlows
      .slice()
      .sort(
        (a, b) => new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime(),
      )[0];
  }, [pendingFlows]);

  const getBookingStatusClass = (status: string) => {
    switch (status) {
      case "ACCEPTED":
        return "bg-emerald-50 text-emerald-700 border-emerald-200";
      case "COMPLETED":
        return "bg-blue-50 text-blue-700 border-blue-200";
      case "HOLD":
        return "bg-amber-50 text-amber-700 border-amber-200";
      case "CANCELLED":
        return "bg-red-50 text-red-700 border-red-200";
      default:
        return "bg-zinc-50 text-zinc-700 border-zinc-200";
    }
  };

  const getFlowStatusClass = (status: string) => {
    return status === "COMPLETED"
      ? "bg-emerald-50 text-emerald-700 border-emerald-200"
      : "bg-amber-50 text-amber-700 border-amber-200";
  };

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  const getAvatarColor = (name: string) => {
    const colors = [
      "bg-blue-500",
      "bg-green-500",
      "bg-purple-500",
      "bg-orange-500",
      "bg-pink-500",
      "bg-cyan-500",
      "bg-indigo-500",
      "bg-teal-500",
    ];
    const index = name.charCodeAt(0) % colors.length;
    return colors[index];
  };

  return (
    <div className="flex flex-col p-4 md:p-8 bg-zinc-50/50 min-h-screen">
      <section className="flex-1 flex flex-col w-full max-w-7xl mx-auto space-y-6">
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-2xl font-bold text-zinc-900 tracking-tight">
              Customers
            </h1>
            <p className="text-sm text-zinc-500 mt-1">
              Manage your customer database
            </p>
          </div>
          <div className="flex items-center gap-2">
            <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-emerald-600 hover:bg-emerald-700 text-white shadow-sm shadow-emerald-200 w-full md:w-auto">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Customer
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[500px]">
                <DialogHeader>
                  <DialogTitle>Add New Customer</DialogTitle>
                  <DialogDescription>
                    Create a new customer profile.
                  </DialogDescription>
                </DialogHeader>
                <CustomerForm formData={formData} setFormData={setFormData} />
                <DialogFooter>
                  <Button
                    variant="outline"
                    onClick={() => setIsAddDialogOpen(false)}
                    className="border-zinc-200 hover:bg-zinc-50 hover:text-zinc-900"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={handleAddCustomer}
                    disabled={isLoading}
                    className="bg-emerald-600 hover:bg-emerald-700 text-white"
                  >
                    {isLoading ? "Creating..." : "Create Customer"}
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        <div className="flex flex-col sm:flex-row gap-3">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-zinc-400" />
            <Input
              placeholder="Search customers..."
              className="pl-10 bg-white border-zinc-200 focus-visible:ring-emerald-500"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>
        </div>

        <div className="flex-1">
          {filteredCustomers.length === 0 ? (
            <div className="text-center py-12 bg-white rounded-2xl border border-zinc-100 shadow-sm">
              <div className="bg-zinc-50 h-16 w-16 mobile-center rounded-full flex items-center justify-center mx-auto mb-4">
                <Users className="h-8 w-8 text-zinc-400" />
              </div>
              <h3 className="text-lg font-medium text-zinc-900">
                No customers found
              </h3>
              <p className="text-zinc-500 max-w-sm mx-auto mt-1 mb-6">
                {customers.length === 0
                  ? "Get started by adding your first customer."
                  : "Try adjusting your search terms."}
              </p>
              {customers.length === 0 && (
                <Button
                  onClick={() => setIsAddDialogOpen(true)}
                  className="bg-emerald-600 hover:bg-emerald-700 text-white"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Add Your First Customer
                </Button>
              )}
            </div>
          ) : (
            <div className="space-y-6">
              {/* Mobile Card View */}
              <div className="md:hidden grid grid-cols-1 gap-4">
                {filteredCustomers.map((customer) => (
                  <div
                    key={customer.id}
                    className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-4 cursor-pointer hover:border-emerald-100 hover:shadow-md transition"
                    onClick={() => openHistoryDialog(customer)}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <Avatar
                          className={`${getAvatarColor(customer.name)} h-12 w-12 text-white shadow-sm ring-2 ring-white`}
                        >
                          <AvatarFallback className="bg-transparent font-medium text-lg">
                            {getInitials(customer.name)}
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex flex-col">
                          <span className="font-bold text-zinc-900 text-lg">
                            {customer.name}
                          </span>
                          {customer.email && (
                            <span className="text-sm text-zinc-500 flex items-center gap-1.5">
                              <Mail className="h-3.5 w-3.5" />
                              {customer.email}
                            </span>
                          )}
                        </div>
                      </div>
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button
                            variant="ghost"
                            size="icon"
                            className="h-8 w-8 text-zinc-400"
                            onClick={(event) => event.stopPropagation()}
                          >
                            <MoreHorizontal className="h-4 w-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuLabel>Actions</DropdownMenuLabel>
                          <DropdownMenuSeparator />
                          <DropdownMenuItem
                            onClick={() => openHistoryDialog(customer)}
                          >
                            <Calendar className="h-4 w-4 mr-2" />
                            View History
                          </DropdownMenuItem>
                          <DropdownMenuSeparator />
                          <DropdownMenuItem
                            onClick={() => openEditDialog(customer)}
                          >
                            <Pencil className="h-4 w-4 mr-2" />
                            Edit Details
                          </DropdownMenuItem>
                          <DropdownMenuSeparator />
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <DropdownMenuItem
                                onSelect={(e) => e.preventDefault()}
                                className="text-red-600 focus:text-red-600 focus:bg-red-50"
                              >
                                <Trash2 className="h-4 w-4 mr-2" />
                                Delete Customer
                              </DropdownMenuItem>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>
                                  Delete Customer
                                </AlertDialogTitle>
                                <AlertDialogDescription>
                                  Are you sure you want to delete "
                                  {customer.name}"? This action cannot be
                                  undone.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction
                                  className="bg-red-600 hover:bg-red-700 text-white"
                                  onClick={() =>
                                    handleDeleteCustomer(customer.id)
                                  }
                                >
                                  Delete
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </div>

                    <div className="grid grid-cols-1 gap-3 pt-2 border-t border-zinc-50">
                      <div className="flex items-center justify-between p-2.5 bg-zinc-50 rounded-xl border border-zinc-100">
                        <span className="text-xs text-zinc-400 font-medium uppercase">
                          Contact Info
                        </span>
                        {customer.phone ? (
                          <div className="flex items-center gap-2 text-sm text-zinc-900 font-medium">
                            <Phone className="h-3.5 w-3.5 text-zinc-400" />
                            {customer.phone}
                          </div>
                        ) : (
                          <span className="text-sm text-zinc-400 italic">
                            No phone number
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Desktop Table View */}
              <div className="hidden md:block rounded-3xl border border-zinc-100 overflow-hidden shadow-sm bg-white">
                <Table>
                  <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
                    <TableRow className="hover:bg-transparent border-zinc-100">
                      <TableHead className="pl-6 h-12 font-semibold text-zinc-500 w-[300px]">
                        Customer
                      </TableHead>
                      <TableHead className="h-12 font-semibold text-zinc-500">
                        Phone
                      </TableHead>
                      <TableHead className="text-right pr-6 h-12 font-semibold text-zinc-500 w-[80px]">
                        Actions
                      </TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredCustomers.map((customer) => (
                      <TableRow
                        key={customer.id}
                        className="hover:bg-zinc-50/50 transition-colors border-zinc-100 cursor-pointer"
                        onClick={() => openHistoryDialog(customer)}
                      >
                        <TableCell className="pl-6 py-4">
                          <div className="flex items-center gap-3">
                            <Avatar
                              className={`${getAvatarColor(customer.name)} h-10 w-10 text-white shadow-sm ring-2 ring-white`}
                            >
                              <AvatarFallback className="bg-transparent font-medium">
                                {getInitials(customer.name)}
                              </AvatarFallback>
                            </Avatar>
                            <div className="flex flex-col">
                              <span className="font-semibold text-zinc-900">
                                {customer.name}
                              </span>
                              {customer.email && (
                                <span className="text-xs text-zinc-500 flex items-center gap-1.5">
                                  <Mail className="h-3 w-3 text-zinc-400" />
                                  {customer.email}
                                </span>
                              )}
                            </div>
                          </div>
                        </TableCell>
                        <TableCell className="py-4">
                          {customer.phone ? (
                            <div className="flex items-center gap-2 text-sm text-zinc-600">
                              <Phone className="h-3 w-3 text-zinc-400" />
                              {customer.phone}
                            </div>
                          ) : (
                            <span className="text-zinc-400 text-sm italic">
                              -
                            </span>
                          )}
                        </TableCell>
                        <TableCell
                          className="text-right pr-6 py-4"
                          onClick={(event) => event.stopPropagation()}
                        >
                          <DropdownMenu>
                            <DropdownMenuTrigger asChild>
                              <Button
                                variant="ghost"
                                size="icon"
                                className="h-8 w-8 text-zinc-400 hover:text-zinc-900"
                                onClick={(event) => event.stopPropagation()}
                              >
                                <MoreHorizontal className="h-4 w-4" />
                              </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent align="end">
                              <DropdownMenuLabel>Actions</DropdownMenuLabel>
                              <DropdownMenuSeparator />
                              <DropdownMenuItem
                                onClick={() => openHistoryDialog(customer)}
                              >
                                <Calendar className="h-4 w-4 mr-2" />
                                View History
                              </DropdownMenuItem>
                              <DropdownMenuSeparator />
                              <DropdownMenuItem
                                onClick={() => openEditDialog(customer)}
                              >
                                <Pencil className="h-4 w-4 mr-2" />
                                Edit Details
                              </DropdownMenuItem>
                              <DropdownMenuSeparator />
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <DropdownMenuItem
                                    onSelect={(e) => e.preventDefault()}
                                    className="text-red-600 focus:text-red-600 focus:bg-red-50"
                                  >
                                    <Trash2 className="h-4 w-4 mr-2" />
                                    Delete Customer
                                  </DropdownMenuItem>
                                </AlertDialogTrigger>
                                <AlertDialogContent>
                                  <AlertDialogHeader>
                                    <AlertDialogTitle>
                                      Delete Customer
                                    </AlertDialogTitle>
                                    <AlertDialogDescription>
                                      Are you sure you want to delete "
                                      {customer.name}"? This action cannot be
                                      undone.
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel>
                                      Cancel
                                    </AlertDialogCancel>
                                    <AlertDialogAction
                                      className="bg-red-600 hover:bg-red-700 text-white"
                                      onClick={() =>
                                        handleDeleteCustomer(customer.id)
                                      }
                                    >
                                      Delete
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            </DropdownMenuContent>
                          </DropdownMenu>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            </div>
          )}
        </div>

        {/* History Dialog */}
        <Dialog open={isHistoryDialogOpen} onOpenChange={closeHistoryDialog}>
          <DialogContent className="sm:max-w-[900px]">
            <DialogHeader>
              <DialogTitle>Customer History</DialogTitle>
              <DialogDescription>
                {historyCustomer
                  ? `Overview for ${historyCustomer.name}`
                  : "Customer overview"}
              </DialogDescription>
            </DialogHeader>

            {isHistoryLoading && (
              <div className="text-sm text-zinc-500">Loading history...</div>
            )}

            {!isHistoryLoading && historyResult?.success === false && (
              <div className="text-sm text-red-600">
                {historyResult.error || "Failed to load history"}
              </div>
            )}

            {!isHistoryLoading && historyData && (
              <div className="space-y-5">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                  <Card className="border-zinc-100">
                    <CardContent className="p-4 space-y-2">
                      <p className="text-xs uppercase tracking-wider font-semibold text-zinc-500">
                        Next Appointment
                      </p>
                      {historyData.nextAppointment ? (
                        <div className="space-y-1">
                          <p className="text-sm font-semibold text-zinc-900">
                            {formatPH(
                              historyData.nextAppointment.scheduledAt,
                              "PPP p",
                            )}
                          </p>
                          <p className="text-xs text-zinc-500">
                            {historyData.nextAppointment.services.length > 0
                              ? historyData.nextAppointment.services.join(", ")
                              : "No services listed"}
                          </p>
                        </div>
                      ) : (
                        <p className="text-sm text-zinc-500">
                          No upcoming appointment scheduled.
                        </p>
                      )}
                    </CardContent>
                  </Card>

                  <Card className="border-zinc-100">
                    <CardContent className="p-4 space-y-2">
                      <p className="text-xs uppercase tracking-wider font-semibold text-zinc-500">
                        Next Recommended (Service Flow)
                      </p>
                      {nextFlowDue ? (
                        <div className="space-y-1">
                          <p className="text-sm font-semibold text-zinc-900">
                            {nextFlowDue.suggestedServiceName}
                          </p>
                          <p className="text-xs text-zinc-500">
                            Due {formatPH(nextFlowDue.dueDate, "MMM d, yyyy")}
                          </p>
                          <div className="flex items-center gap-2">
                            <Badge
                              className={`border ${getFlowStatusClass(nextFlowDue.status)}`}
                            >
                              {nextFlowDue.status}
                            </Badge>
                            <Badge className="bg-zinc-50 text-zinc-700 border border-zinc-200">
                              {nextFlowDue.flowType}
                            </Badge>
                          </div>
                        </div>
                      ) : (
                        <p className="text-sm text-zinc-500">
                          No pending service flows.
                        </p>
                      )}
                    </CardContent>
                  </Card>
                </div>

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <h4 className="text-sm font-semibold text-zinc-800">
                      Service Flow Status
                    </h4>
                    <span className="text-xs text-zinc-500">
                      {historyData.flowStatus.length} total
                    </span>
                  </div>

                  {historyData.flowStatus.length === 0 ? (
                    <div className="text-sm text-zinc-500">
                      No service flows configured for this customer.
                    </div>
                  ) : (
                    <div className="space-y-2">
                      {historyData.flowStatus.map((flow) => (
                        <div
                          key={`${flow.triggerServiceId}-${flow.suggestedServiceId}`}
                          className="flex flex-col gap-2 border border-zinc-100 rounded-xl p-3 bg-white"
                        >
                          <div className="flex flex-wrap items-center gap-2">
                            <span className="text-sm font-semibold text-zinc-900">
                              {flow.triggerServiceName}
                            </span>
                            <span className="text-xs text-zinc-400">-&gt;</span>
                            <span className="text-sm font-semibold text-zinc-900">
                              {flow.suggestedServiceName}
                            </span>
                            <Badge
                              className={`border ${getFlowStatusClass(flow.status)}`}
                            >
                              {flow.status}
                            </Badge>
                            <Badge className="bg-zinc-50 text-zinc-700 border border-zinc-200">
                              {flow.flowType}
                            </Badge>
                          </div>
                          <div className="text-xs text-zinc-500">
                            Last service:{" "}
                            {formatPH(flow.lastServiceDate, "MMM d, yyyy")}
                            {"  "}
                            {flow.status === "COMPLETED"
                              ? `Completed ${formatPH(flow.completedAt, "MMM d, yyyy")}`
                              : `Due ${formatPH(flow.dueDate, "MMM d, yyyy")}`}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <h4 className="text-sm font-semibold text-zinc-800">
                      Booking History
                    </h4>
                    <span className="text-xs text-zinc-500">
                      {historyData.bookings.length} total
                    </span>
                  </div>

                  {historyData.bookings.length === 0 ? (
                    <div className="text-sm text-zinc-500">
                      No bookings found for this customer.
                    </div>
                  ) : (
                    <ScrollArea className="h-[320px] pr-3">
                      <div className="space-y-3">
                        {historyData.bookings.map((booking) => {
                          const serviceNames =
                            booking.availed_services
                              ?.map((item) => item.service?.name)
                              .filter(Boolean) || [];

                          const displayDate =
                            booking.scheduled_at || booking.created_at;

                          return (
                            <div
                              key={booking.id}
                              className="border border-zinc-100 rounded-xl p-3 bg-white"
                            >
                              <div className="flex flex-wrap items-center justify-between gap-2">
                                <div className="text-sm font-semibold text-zinc-900">
                                  Booking #{booking.id}
                                </div>
                                <Badge
                                  className={`border ${getBookingStatusClass(booking.status)}`}
                                >
                                  {booking.status}
                                </Badge>
                              </div>
                              <div className="text-xs text-zinc-500 mt-1">
                                {formatPH(displayDate, "PPP p")}
                              </div>
                              <div className="text-xs text-zinc-600 mt-2">
                                {serviceNames.length > 0
                                  ? `Services: ${serviceNames.join(", ")}`
                                  : "Services: Not listed"}
                              </div>
                              <div className="text-xs text-zinc-600 mt-1">
                                Payment: {booking.payment_method}  Total: PHP{" "}
                                {booking.grand_total.toLocaleString()}
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </ScrollArea>
                  )}
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>

        {/* Edit Dialog */}
        <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle>Edit Customer</DialogTitle>
              <DialogDescription>
                Update customer information.
              </DialogDescription>
            </DialogHeader>
            <CustomerForm formData={formData} setFormData={setFormData} />
            <DialogFooter>
              <Button
                variant="outline"
                onClick={() => setIsEditDialogOpen(false)}
                className="border-zinc-200 hover:bg-zinc-50 hover:text-zinc-900"
              >
                Cancel
              </Button>
              <Button
                onClick={handleEditCustomer}
                disabled={isLoading}
                className="bg-emerald-600 hover:bg-emerald-700 text-white"
              >
                {isLoading ? "Saving..." : "Save Changes"}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </section>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import {
  eachDayOfInterval,
  endOfMonth,
  format,
  isSameDay,
  isSameMonth,
  isToday,
  startOfMonth,
  startOfToday,
} from "date-fns";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { useState, useEffect } from "react";

interface AttendanceRecord {
  date: Date | string;
  status: string;
  time_in?: Date | string | null;
  time_out?: Date | string | null;
}

interface AttendanceCalendarProps {
  attendanceRecords: AttendanceRecord[];
  currentDate?: Date;
  onMonthChange?: (date: Date) => void;
}

export default function AttendanceCalendar({
  attendanceRecords,
  currentDate = new Date(),
  onMonthChange,
}: AttendanceCalendarProps) {
  const [displayedMonth, setDisplayedMonth] = useState(
    startOfMonth(currentDate),
  );

  useEffect(() => {
    setDisplayedMonth(startOfMonth(currentDate));
  }, [currentDate]);

  const daysInMonth = eachDayOfInterval({
    start: startOfMonth(displayedMonth),
    end: endOfMonth(displayedMonth),
  });

  const getStatusColor = (day: Date) => {
    if (!isSameMonth(day, displayedMonth))
      return "text-zinc-300 bg-transparent";

    const record = attendanceRecords.find((r) =>
      isSameDay(new Date(r.date), day),
    );

    if (record) {
      switch (record.status) {
        case "PRESENT":
          return "bg-green-100/50 text-green-700 border-green-200";
        case "ABSENT":
          return "bg-red-100/50 text-red-700 border-red-200";
        case "LATE":
          return "bg-yellow-100/50 text-yellow-700 border-yellow-200";
        case "OFF":
          return "bg-gray-100 text-gray-500 border-gray-200";
        default:
          return "bg-zinc-50 text-zinc-900 border-zinc-100";
      }
    }

    if (isToday(day)) {
      return "bg-zinc-100 text-zinc-900 font-bold border-zinc-200";
    }

    return "bg-zinc-50/50 text-zinc-500 border-transparent hover:bg-zinc-50";
  };

  const nextMonth = () => {
    const newDate = new Date(
      displayedMonth.getFullYear(),
      displayedMonth.getMonth() + 1,
      1,
    );
    setDisplayedMonth(newDate);
    onMonthChange?.(newDate);
  };

  const prevMonth = () => {
    const newDate = new Date(
      displayedMonth.getFullYear(),
      displayedMonth.getMonth() - 1,
      1,
    );
    setDisplayedMonth(newDate);
    onMonthChange?.(newDate);
  };

  return (
    <div className="w-full h-full flex flex-col">
      <div className="flex items-center justify-between mb-4">
        <h4 className="font-semibold text-zinc-800">
          {format(displayedMonth, "MMMM yyyy")}
        </h4>
        <div className="flex gap-1">
          <button
            onClick={prevMonth}
            className="p-1 hover:bg-zinc-100 rounded text-zinc-500 hover:text-zinc-900 transition-colors"
          >
            <ChevronLeft size={16} />
          </button>
          <button
            onClick={nextMonth}
            className="p-1 hover:bg-zinc-100 rounded text-zinc-500 hover:text-zinc-900 transition-colors"
          >
            <ChevronRight size={16} />
          </button>
        </div>
      </div>

      <div className="grid grid-cols-7 gap-2 text-center text-xs text-zinc-400 mb-2">
        <div>Sun</div>
        <div>Mon</div>
        <div>Tue</div>
        <div>Wed</div>
        <div>Thu</div>
        <div>Fri</div>
        <div>Sat</div>
      </div>

      <div className="grid grid-cols-7 gap-2 flex-1">
        {daysInMonth.map((day, idx) => {
          // align first day
          const colStart = idx === 0 ? `col-start-${day.getDay() + 1}` : "";

          return (
            <div
              key={day.toISOString()}
              className={cn(
                "aspect-square rounded-xl flex flex-col items-center justify-center text-xs border transition-colors",
                getStatusColor(day),
                colStart,
              )}
            >
              <span className="font-medium">{format(day, "d")}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
}

"use client";

import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import {
  MapPin,
  Loader2,
  PlayCircle,
  StopCircle,
  CalendarDays,
} from "lucide-react";
import { toast } from "sonner";
import {
  clockInAction,
  clockOutAction,
  getMonthlyAttendanceAction,
} from "@/lib/server actions/attendance";
import AttendanceCalendar from "./AttendanceCalendar";
import { motion, AnimatePresence } from "motion/react";

interface AttendanceCardProps {
  employeeId: number;
  businessSlug: string;
  todayAttendance: {
    time_in: Date | null;
    time_out: Date | null;
  } | null;
}

export default function AttendanceCard({
  employeeId,
  businessSlug,
  todayAttendance,
  className,
}: AttendanceCardProps & { className?: string }) {
  const [loading, setLoading] = useState(false);
  const [showCalendar, setShowCalendar] = useState(false);
  const [monthlyAttendance, setMonthlyAttendance] = useState<any[]>([]);
  const [calendarDate, setCalendarDate] = useState(new Date());

  const isClockedIn = !!todayAttendance?.time_in && !todayAttendance.time_out;
  const isClockedOut = !!todayAttendance?.time_out;

  useEffect(() => {
    const fetchAttendance = async () => {
      const result = await getMonthlyAttendanceAction(
        employeeId,
        calendarDate.getFullYear(),
        calendarDate.getMonth(),
      );
      if (result.success && result.data) {
        setMonthlyAttendance(result.data);
      }
    };

    fetchAttendance();
  }, [employeeId, calendarDate, todayAttendance]);

  const handleClockIn = async () => {
    setLoading(true);

    if (!navigator.geolocation) {
      toast.error("Geolocation is not supported by your browser");
      setLoading(false);
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 0,
    };

    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        const result = await clockInAction(
          employeeId,
          latitude,
          longitude,
          businessSlug,
        );

        if (result.success) {
          toast.success("Clocked in successfully!");
        } else {
          toast.error(result.error);
        }
        setLoading(false);
      },
      async (error) => {
        console.error("Geolocation error:", error);

        // Developer Bypass: If in dev mode, try to clock in with mock location
        if (process.env.NODE_ENV === "development") {
          toast.info("Dev Mode: Geolocation failed, using mock location.");
          // Use mock coordinates (0,0) - backend will skip check in dev
          const result = await clockInAction(employeeId, 0, 0, businessSlug);

          if (result.success) {
            toast.success("Clocked in successfully (Dev Bypass)!");
          } else {
            toast.error(result.error);
          }
          setLoading(false);
          return;
        }

        let errorMessage = "Unable to retrieve location.";
        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMessage =
              "Location permission denied. Please enable it in browser settings or OS settings.";
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = "Location information is unavailable.";
            break;
          case error.TIMEOUT:
            errorMessage = "Location request timed out. Please try again.";
            break;
        }
        toast.error(errorMessage);
        setLoading(false);
      },
      options,
    );
  };

  const handleClockOut = async () => {
    setLoading(true);
    const result = await clockOutAction(employeeId);
    if (result.success) {
      toast.success("Clocked out successfully!");
    } else {
      toast.error(result.error);
    }
    setLoading(false);
  };

  return (
    <motion.div
      layout="position"
      transition={{ type: "spring", stiffness: 300, damping: 30 }}
      className={`p-6 rounded-2xl flex flex-col justify-between bg-white text-slate-900 relative overflow-hidden transition-all duration-300 ${
        showCalendar ? "row-span-2" : "min-h-[160px]"
      } ${className}`}
    >
      <div className="flex justify-between items-start w-full relative z-10 mb-6">
        <div>
          <h3 className="text-xs font-bold text-slate-400 uppercase tracking-widest mb-1">
            Today's Attendance
          </h3>
          <p className="text-2xl font-bold tracking-tight text-slate-900">
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
            })}
          </p>
          <p className="text-sm text-slate-500 font-medium">
            {new Date().toLocaleDateString("en-US", {
              day: "numeric",
              month: "long",
              year: "numeric",
            })}
          </p>
        </div>
        <motion.button
          layout="position"
          onClick={() => setShowCalendar(!showCalendar)}
          className={`h-10 w-10 rounded-full flex items-center justify-center transition-all ${
            showCalendar
              ? "bg-slate-900 text-white shadow-lg"
              : "bg-slate-100 text-slate-600 hover:bg-slate-200"
          }`}
        >
          {showCalendar ? <MapPin size={18} /> : <CalendarDays size={18} />}
        </motion.button>
      </div>

      <div className="flex flex-col gap-6 relative z-10 h-full">
        <div className="w-full space-y-5">
          <div className="flex items-center gap-3 bg-slate-50 p-3 rounded-xl border border-slate-100">
            {isClockedOut ? (
              <>
                <div className="h-2.5 w-2.5 rounded-full bg-slate-400"></div>
                <span className="text-sm font-semibold text-slate-600">
                  Shift Completed
                </span>
              </>
            ) : isClockedIn ? (
              <>
                <div className="relative flex h-2.5 w-2.5">
                  <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-green-400 opacity-75"></span>
                  <span className="relative inline-flex rounded-full h-2.5 w-2.5 bg-green-500"></span>
                </div>
                <div className="flex flex-col">
                  <span className="text-sm font-bold text-green-700">
                    Clocked In
                  </span>
                  {todayAttendance?.time_in && (
                    <span className="text-xs text-green-600/80 font-medium">
                      Since{" "}
                      {new Date(todayAttendance.time_in).toLocaleTimeString(
                        "en-US",
                        { hour: "2-digit", minute: "2-digit" },
                      )}
                    </span>
                  )}
                </div>
              </>
            ) : (
              <>
                <div className="h-2.5 w-2.5 rounded-full bg-orange-400"></div>
                <span className="text-sm font-semibold text-slate-600">
                  Not Started
                </span>
              </>
            )}
          </div>

          {!isClockedOut && (
            <div>
              <Button
                onClick={isClockedIn ? handleClockOut : handleClockIn}
                disabled={loading}
                className={`w-full py-6 h-[50px]  text-base font-bold transition-all rounded-xl shadow-lg active:scale-[0.98] ${
                  isClockedIn
                    ? "bg-white text-red-600 hover:bg-red-50 border-2 border-red-100 shadow-red-100/50"
                    : "bg-slate-900 text-white hover:bg-slate-800 shadow-slate-900/20"
                }`}
              >
                {loading ? (
                  <Loader2 className="w-5 h-5 animate-spin mr-3" />
                ) : isClockedIn ? (
                  <StopCircle className="w-5 h-5 mr-3" />
                ) : (
                  <PlayCircle className="w-5 h-5 mr-3" />
                )}
                {loading
                  ? "Processing..."
                  : isClockedIn
                    ? "End Shift"
                    : "Start Shift"}
              </Button>
            </div>
          )}
        </div>

        <AnimatePresence>
          {showCalendar && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: "auto" }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="w-full shrink-0 border-t border-slate-100 pt-6 overflow-hidden"
            >
              <AttendanceCalendar
                attendanceRecords={monthlyAttendance}
                currentDate={calendarDate}
                onMonthChange={setCalendarDate}
              />
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
}

"use client";

import { LogOut, User as UserIcon } from "lucide-react";
import { Button } from "@/components/ui/button";
import EmployeeServedHistory from "./EmployeeServedHistory";
import PendingServicesList from "./PendingServicesList";
import AttendanceCard from "./AttendanceCard";
import { signOut } from "next-auth/react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { BookingDialog } from "../owner/BookingDialog";

import { EmployeeLeaveClient } from "../attendance/EmployeeLeaveClient";
import {
  Service,
  ServicePackage,
  LeaveRequest,
} from "@/prisma/generated/prisma/client";
import { PackageWithItems } from "@/components/bookings/BookingForm";

export default function EmployeeDashboard({
  businessName,
  businessSlug,
  businessId,
  servedHistory,
  pendingServices,
  currentEmployeeId,
  currentEmployeeCommission,
  currentEmployeeSalary,
  todayAttendance,
  services,
  packages,
  categories,
  saleEvents,
  leaveRequests,
}: {
  businessName: string | null;
  businessSlug: string;
  businessId: string;
  servedHistory: any[];
  pendingServices: any[];
  currentEmployeeId: number;
  currentEmployeeCommission: number;
  currentEmployeeSalary: number;
  todayAttendance: any;
  services: Service[];
  packages: PackageWithItems[];
  categories: string[];
  saleEvents: any[];
  leaveRequests: LeaveRequest[];
}) {
  const handleSignOut = () => {
    signOut({ callbackUrl: "/" });
  };

  return (
    <main className="min-h-screen bg-slate-50/50 pb-24 md:pb-12 font-sans">
      <header className="sticky top-0 z-40 bg-white/80 backdrop-blur-xl border-b border-slate-200/60 px-4 py-3 flex items-center justify-between md:px-8 md:py-4 shadow-[0_2px_20px_-12px_rgba(0,0,0,0.1)] transition-all duration-300">
        <div className="flex flex-col">
          <h1 className="font-bold text-lg leading-none md:text-xl text-slate-900 tracking-tight">
            {businessName}
          </h1>
          <p className="text-[10px] uppercase tracking-widest font-semibold text-indigo-500 mt-1">
            Employee Portal
          </p>
        </div>

        <div className="flex items-center gap-3">
          <BookingDialog
            services={services}
            packages={packages}
            categories={categories}
            businessSlug={businessSlug}
          />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="icon"
                className="h-9 w-9 rounded-full ring-2 ring-slate-100 hover:bg-slate-100 transition-all"
              >
                <Avatar className="h-9 w-9">
                  <AvatarFallback className="bg-slate-900 text-white text-xs font-medium">
                    <UserIcon className="h-4 w-4" />
                  </AvatarFallback>
                </Avatar>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent
              align="end"
              className="w-56 rounded-xl shadow-xl border-slate-100"
            >
              <DropdownMenuLabel>My Account</DropdownMenuLabel>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                onClick={handleSignOut}
                className="text-red-600 focus:text-red-700 focus:bg-red-50 cursor-pointer rounded-lg"
              >
                <LogOut className="mr-2 h-4 w-4" />
                Log out
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </header>

      {/*       <div className="md:hidden fixed bottom-6 right-6 z-50">
        <Button
          asChild
          size="icon"
          className="h-14 w-14 rounded-full shadow-xl shadow-indigo-600/30 bg-indigo-600 hover:bg-indigo-700 text-white transition-transform active:scale-90"
        >
          <Link href={`/${businessSlug}/booking`}>
            <Plus className="h-7 w-7" />
            <span className="sr-only">Add Booking</span>
          </Link>
        </Button>
      </div> */}

      <div className="px-4 py-6 md:px-8 max-w-7xl mx-auto space-y-8">
        <section className="grid grid-cols-1 md:grid-cols-12 gap-4 md:gap-6">
          <div className="md:col-span-5 lg:col-span-4 order-1">
            <AttendanceCard
              employeeId={currentEmployeeId}
              businessSlug={businessSlug}
              todayAttendance={todayAttendance}
              className="h-full shadow-lg shadow-slate-200/50 border-0"
            />
          </div>

          <div className="md:col-span-7 lg:col-span-8 order-2 grid grid-cols-2 lg:grid-cols-3 gap-3 md:gap-4">
            <div className="bg-white rounded-2xl p-5 border border-slate-100 shadow-sm flex flex-col justify-between group hover:border-indigo-100 transition-colors">
              <div className="flex items-start justify-between">
                <span className="text-xs text-slate-500 font-bold uppercase tracking-wider">
                  Est. Salary
                </span>
                <div className="h-8 w-8 rounded-full bg-green-50 flex items-center justify-center text-green-600 group-hover:scale-110 transition-transform">
                  <span className="text-xs font-bold"></span>
                </div>
              </div>
              <div className="mt-4">
                <span className="text-2xl md:text-3xl font-bold text-slate-900 tracking-tight block">
                  {currentEmployeeSalary.toLocaleString()}
                </span>
                <span className="text-xs text-slate-400 mt-1 block font-medium">
                  This Month
                </span>
              </div>
            </div>

            <div className="bg-white rounded-2xl p-5 border border-slate-100 shadow-sm flex flex-col justify-between group hover:border-blue-100 transition-colors">
              <div className="flex items-start justify-between">
                <span className="text-xs text-slate-500 font-bold uppercase tracking-wider">
                  Served
                </span>
                <div className="h-8 w-8 rounded-full bg-blue-50 flex items-center justify-center text-blue-600 group-hover:scale-110 transition-transform">
                  <UserIcon className="h-4 w-4" />
                </div>
              </div>
              <div className="mt-4">
                <span className="text-2xl md:text-3xl font-bold text-slate-900 tracking-tight block">
                  {
                    servedHistory.filter((s) => {
                      const today = new Date();
                      const date = new Date(s.updated_at);
                      return (
                        date.getDate() === today.getDate() &&
                        date.getMonth() === today.getMonth() &&
                        date.getFullYear() === today.getFullYear()
                      );
                    }).length
                  }
                </span>
                <span className="text-xs text-slate-400 mt-1 block font-medium">
                  Clients Today
                </span>
              </div>
            </div>

            <div className="col-span-2 lg:col-span-1 bg-white rounded-2xl p-5 border border-slate-100 shadow-sm flex flex-col justify-between group hover:border-orange-100 transition-colors">
              <div className="flex items-start justify-between">
                <span className="text-xs text-slate-500 font-bold uppercase tracking-wider">
                  Queue
                </span>
                <div className="h-8 w-8 rounded-full bg-orange-50 flex items-center justify-center text-orange-600 group-hover:scale-110 transition-transform">
                  <span className="text-xs font-bold font-mono">Q</span>
                </div>
              </div>
              <div className="mt-4">
                <span className="text-2xl md:text-3xl font-bold text-slate-900 tracking-tight block">
                  {pendingServices.length}
                </span>
                <span className="text-xs text-slate-400 mt-1 block font-medium">
                  Waiting
                </span>
              </div>
            </div>
          </div>
        </section>

        <section>
          <EmployeeLeaveClient
            employeeId={currentEmployeeId}
            businessId={businessId}
            businessSlug={businessSlug}
            requests={leaveRequests}
          />
        </section>

        <div className="grid grid-cols-1 xl:grid-cols-12 gap-8 md:gap-10">
          <section className="xl:col-span-7 space-y-5">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <h2 className="text-lg font-bold text-slate-900">
                  Current Queue
                </h2>
                <span className="hidden md:inline-flex h-6 items-center rounded-full bg-slate-100 px-2.5 text-xs font-bold text-slate-600">
                  {pendingServices.length}
                </span>
              </div>
            </div>

            <div className="-mx-4 md:mx-0">
              <PendingServicesList
                services={pendingServices}
                businessSlug={businessSlug}
                currentEmployeeId={currentEmployeeId}
                saleEvents={saleEvents}
              />
            </div>
          </section>

          <section className="xl:col-span-5 space-y-5">
            <div className="flex items-center gap-3">
              <h2 className="text-lg font-bold text-slate-900">
                Served History
              </h2>
            </div>
            <div className="-mx-4 md:mx-0">
              <EmployeeServedHistory
                services={servedHistory}
                currentEmployeeId={currentEmployeeId}
                currentEmployeeCommission={currentEmployeeCommission}
              />
            </div>
          </section>
        </div>
      </div>
    </main>
  );
}

"use client";

import { formatPH } from "@/lib/date-utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  markServiceServedAction,
  unclaimServiceAction,
  unserveServiceAction,
} from "@/lib/server actions/dashboard";
import { useState } from "react";
import {
  Loader2,
  History,
  CheckCircle2,
  User,
  Calendar,
  RotateCcw,
} from "lucide-react";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface ServedService {
  id: number;
  service: {
    name: string;
    duration: number | null;
  };
  booking: {
    customer: {
      name: string;
    };
    downpayment: number | null;
    downpayment_status: string | null;
    grand_total: number;
  };
  scheduled_at: Date | null;
  price: number;
  final_price?: number | null;
  discount?: number | null;
  discount_reason?: string | null;
  status: string;
  package?: {
    name: string;
  } | null;
}

export default function EmployeeServedHistory({
  services,
  currentEmployeeId,
  currentEmployeeCommission,
}: {
  services: ServedService[];
  currentEmployeeId: number;
  currentEmployeeCommission: number;
}) {
  const [loadingId, setLoadingId] = useState<number | null>(null);
  const [selectedService, setSelectedService] = useState<ServedService | null>(
    null,
  );
  const [pendingAction, setPendingAction] = useState<{
    type: "unclaim" | "unserve";
    serviceId: number;
  } | null>(null);

  const handleMarkAsServed = async (serviceId: number) => {
    setLoadingId(serviceId);
    try {
      const result = await markServiceServedAction(
        serviceId,
        currentEmployeeId,
      );
      if (!result.success) {
        alert(result.error);
      }
    } catch (error) {
      console.error(error);
      alert("Something went wrong");
    } finally {
      setLoadingId(null);
    }
  };

  const requestUnclaim = (serviceId: number) => {
    setPendingAction({ type: "unclaim", serviceId });
  };

  const executeUnclaim = async (serviceId: number) => {
    setLoadingId(serviceId);
    try {
      const result = await unclaimServiceAction(serviceId);
      if (!result.success) {
        alert(result.error);
      } else {
        setSelectedService(null);
      }
    } catch (error) {
      console.error(error);
      alert("Something went wrong");
    } finally {
      setLoadingId(null);
      setPendingAction(null);
    }
  };

  const requestUnserve = (serviceId: number) => {
    setPendingAction({ type: "unserve", serviceId });
  };

  const executeUnserve = async (serviceId: number) => {
    setLoadingId(serviceId);
    try {
      const result = await unserveServiceAction(serviceId);
      if (!result.success) {
        alert(result.error);
      } else {
        setSelectedService(null);
      }
    } catch (error) {
      console.error(error);
      alert("Something went wrong");
    } finally {
      setLoadingId(null);
      setPendingAction(null);
    }
  };

  const handleConfirmAction = () => {
    if (!pendingAction) return;
    if (pendingAction.type === "unclaim") {
      executeUnclaim(pendingAction.serviceId);
    } else if (pendingAction.type === "unserve") {
      executeUnserve(pendingAction.serviceId);
    }
  };

  return (
    <>
      <div className="h-full flex flex-col">
        <div className="space-y-3 p-4 lg:p-0">
          {services.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12 text-center space-y-3 opacity-60 bg-slate-50 rounded-2xl border-2 border-dashed border-slate-100">
              <div className="bg-white p-4 rounded-full shadow-sm">
                <History className="w-6 h-6 text-slate-400" />
              </div>
              <div>
                <h3 className="font-semibold text-base text-slate-800">
                  No served history
                </h3>
                <p className="text-xs text-slate-500">
                  Completed services will appear here.
                </p>
              </div>
            </div>
          ) : (
            services.map((item) => {
              const customerInitials = item.booking.customer.name
                .split(" ")
                .map((n) => n[0])
                .join("")
                .toUpperCase()
                .slice(0, 2);

              const displayPrice = item.final_price ?? item.price;
              const hasDiscount = item.discount && item.discount > 0;

              return (
                <div
                  key={item.id}
                  className="flex items-center justify-between p-4 bg-white border border-slate-100 rounded-xl shadow-sm hover:shadow-md hover:border-slate-200 transition-all cursor-pointer group active:scale-[0.98]"
                  onClick={() => setSelectedService(item)}
                >
                  <div className="flex items-center gap-3">
                    <div className="h-10 w-10 rounded-full bg-slate-100 border border-slate-200 flex items-center justify-center text-xs font-bold text-slate-600">
                      {customerInitials}
                    </div>

                    <div className="space-y-0.5">
                      <div className="font-semibold text-sm text-slate-900 flex items-center gap-2">
                        {item.service.name}
                        {item.package && (
                          <span className="bg-slate-100 text-slate-600 border border-slate-200 text-[9px] px-1.5 py-0.5 rounded-full font-medium">
                            {item.package.name}
                          </span>
                        )}
                      </div>
                      <div className="text-xs text-slate-500 font-medium">
                        {item.booking.customer.name}
                      </div>
                    </div>
                  </div>

                  <div className="flex flex-col items-end gap-1.5">
                    {hasDiscount ? (
                      <div className="flex flex-col items-end leading-none gap-0.5">
                        <span className="text-xs text-red-500 line-through opacity-70">
                          {item.price.toLocaleString()}
                        </span>
                        <span className="font-bold text-sm text-emerald-600">
                          {displayPrice.toLocaleString()}
                        </span>
                      </div>
                    ) : (
                      <span className="font-bold text-sm text-slate-700">
                        {item.price.toLocaleString()}
                      </span>
                    )}
                    {item.status === "CLAIMED" ? (
                      <Badge
                        variant="outline"
                        className="bg-blue-50 text-blue-700 border-blue-200 text-[10px] py-0 h-5"
                      >
                        Serving
                      </Badge>
                    ) : (
                      <Badge
                        variant="secondary"
                        className="bg-green-50 text-green-700 border-green-200 text-[10px] py-0 h-5"
                      >
                        {item.status}
                      </Badge>
                    )}
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>

      <Dialog
        open={!!selectedService}
        onOpenChange={(open) => !open && setSelectedService(null)}
      >
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Service Details</DialogTitle>
            <DialogDescription>
              Full information about this service
            </DialogDescription>
          </DialogHeader>

          {selectedService && (
            <div className="space-y-6">
              <div className="flex items-center gap-4 pb-4 border-b">
                <Avatar className="h-12 w-12 border">
                  <AvatarFallback className="bg-primary/10 text-primary text-lg font-medium">
                    {selectedService.booking.customer.name
                      .slice(0, 2)
                      .toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h3 className="font-semibold text-lg">
                    {selectedService.service.name}
                  </h3>
                  <p className="text-muted-foreground">
                    {selectedService.booking.customer.name}
                  </p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="p-3 bg-muted/40 rounded-lg space-y-1">
                  <p className="text-xs text-muted-foreground font-medium uppercase">
                    Price
                  </p>
                  <div className="text-lg font-bold">
                    {selectedService.final_price &&
                    selectedService.final_price !== selectedService.price ? (
                      <div className="flex flex-col">
                        <span className="text-xs text-red-500 line-through opacity-70">
                          {selectedService.price.toLocaleString()}
                        </span>
                        <span className="text-emerald-600">
                          {selectedService.final_price.toLocaleString()}
                        </span>
                      </div>
                    ) : (
                      `${selectedService.price.toLocaleString()}`
                    )}
                  </div>
                </div>
                <div className="p-3 bg-green-50/50 rounded-lg space-y-1 border border-green-100">
                  <p className="text-xs text-green-700 font-medium uppercase">
                    Commission ({currentEmployeeCommission}%)
                  </p>
                  <p className="text-lg font-bold text-green-700">
                    
                    {(
                      ((selectedService.final_price ?? selectedService.price) *
                        currentEmployeeCommission) /
                      100
                    ).toLocaleString()}
                  </p>
                </div>
              </div>

              <div className="space-y-3 text-sm">
                <div className="flex justify-between py-2 border-b border-dashed">
                  <span className="text-muted-foreground">Scheduled For</span>
                  <span className="font-medium">
                    {selectedService.scheduled_at
                      ? formatPH(selectedService.scheduled_at, "PPP p")
                      : "Unscheduled"}
                  </span>
                </div>
                <div className="flex justify-between py-2 border-b border-dashed">
                  <span className="text-muted-foreground">Duration</span>
                  <span className="font-medium">
                    {selectedService.service.duration || "-"} min
                  </span>
                </div>
                <div className="flex justify-between py-2 border-b border-dashed">
                  <span className="text-muted-foreground">Status</span>
                  <Badge variant="outline">{selectedService.status}</Badge>
                </div>
              </div>

              <div className="border-t pt-4 space-y-2">
                <h4 className="text-sm font-semibold text-slate-900">
                  Payment Status
                </h4>
                <div className="space-y-1 text-sm">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Total Amount:</span>
                    <span className="font-medium">
                      {selectedService.booking.grand_total.toLocaleString()}
                    </span>
                  </div>
                  {selectedService.booking.downpayment &&
                  selectedService.booking.downpayment > 0 ? (
                    <>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">
                          Downpayment:
                        </span>
                        <span className="font-medium text-green-600">
                          
                          {selectedService.booking.downpayment.toLocaleString()}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Balance:</span>
                        <span className="font-bold text-orange-600">
                          
                          {(
                            selectedService.booking.grand_total -
                            selectedService.booking.downpayment
                          ).toLocaleString()}
                        </span>
                      </div>
                      <div className="mt-2 p-2 bg-orange-50 border border-orange-200 rounded-md">
                        <p className="text-xs text-orange-800 font-medium">
                           Half-paid: Balance of 
                          {(
                            selectedService.booking.grand_total -
                            selectedService.booking.downpayment
                          ).toLocaleString()}{" "}
                          still due
                        </p>
                      </div>
                    </>
                  ) : (
                    <div className="mt-2 p-2 bg-green-50 border border-green-200 rounded-md">
                      <p className="text-xs text-green-800 font-medium">
                         Fully paid
                      </p>
                    </div>
                  )}
                </div>
              </div>

              {selectedService.status === "CLAIMED" && (
                <div className="flex gap-2 pt-2">
                  <Button
                    variant="outline"
                    className="flex-1 text-red-600 hover:text-red-700 hover:bg-red-50 h-11"
                    onClick={() => requestUnclaim(selectedService.id)}
                    disabled={loadingId === selectedService.id}
                  >
                    {loadingId === selectedService.id ? (
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    ) : (
                      <RotateCcw className="mr-2 h-4 w-4" />
                    )}
                    Unclaim
                  </Button>
                  <Button
                    className="flex-2 bg-green-600 hover:bg-green-700 text-white h-11"
                    onClick={() => {
                      handleMarkAsServed(selectedService.id);
                      setSelectedService(null);
                    }}
                    disabled={loadingId === selectedService.id}
                  >
                    {loadingId === selectedService.id && (
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    )}
                    Mark as Served
                  </Button>
                </div>
              )}
              {selectedService.status === "COMPLETED" && (
                <Button
                  variant="outline"
                  className="w-full text-orange-600 hover:text-orange-700 hover:bg-orange-50 h-11"
                  onClick={() => requestUnserve(selectedService.id)}
                  disabled={loadingId === selectedService.id}
                >
                  {loadingId === selectedService.id ? (
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  ) : (
                    <RotateCcw className="mr-2 h-4 w-4" />
                  )}
                  Undo (Unserve)
                </Button>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>

      <AlertDialog
        open={!!pendingAction}
        onOpenChange={(open) => !open && setPendingAction(null)}
      >
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>
              {pendingAction?.type === "unclaim"
                ? "Unclaim this service?"
                : "Undo this service?"}
            </AlertDialogTitle>
            <AlertDialogDescription>
              {pendingAction?.type === "unclaim"
                ? "This service will go back to the pending list and can be claimed by anyone."
                : "This will revert the service status and remove the commission."}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleConfirmAction}
              className={
                pendingAction?.type === "unclaim"
                  ? "bg-red-600 hover:bg-red-700"
                  : "bg-orange-600 hover:bg-orange-700"
              }
            >
              {pendingAction?.type === "unclaim" ? "Unclaim" : "Undo"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

"use client";

import { formatPH } from "@/lib/date-utils";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Inbox, Clock as ClockIcon } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import {
  claimServiceAction,
  getPendingServicesAction,
} from "@/lib/server actions/dashboard";

import { getApplicableDiscount } from "@/lib/utils/pricing";

interface PendingService {
  id: number;
  service: {
    id: number; // Ensure ID is present
    name: string;
    duration: number | null;
  };
  booking: {
    customer: {
      name: string;
    };
    downpayment: number | null;
    downpayment_status: string | null;
    grand_total: number;
  };
  scheduled_at: Date | null;
  price: number;
  package_id: number | null;
  package: {
    name: string;
  } | null;
}

export default function PendingServicesList({
  services: initialServices,
  businessSlug,
  currentEmployeeId,
  saleEvents = [],
}: {
  services: PendingService[];
  businessSlug: string;
  currentEmployeeId: number;
  saleEvents?: any[];
}) {
  const [selectedService, setSelectedService] = useState<PendingService | null>(
    null,
  );
  const [isClaiming, setIsClaiming] = useState(false);

  const { data: services } = useQuery({
    queryKey: ["pending-services", businessSlug],
    queryFn: () => getPendingServicesAction(),
    initialData: initialServices,
    refetchInterval: 5000,
  });

  const handleClaim = async () => {
    if (!selectedService) return;
    setIsClaiming(true);
    const result = await claimServiceAction(
      selectedService.id,
      currentEmployeeId,
    );
    setIsClaiming(false);

    if (result.success) {
      setSelectedService(null);
    } else {
      alert(result.error);
    }
  };

  return (
    <>
      <div className="h-full flex flex-col">
        <div className="space-y-4 p-4 lg:p-0">
          {services.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-16 text-center space-y-4 opacity-70 border-2 border-dashed border-slate-200 rounded-3xl">
              <div className="bg-slate-50 p-6 rounded-full">
                <Inbox className="w-10 h-10 text-slate-300" />
              </div>
              <div className="max-w-xs mx-auto">
                <h3 className="font-bold text-lg text-slate-900">
                  All caught up!
                </h3>
                <p className="text-sm text-slate-500 mt-1">
                  There are no pending services in the queue right now.
                </p>
              </div>
            </div>
          ) : (
            services.map((item) => {
              const customerInitials = item.booking.customer.name
                .split(" ")
                .map((n) => n[0])
                .join("")
                .toUpperCase()
                .slice(0, 2);

              const discountInfo = getApplicableDiscount(
                item.service.id, // Assuming service ID is available on item.service
                item.package_id ? Number(item.package_id) : undefined,
                item.price,
                saleEvents,
              );

              return (
                <div
                  key={item.id}
                  className="bg-white rounded-xl p-3 shadow-sm border border-slate-100 hover:border-indigo-200 hover:shadow-md transition-all cursor-pointer group active:scale-[0.99] relative overflow-hidden"
                  onClick={() => setSelectedService(item)}
                >
                  <div className="absolute left-0 top-0 bottom-0 w-1 bg-indigo-500"></div>

                  <div className="flex items-start justify-between">
                    <div className="flex items-start gap-3">
                      <div className="h-10 w-10 rounded-xl bg-slate-100 text-slate-600 flex items-center justify-center text-xs font-bold border border-slate-200 shadow-inner shrink-0">
                        {customerInitials}
                      </div>

                      <div className="space-y-0.5">
                        <div className="flex items-center gap-2 flex-wrap">
                          <h4 className="font-bold text-slate-900 text-sm leading-tight group-hover:text-indigo-600 transition-colors">
                            {item.service.name}
                          </h4>
                          {item.package && (
                            <Badge
                              variant="secondary"
                              className="bg-indigo-50 text-indigo-700 border border-indigo-100 text-[9px] font-semibold h-4 px-1"
                            >
                              {item.package.name}
                            </Badge>
                          )}
                        </div>

                        <p className="text-xs font-medium text-slate-500">
                          {item.booking.customer.name}
                        </p>

                        <div className="flex items-center gap-3 pt-1">
                          <span className="flex items-center gap-1 text-[10px] font-semibold text-slate-400 bg-slate-50 px-2 py-0.5 rounded-md">
                            <ClockIcon className="w-3 h-3" />
                            {item.scheduled_at
                              ? formatPH(item.scheduled_at, "h:mm a")
                              : "Walk-in"}
                          </span>
                          {item.service.duration && (
                            <span className="text-[10px] text-slate-400 font-medium">
                              {item.service.duration} mins
                            </span>
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="flex flex-col items-end pl-2">
                      <span className="text-sm font-bold text-slate-900 bg-slate-50 px-2 py-0.5 rounded-md border border-slate-100">
                        {discountInfo &&
                        discountInfo.finalPrice !== item.price ? (
                          <div className="flex flex-col items-end leading-none gap-0.5">
                            <span className="text-xs text-red-500 line-through opacity-70">
                              {item.price.toLocaleString()}
                            </span>
                            <span className="text-emerald-600">
                              {discountInfo.finalPrice.toLocaleString()}
                            </span>
                          </div>
                        ) : (
                          `${item.price.toLocaleString()}`
                        )}
                      </span>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>

      <Dialog
        open={!!selectedService}
        onOpenChange={(open) => !open && setSelectedService(null)}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Claim Service</DialogTitle>
            <DialogDescription>
              Are you sure you want to claim this service?
              <br />
              <strong>{selectedService?.service.name}</strong> for{" "}
              {selectedService?.booking.customer.name}
            </DialogDescription>
          </DialogHeader>
          <div className="py-4 space-y-4">
            <p className="text-sm text-muted-foreground">
              By claiming this service, you will be assigned as the staff member
              responsible for it. You can mark it as served later.
            </p>

            {selectedService && (
              <div className="border-t pt-4 space-y-2">
                <h4 className="text-sm font-semibold text-slate-900">
                  Payment Status
                </h4>
                <div className="space-y-1 text-sm">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Total Amount:</span>
                    <span className="font-medium">
                      {selectedService.booking.grand_total.toLocaleString()}
                    </span>
                  </div>
                  {selectedService.booking.downpayment &&
                  selectedService.booking.downpayment > 0 ? (
                    <>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">
                          Downpayment:
                        </span>
                        <span className="font-medium text-green-600">
                          
                          {selectedService.booking.downpayment.toLocaleString()}
                        </span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-muted-foreground">Balance:</span>
                        <span className="font-bold text-orange-600">
                          
                          {(
                            selectedService.booking.grand_total -
                            selectedService.booking.downpayment
                          ).toLocaleString()}
                        </span>
                      </div>
                      <div className="mt-2 p-2 bg-orange-50 border border-orange-200 rounded-md">
                        <p className="text-xs text-orange-800 font-medium">
                           Half-paid: Balance of 
                          {(
                            selectedService.booking.grand_total -
                            selectedService.booking.downpayment
                          ).toLocaleString()}{" "}
                          still due
                        </p>
                      </div>
                    </>
                  ) : (
                    <div className="mt-2 p-2 bg-green-50 border border-green-200 rounded-md">
                      <p className="text-xs text-green-800 font-medium">
                         Fully paid
                      </p>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setSelectedService(null)}
              disabled={isClaiming}
            >
              Cancel
            </Button>
            <Button onClick={handleClaim} disabled={isClaiming}>
              {isClaiming ? "Claiming..." : "Claim Service"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

import {
