"use client";

import { useMemo, useState } from "react";
import { useRouter } from "next/navigation";
import {
  Booking,
  Customer,
  BookingStatus,
  AvailedService,
  Service,
  Employee,
  User,
  Voucher,
  Owner,
} from "@/prisma/generated/prisma/client";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { formatPH } from "@/lib/date-utils";
import {
  Search,
  Check,
  X,
  Trash2,
  RefreshCcw,
  MoreHorizontal,
  Calendar,
  ArrowRight,
} from "lucide-react";
import {
  deleteBookingAction,
  updateBookingStatusAction,
} from "@/lib/server actions/dashboard";
import { toast } from "sonner";
import { Button } from "@/components/ui/button";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { BookingDetailsDialog } from "./BookingDetailsDialog";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

type BookingWithDetails = Booking & {
  customer: Customer;
  vouchers: Voucher[];
  availed_services: (AvailedService & {
    service: Service;
    served_by: (Employee & { user: User }) | null;
    served_by_owner: (Owner & { user: User }) | null;
  })[];
};

export function BookingList({
  bookings,
  variant = "card",
  className,
}: {
  bookings: BookingWithDetails[];
  variant?: "card" | "embedded";
  className?: string;
}) {
  const router = useRouter();
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("ALL");
  const [selectedBooking, setSelectedBooking] =
    useState<BookingWithDetails | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const filteredBookings = useMemo(() => {
    return bookings.filter((booking) => {
      // Search Logic
      const searchLower = search.toLowerCase();
      const matchesSearch =
        booking.customer.name.toLowerCase().includes(searchLower) ||
        booking.id.toString().includes(searchLower) ||
        (booking.customer.phone &&
          booking.customer.phone.includes(searchLower));

      // Status Logic
      const matchesStatus =
        statusFilter === "ALL" || booking.status === statusFilter;

      return matchesSearch && matchesStatus;
    });
  }, [bookings, search, statusFilter]);

  const getStatusColor = (status: BookingStatus) => {
    switch (status) {
      case "ACCEPTED":
        return "bg-blue-50 text-blue-700 border-blue-200 hover:bg-blue-100";
      case "COMPLETED":
        return "bg-emerald-50 text-emerald-700 border-emerald-200 hover:bg-emerald-100";
      case "CANCELLED":
        return "bg-red-50 text-red-700 border-red-200 hover:bg-red-100";
      default:
        return "bg-zinc-100 text-zinc-700 border-zinc-200 hover:bg-zinc-100";
    }
  };

  const handleStatusUpdate = async (id: number, status: BookingStatus) => {
    const res = await updateBookingStatusAction(id, status);
    if (res.success) {
      toast.success(`Booking ${status.toLowerCase()} successfully`);
      router.refresh();
    } else {
      toast.error(res.error);
    }
  };

  const handleDelete = async (id: number) => {
    const res = await deleteBookingAction(id);
    if (res.success) {
      toast.success("Booking deleted successfully");
      router.refresh();
    } else {
      toast.error(res.error);
    }
  };

  const isEmbedded = variant === "embedded";
  const tablePaddingLeft = isEmbedded ? "pl-4" : "pl-8";
  const tablePaddingRight = isEmbedded ? "pr-4" : "pr-8";

  return (
    <>
      <Card
        className={cn(
          "h-full flex flex-col overflow-hidden",
          isEmbedded
            ? "border-0 shadow-none bg-transparent rounded-none"
            : "shadow-xl shadow-zinc-200/50 border-none bg-white rounded-[32px]",
          className,
        )}
      >
        <CardHeader
          className={cn(
            "pb-6 space-y-6 border-b",
            isEmbedded
              ? "px-0 pt-0 bg-transparent border-zinc-100/60"
              : "md:px-8 md:pt-8 bg-zinc-50/50 border-zinc-100",
          )}
        >
          <div className="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
            <div className="space-y-1">
              <CardTitle className="text-2xl font-bold tracking-tight text-zinc-900">
                Bookings
              </CardTitle>
              <CardDescription className="text-base font-medium text-zinc-500">
                Manage and track all customer appointments
              </CardDescription>
            </div>
            <Button
              variant="outline"
              size="sm"
              className="h-11 gap-2 rounded-xl border-zinc-200 bg-white hover:bg-zinc-50 hover:text-zinc-900 transition-colors"
              onClick={() => router.refresh()}
            >
              <RefreshCcw className="h-4 w-4" />
              <span className="sr-only sm:not-sr-only sm:whitespace-nowrap font-medium">
                Refresh
              </span>
            </Button>
          </div>
          <div className="flex flex-col sm:flex-row gap-3">
            <div className="relative flex-1">
              <Search className="absolute left-3.5 top-3 h-4 w-4 text-zinc-400" />
              <Input
                placeholder="Search by customer name or ID..."
                className="pl-10 h-11 rounded-xl border-zinc-200 bg-white focus-visible:ring-emerald-500 placeholder:text-zinc-400"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-full sm:w-[150px] h-11 rounded-xl border-zinc-200 bg-white focus:ring-emerald-500">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent className="rounded-xl">
                <SelectItem value="ALL">All Status</SelectItem>
                <SelectItem value="ACCEPTED">Accepted</SelectItem>
                <SelectItem value="COMPLETED">Completed</SelectItem>
                <SelectItem value="CANCELLED">Cancelled</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>

        <CardContent
          className={cn(
            "flex-1 overflow-auto p-0 min-h-[400px]",
            isEmbedded ? "bg-transparent" : "bg-white",
          )}
        >
          {/* Mobile Card View */}
          <div
            className={cn(
              "md:hidden flex flex-col space-y-4",
              isEmbedded ? "p-2" : "p-4",
            )}
          >
            {filteredBookings.length > 0 ? (
              filteredBookings.map((booking) => (
                <div
                  key={booking.id}
                  className="p-5 bg-white border border-zinc-100 rounded-2xl shadow-sm hover:shadow-md transition-shadow active:scale-[0.98] active:transition-transform"
                  onClick={() => {
                    setSelectedBooking(booking);
                    setIsDialogOpen(true);
                  }}
                >
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex items-center gap-3">
                      <div className="h-10 w-10 rounded-full bg-emerald-50 flex items-center justify-center text-emerald-600 font-bold text-sm">
                        {booking.customer.name.charAt(0)}
                      </div>
                      <div className="flex flex-col">
                        <span className="font-semibold text-zinc-900 line-clamp-1">
                          {booking.customer.name}
                        </span>
                        <span className="text-xs text-zinc-500 font-medium">
                          #{booking.id}
                        </span>
                      </div>
                    </div>
                    <Badge
                      variant="secondary"
                      className={cn(
                        "text-[10px] uppercase font-bold tracking-wider px-2.5 py-1 rounded-lg border",
                        getStatusColor(booking.status),
                      )}
                    >
                      {booking.status}
                    </Badge>
                  </div>

                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div className="flex flex-col gap-1">
                      <span className="text-[10px] text-zinc-400 uppercase tracking-wider font-semibold">
                        Date
                      </span>
                      <div className="flex items-center gap-1.5 text-sm font-medium text-zinc-700">
                        <Calendar className="w-3.5 h-3.5 text-zinc-400" />
                        {formatPH(booking.created_at, "MMM d, h:mm a")}
                      </div>
                    </div>
                    <div className="flex flex-col gap-1">
                      <span className="text-[10px] text-zinc-400 uppercase tracking-wider font-semibold">
                        Total
                      </span>
                      <div className="flex flex-col items-start gap-0.5">
                        {booking.total_discount > 0 && (
                          <span className="text-xs text-zinc-400 line-through">
                            
                            {(
                              booking.grand_total + booking.total_discount
                            ).toLocaleString()}
                          </span>
                        )}
                        <div className="flex items-center gap-1.5">
                          <span className="text-sm font-bold text-zinc-900">
                            {booking.grand_total.toLocaleString()}
                          </span>
                          {booking.total_discount > 0 && (
                            <Badge
                              variant="outline"
                              className="text-[10px] px-1.5 py-0.5 bg-emerald-50 text-emerald-700 border-emerald-200"
                            >
                              -{booking.total_discount.toLocaleString()}
                              {booking.vouchers.length > 0 &&
                                ` (${booking.vouchers[0].code})`}
                            </Badge>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center justify-between pt-4 border-t border-zinc-50">
                    <span className="text-xs text-zinc-400">
                      Tap to view details
                    </span>
                    <Button
                      size="sm"
                      variant="ghost"
                      className="h-8 gap-1 text-emerald-600 hover:text-emerald-700 hover:bg-emerald-50 -mr-2"
                    >
                      View Details
                      <ArrowRight className="w-3.5 h-3.5" />
                    </Button>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex flex-col items-center justify-center h-64 text-center p-8 space-y-3">
                <div className="h-12 w-12 rounded-full bg-zinc-50 flex items-center justify-center">
                  <Calendar className="h-6 w-6 text-zinc-300" />
                </div>
                <div className="space-y-1">
                  <p className="text-base font-semibold text-zinc-900">
                    No bookings found
                  </p>
                  <p className="text-sm text-zinc-500">
                    Try adjusting your search or filters.
                  </p>
                </div>
              </div>
            )}
          </div>

          {/* Desktop Table View */}
          <div className="hidden md:block">
            <Table>
              <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
                <TableRow className="hover:bg-transparent border-zinc-100">
                  <TableHead
                    className={cn(
                      "w-[100px] font-semibold text-zinc-500 h-12",
                      tablePaddingLeft,
                    )}
                  >
                    ID
                  </TableHead>
                  <TableHead className="font-semibold text-zinc-500 h-12">
                    Customer
                  </TableHead>
                  <TableHead className="font-semibold text-zinc-500 h-12">
                    Date & Time
                  </TableHead>
                  <TableHead className="font-semibold text-zinc-500 h-12">
                    Status
                  </TableHead>
                  <TableHead className="text-right font-semibold text-zinc-500 h-12">
                    Total
                  </TableHead>
                  <TableHead
                    className={cn(
                      "text-right font-semibold text-zinc-500 h-12",
                      tablePaddingRight,
                    )}
                  >
                    Actions
                  </TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredBookings.length > 0 ? (
                  filteredBookings.map((booking) => (
                    <TableRow
                      key={booking.id}
                      className="hover:bg-emerald-50/30 cursor-pointer transition-colors border-zinc-100 group"
                      onClick={() => {
                        setSelectedBooking(booking);
                        setIsDialogOpen(true);
                      }}
                    >
                      <TableCell
                        className={cn(
                          "font-medium text-zinc-500 py-4",
                          tablePaddingLeft,
                        )}
                      >
                        <span className="bg-zinc-100/80 text-zinc-600 px-2 py-1 rounded-md text-xs font-semibold">
                          #{booking.id}
                        </span>
                      </TableCell>
                      <TableCell className="py-4">
                        <div className="flex items-center gap-3">
                          <div className="h-9 w-9 rounded-full bg-emerald-100 text-emerald-700 flex items-center justify-center text-xs font-bold ring-2 ring-white shadow-sm">
                            {booking.customer.name.charAt(0)}
                          </div>
                          <div className="flex flex-col">
                            <span className="font-semibold text-zinc-900 group-hover:text-emerald-700 transition-colors">
                              {booking.customer.name}
                            </span>
                            {booking.customer.phone && (
                              <span className="text-xs text-zinc-500">
                                {booking.customer.phone}
                              </span>
                            )}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell className="text-zinc-600 font-medium py-4">
                        <div className="flex items-center gap-2">
                          <Calendar className="w-4 h-4 text-zinc-400" />
                          {formatPH(booking.created_at, "MMM d, yyyy  h:mm a")}
                        </div>
                      </TableCell>
                      <TableCell className="py-4">
                        <div className="flex items-center gap-2">
                          <Badge
                            variant="secondary"
                            className={cn(
                              "text-[10px] uppercase font-bold tracking-wider px-2.5 py-1 rounded-lg border shadow-sm",
                              getStatusColor(booking.status),
                            )}
                          >
                            {booking.status}
                          </Badge>
                          {booking.status === "ACCEPTED" &&
                            (booking.downpayment || 0) > 0 &&
                            booking.grand_total >
                              (booking.downpayment || 0) && (
                              <Badge
                                variant="outline"
                                className="border-orange-200 text-orange-700 bg-orange-50 text-[10px] px-2 py-0.5 font-medium rounded-md"
                              >
                                Bal: 
                                {(
                                  booking.grand_total -
                                  (booking.downpayment || 0)
                                ).toLocaleString()}
                              </Badge>
                            )}
                        </div>
                      </TableCell>
                      <TableCell className="text-right py-4">
                        <div className="flex items-center justify-end gap-2">
                          {booking.total_discount > 0 && (
                            <Badge
                              variant="outline"
                              className="text-[10px] px-1.5 py-0.5 bg-emerald-50 text-emerald-700 border-emerald-200 hidden xl:inline-flex"
                            >
                              -{booking.total_discount.toLocaleString()}
                              {booking.vouchers.length > 0 &&
                                ` (${booking.vouchers[0].code})`}
                            </Badge>
                          )}
                          <div className="flex flex-col items-end gap-0.5">
                            {booking.total_discount > 0 && (
                              <span className="text-xs text-zinc-400 line-through">
                                
                                {(
                                  booking.grand_total + booking.total_discount
                                ).toLocaleString()}
                              </span>
                            )}
                            <span className="font-bold text-zinc-900 text-base">
                              {booking.grand_total.toLocaleString()}
                            </span>
                          </div>
                        </div>
                      </TableCell>
                      <TableCell
                        className={cn("text-right py-4", tablePaddingRight)}
                      >
                        <div
                          className="flex justify-end gap-1"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <DropdownMenu>
                            <DropdownMenuTrigger asChild>
                              <Button
                                variant="ghost"
                                size="icon"
                                className="h-8 w-8 text-zinc-400 hover:text-zinc-700 hover:bg-zinc-100 rounded-lg"
                              >
                                <MoreHorizontal className="h-4 w-4" />
                              </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent
                              align="end"
                              className="w-[180px] rounded-xl border-zinc-100 shadow-xl p-1"
                            >
                              <DropdownMenuLabel className="px-2 py-1.5 text-xs font-semibold text-zinc-500 uppercase tracking-wider">
                                Actions
                              </DropdownMenuLabel>
                              <DropdownMenuSeparator className="-mx-1 my-1 bg-zinc-100" />
                              <DropdownMenuItem
                                className="rounded-lg cursor-pointer focus:bg-emerald-50 focus:text-emerald-700"
                                onClick={() => {
                                  setSelectedBooking(booking);
                                  setIsDialogOpen(true);
                                }}
                              >
                                View Details
                              </DropdownMenuItem>
                              {booking.status === "ACCEPTED" && (
                                <DropdownMenuItem
                                  className="text-red-600 focus:text-red-700 focus:bg-red-50 rounded-lg cursor-pointer"
                                  onClick={() =>
                                    handleStatusUpdate(booking.id, "CANCELLED")
                                  }
                                >
                                  Cancel Booking
                                </DropdownMenuItem>
                              )}
                              <DropdownMenuSeparator className="-mx-1 my-1 bg-zinc-100" />
                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <div className="relative flex select-none items-center rounded-lg px-2 py-1.5 text-sm outline-none transition-colors data-[disabled]:pointer-events-none data-[disabled]:opacity-50 hover:bg-red-50 text-red-600 focus:bg-red-50 cursor-pointer w-full group">
                                    <Trash2 className="w-3.5 h-3.5 mr-2 group-hover:text-red-700" />
                                    Delete
                                  </div>
                                </AlertDialogTrigger>
                                <AlertDialogContent
                                  onClick={(e) => e.stopPropagation()}
                                  className="rounded-3xl border-0 shadow-2xl"
                                >
                                  <AlertDialogHeader>
                                    <AlertDialogTitle className="text-xl">
                                      Delete Booking?
                                    </AlertDialogTitle>
                                    <AlertDialogDescription>
                                      This action cannot be undone. This will
                                      permanently delete the booking for{" "}
                                      <span className="font-semibold text-zinc-900">
                                        {booking.customer.name}
                                      </span>
                                      .
                                    </AlertDialogDescription>
                                  </AlertDialogHeader>
                                  <AlertDialogFooter>
                                    <AlertDialogCancel className="rounded-xl border-0 bg-zinc-100 hover:bg-zinc-200 text-zinc-700">
                                      Cancel
                                    </AlertDialogCancel>
                                    <AlertDialogAction
                                      className="bg-red-600 hover:bg-red-700 rounded-xl text-white shadow-md shadow-red-200"
                                      onClick={() => handleDelete(booking.id)}
                                    >
                                      Delete Booking
                                    </AlertDialogAction>
                                  </AlertDialogFooter>
                                </AlertDialogContent>
                              </AlertDialog>
                            </DropdownMenuContent>
                          </DropdownMenu>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={6} className="h-96">
                      <div className="flex flex-col items-center justify-center text-center space-y-3">
                        <div className="h-12 w-12 rounded-full bg-zinc-50 flex items-center justify-center">
                          <Calendar className="h-6 w-6 text-zinc-300" />
                        </div>
                        <div className="space-y-1">
                          <p className="text-base font-semibold text-zinc-900">
                            No bookings found
                          </p>
                          <p className="text-sm text-zinc-500">
                            Try adjusting your search or filters.
                          </p>
                        </div>
                      </div>
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <BookingDetailsDialog
        booking={selectedBooking}
        open={isDialogOpen}
        onOpenChange={setIsDialogOpen}
      />
    </>
  );
}

import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { PhilippinePeso, Plus, Calendar, Users, Bell } from "lucide-react";
import Link from "next/link";
import DashboardCard from "../DashboardCard";
import { SalesChart } from "./SalesChart";

import { formatPH } from "@/lib/date-utils";
import {
  Booking,
  Customer,
  AvailedService,
  Service,
  Employee,
  User,
  Voucher,
  Owner,
  ServicePackage,
} from "@/prisma/generated/prisma/client";
import { BookingList } from "./BookingList";
import { BookingDialog } from "./BookingDialog";
import {
  getCachedServices,
  getCachedPackages,
  getCachedBusinessBySlug,
} from "@/lib/data/cached";
import OwnerServiceQueue, {
  OwnerClaimedService,
  OwnerPendingService,
} from "./OwnerServiceQueue";

type BookingWithDetails = Booking & {
  customer: Customer;
  vouchers: Voucher[];
  availed_services: (AvailedService & {
    service: Service;
    served_by: (Employee & { user: User }) | null;
    served_by_owner: (Owner & { user: User }) | null;
  })[];
};

export default async function OwnerDashboard({
  businessName,
  businessSlug,
  totalSales,
  bookingsToday,
  presentEmployeesToday,
  bookings,
  allBookings,
  pendingServices,
  ownerClaimedServices,
  payroll,
  flowRemindersThisWeek,
}: {
  businessName: string;
  businessSlug: string;
  totalSales: number;
  bookingsToday: number;
  presentEmployeesToday: number;
  bookings: { id: number; created_at: Date; grand_total: number }[];
  allBookings: BookingWithDetails[];
  pendingServices: OwnerPendingService[];
  ownerClaimedServices: OwnerClaimedService[];
  payroll: {
    id: number;
    total_salary: number;
    starting_date: Date;
    ending_date: Date;
    employee: {
      user: {
        name: string;
      };
    };
  }[];
  flowRemindersThisWeek: number;
}) {
  const business = await getCachedBusinessBySlug(businessSlug);
  const services = business ? await getCachedServices(business.id) : [];
  const packages = business ? await getCachedPackages(business.id) : [];
  const categories = Array.from(new Set(services.map((s) => s.category)));

  const todayLabel = formatPH(new Date(), "MMM d, yyyy");
  return (
    <div className="min-h-screen bg-gradient-to-b from-zinc-50 via-white to-zinc-50">
      <header className="sticky top-0 z-40 bg-white/80 backdrop-blur-xl border-b border-slate-200/60 px-4 py-3 flex items-center justify-between md:px-8 md:py-4 shadow-[0_2px_20px_-12px_rgba(0,0,0,0.1)] transition-all duration-300">
        <div className="flex flex-col">
          <h1 className="font-bold text-lg leading-none md:text-xl text-slate-900 tracking-tight">
            {businessName}
          </h1>
          <p className="text-[10px] uppercase tracking-widest font-semibold text-indigo-500 mt-1">
            Owner Dashboard
          </p>
        </div>

        <div className="flex items-center gap-3">
          <BookingDialog
            services={services}
            packages={packages}
            categories={categories}
            businessSlug={businessSlug}
          />
        </div>
      </header>

      <main className="max-w-[1720px] mx-auto px-4 sm:px-6 lg:px-8 py-6 space-y-6">
        <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          <DashboardCard
            variant="filled"
            Icon={PhilippinePeso}
            description="Total sales this period"
            title="Revenue"
            count={totalSales}
          />

          <DashboardCard
            Icon={Calendar}
            description="Appointments for today"
            title="Bookings"
            count={bookingsToday}
          />

          <DashboardCard
            Icon={Users}
            description="Staff currently present"
            title="Active Staff"
            count={presentEmployeesToday}
          />

          <DashboardCard
            Icon={Bell}
            description="Follow-up reminders sent"
            title="Flow Reminders"
            count={flowRemindersThisWeek}
          />
        </section>

        <SnapshotSection
          bookingsToday={bookingsToday}
          presentEmployeesToday={presentEmployeesToday}
          pendingServices={pendingServices}
          ownerClaimedServices={ownerClaimedServices}
          className="lg:hidden"
        />

        <section className="grid grid-cols-1 lg:grid-cols-12 gap-6">
          <div className="lg:col-span-8">
            <section className="rounded-[28px] border border-zinc-100 bg-white p-4 sm:p-6 h-[clamp(520px,70vh,860px)]">
              <BookingList
                bookings={allBookings}
                variant="embedded"
                className="h-full"
              />
            </section>
          </div>
          <div className="lg:col-span-4 space-y-6">
            <SnapshotSection
              bookingsToday={bookingsToday}
              presentEmployeesToday={presentEmployeesToday}
              pendingServices={pendingServices}
              ownerClaimedServices={ownerClaimedServices}
              className="hidden lg:block"
            />

            <section className="rounded-[28px] border border-zinc-100 bg-white p-4 sm:p-6">
              <OwnerServiceQueue
                businessSlug={businessSlug}
                pendingServices={pendingServices}
                claimedServices={ownerClaimedServices}
                variant="embedded"
                className="h-[clamp(400px,53vh,600px)]"
              />
            </section>

            <section className="rounded-[28px] border border-zinc-100 bg-white p-4 sm:p-6">
              <SalesChart
                bookings={bookings}
                allBookings={allBookings}
                payroll={payroll}
                variant="embedded"
                className="h-[clamp(450px,40vh,500px)]"
              />
            </section>
          </div>
        </section>
      </main>
    </div>
  );
}

function SnapshotSection({
  bookingsToday,
  presentEmployeesToday,
  pendingServices,
  ownerClaimedServices,
  className,
}: {
  bookingsToday: number;
  presentEmployeesToday: number;
  pendingServices: OwnerPendingService[];
  ownerClaimedServices: OwnerClaimedService[];
  className?: string;
}) {
  const todayLabel = formatPH(new Date(), "MMM d, yyyy");

  return (
    <section
      className={cn(
        "rounded-[24px] border border-zinc-100 bg-white p-4 sm:p-5",
        className,
      )}
    >
      <div className="flex items-center justify-between">
        <div>
          <p className="text-xs uppercase tracking-wider text-zinc-500 font-semibold">
            Today
          </p>
          <p className="text-sm font-semibold text-zinc-900">{todayLabel}</p>
        </div>
        <span className="text-xs text-zinc-500">Snapshot</span>
      </div>
      <div className="mt-3 grid grid-cols-2 gap-2">
        <div className="rounded-xl border border-zinc-100 bg-zinc-50 px-3 py-2">
          <div className="text-[10px] uppercase tracking-wider text-zinc-500 font-semibold">
            Bookings
          </div>
          <div className="text-lg font-bold text-zinc-900">
            {bookingsToday.toLocaleString()}
          </div>
        </div>
        <div className="rounded-xl border border-zinc-100 bg-zinc-50 px-3 py-2">
          <div className="text-[10px] uppercase tracking-wider text-zinc-500 font-semibold">
            Active Staff
          </div>
          <div className="text-lg font-bold text-zinc-900">
            {presentEmployeesToday.toLocaleString()}
          </div>
        </div>
        <div className="rounded-xl border border-zinc-100 bg-zinc-50 px-3 py-2">
          <div className="text-[10px] uppercase tracking-wider text-zinc-500 font-semibold">
            Queue Pending
          </div>
          <div className="text-lg font-bold text-zinc-900">
            {pendingServices.length.toLocaleString()}
          </div>
        </div>
        <div className="rounded-xl border border-zinc-100 bg-zinc-50 px-3 py-2">
          <div className="text-[10px] uppercase tracking-wider text-zinc-500 font-semibold">
            Claimed
          </div>
          <div className="text-lg font-bold text-zinc-900">
            {ownerClaimedServices.length.toLocaleString()}
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { useMemo, useState } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { cn } from "@/lib/utils";
import { formatPH } from "@/lib/date-utils";
import { AvailedServiceStatus } from "@/prisma/generated/prisma/client";
import {
  CheckCircle2,
  Clock,
  Inbox,
  RefreshCcw,
  UserCheck,
  XCircle,
} from "lucide-react";
import { toast } from "sonner";
import {
  claimServiceAsOwnerAction,
  getOwnerClaimedServicesAction,
  getPendingServicesAction,
  markServiceServedAsOwnerAction,
  unclaimServiceAsOwnerAction,
  unserveServiceAsOwnerAction,
} from "@/lib/server actions/dashboard";

export type OwnerPendingService = {
  id: number;
  service: {
    name: string;
    duration: number | null;
  };
  booking: {
    customer: {
      name: string;
    };
  };
  scheduled_at: Date | null;
  price: number;
  package_id: number | null;
  package: {
    name: string;
  } | null;
};

export type OwnerClaimedService = OwnerPendingService & {
  claimed_at: Date | null;
  status: AvailedServiceStatus;
};

type OwnerClaimedResponse =
  | { success: true; data: OwnerClaimedService[] }
  | { success: false; error: string };

/**
 * Usage:
 * <OwnerServiceQueue
 *   businessSlug="my-biz"
 *   pendingServices={pendingServices}
 *   claimedServices={claimedServices}
 * />
 */
export default function OwnerServiceQueue({
  businessSlug,
  pendingServices: initialPending,
  claimedServices: initialClaimed,
  className,
  variant = "card",
}: {
  businessSlug: string;
  pendingServices: OwnerPendingService[];
  claimedServices: OwnerClaimedService[];
  className?: string;
  variant?: "card" | "embedded";
}) {
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = useState<"pending" | "claimed">("pending");
  const [actionState, setActionState] = useState<{
    id: number | null;
    type: "claim" | "serve" | "unclaim" | "unserve" | null;
  }>({ id: null, type: null });
  const [isRefreshing, setIsRefreshing] = useState(false);

  const pendingKey = ["owner-pending-services", businessSlug];
  const claimedKey = ["owner-claimed-services", businessSlug];

  const { data: pendingServices = [] } = useQuery({
    queryKey: pendingKey,
    queryFn: () => getPendingServicesAction(),
    initialData: initialPending,
    refetchInterval: 5000,
  });

  const { data: claimedResponse } = useQuery<OwnerClaimedResponse | any>({
    queryKey: claimedKey,
    queryFn: () => getOwnerClaimedServicesAction(),
    initialData: { success: true, data: initialClaimed },
    refetchInterval: 5000,
  });

  const claimedServices = useMemo(() => {
    if (
      claimedResponse &&
      typeof claimedResponse === "object" &&
      "success" in claimedResponse &&
      "data" in claimedResponse &&
      claimedResponse.success
    ) {
      return claimedResponse.data as OwnerClaimedService[];
    }
    return [];
  }, [claimedResponse]);

  const setAction = (id: number, type: typeof actionState.type) =>
    setActionState({ id, type });

  const clearAction = () => setActionState({ id: null, type: null });

  const getPendingCache = () =>
    queryClient.getQueryData<OwnerPendingService[]>(pendingKey) ??
    pendingServices;

  const getClaimedCache = () => {
    const cached = queryClient.getQueryData<OwnerClaimedResponse>(claimedKey);
    if (cached && cached.success) return cached.data;
    return claimedServices;
  };

  const setPendingCache = (items: OwnerPendingService[]) => {
    queryClient.setQueryData(pendingKey, items);
  };

  const setClaimedCache = (items: OwnerClaimedService[]) => {
    queryClient.setQueryData(claimedKey, { success: true, data: items });
  };

  const refreshLists = async () => {
    await Promise.all([
      queryClient.invalidateQueries({
        queryKey: pendingKey,
      }),
      queryClient.invalidateQueries({
        queryKey: claimedKey,
      }),
    ]);
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await refreshLists();
    setIsRefreshing(false);
  };

  const handleClaim = async (serviceId: number) => {
    const previousPending = getPendingCache();
    const previousClaimed = getClaimedCache();
    const target = previousPending.find((item) => item.id === serviceId);

    setAction(serviceId, "claim");
    if (target) {
      const optimisticClaim: OwnerClaimedService = {
        ...target,
        claimed_at: new Date(),
        status: "CLAIMED",
      };
      setPendingCache(previousPending.filter((item) => item.id !== serviceId));
      setClaimedCache([optimisticClaim, ...previousClaimed]);
      setActiveTab("claimed");
    }

    const result = await claimServiceAsOwnerAction(serviceId);
    clearAction();

    if (result.success) {
      toast.success("Service claimed");
      await refreshLists();
    } else {
      setPendingCache(previousPending);
      setClaimedCache(previousClaimed);
      toast.error(result.error || "Unable to claim service");
    }
  };

  const handleUnclaim = async (serviceId: number) => {
    const previousPending = getPendingCache();
    const previousClaimed = getClaimedCache();
    const target = previousClaimed.find((item) => item.id === serviceId);

    setAction(serviceId, "unclaim");
    if (target) {
      const { claimed_at, status, ...pendingItem } = target;
      setClaimedCache(previousClaimed.filter((item) => item.id !== serviceId));
      setPendingCache([pendingItem, ...previousPending]);
    }

    const result = await unclaimServiceAsOwnerAction(serviceId);
    clearAction();

    if (result.success) {
      toast.success("Service returned to queue");
      await refreshLists();
    } else {
      setPendingCache(previousPending);
      setClaimedCache(previousClaimed);
      toast.error(result.error || "Unable to unclaim service");
    }
  };

  const handleServe = async (serviceId: number) => {
    const previousClaimed = getClaimedCache();

    setAction(serviceId, "serve");
    setClaimedCache(previousClaimed.filter((item) => item.id !== serviceId));

    const result = await markServiceServedAsOwnerAction(serviceId);
    clearAction();

    if (result.success) {
      toast.success("Service marked as served");
      await refreshLists();
    } else {
      setClaimedCache(previousClaimed);
      toast.error(result.error || "Unable to mark service as served");
    }
  };

  const handleUnserve = async (serviceId: number) => {
    const previousClaimed = getClaimedCache();
    const target = previousClaimed.find((item) => item.id === serviceId);

    setAction(serviceId, "unserve");
    if (target) {
      setClaimedCache(
        previousClaimed.map((item) =>
          item.id === serviceId ? { ...item, status: "CLAIMED" } : item,
        ),
      );
    }

    const result = await unserveServiceAsOwnerAction(serviceId);
    clearAction();

    if (result.success) {
      toast.success("Service moved back to claimed");
      await refreshLists();
    } else {
      setClaimedCache(previousClaimed);
      toast.error(result.error || "Unable to revert service");
    }
  };

  const renderTime = (date: Date | null) =>
    date ? formatPH(date, "h:mm a") : "Walk-in";

  const isEmbedded = variant === "embedded";

  return (
    <Card
      className={cn(
        "flex flex-col overflow-hidden h-full",
        isEmbedded
          ? "border-0 shadow-none bg-transparent rounded-none"
          : "rounded-[32px] border-none shadow-lg shadow-zinc-200/50 bg-white",
        className,
      )}
    >
      <CardHeader
        className={cn("pb-0", isEmbedded ? "px-0 pt-0" : "md:px-8 md:pt-8")}
      >
        <div className="flex flex-col gap-6 sm:flex-row sm:items-center sm:justify-between">
          <div className="space-y-1">
            <CardTitle className="text-2xl font-bold tracking-tight text-zinc-900">
              Owner Queue
            </CardTitle>
            <CardDescription className="text-base text-zinc-500 font-medium">
              Claim and serve customers as the owner.
            </CardDescription>
          </div>
          <Button
            variant="outline"
            onClick={handleRefresh}
            className="w-full sm:w-auto rounded-2xl h-11 border-zinc-200 bg-zinc-50 font-medium"
            disabled={isRefreshing}
          >
            <RefreshCcw
              className={cn("h-4 w-4 mr-2", isRefreshing && "animate-spin")}
            />
            Refresh
          </Button>
        </div>
      </CardHeader>

      <CardContent
        className={cn(
          "py-6 flex-1 min-h-[300px] overflow-auto",
          isEmbedded ? "px-0" : "px-2 md:px-6",
        )}
      >
        <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as any)}>
          <TabsList className="w-full grid grid-cols-2 rounded-2xl bg-zinc-50 border border-zinc-100 p-1 shadow-sm h-12 overflow-hidden items-stretch">
            <TabsTrigger
              value="pending"
              className="rounded-xl h-full py-0 data-[state=active]:bg-white data-[state=active]:text-emerald-700 data-[state=active]:shadow-sm"
            >
              Pending
              <span className="ml-2 text-[10px] font-semibold text-emerald-700 bg-emerald-100/80 px-2 py-0.5 rounded-full">
                {pendingServices.length}
              </span>
            </TabsTrigger>
            <TabsTrigger
              value="claimed"
              className="rounded-xl h-full py-0 data-[state=active]:bg-white data-[state=active]:text-emerald-700 data-[state=active]:shadow-sm"
            >
              My Claims
              <span className="ml-2 text-[10px] font-semibold text-emerald-700 bg-emerald-100/80 px-2 py-0.5 rounded-full">
                {claimedServices.length}
              </span>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="pending" className="mt-4 space-y-3">
            {pendingServices.length === 0 ? (
              <div className="flex flex-col items-center justify-center text-center py-10 border-2 border-dashed border-zinc-100 rounded-2xl bg-white">
                <div className="h-12 w-12 rounded-full bg-zinc-50 text-zinc-300 flex items-center justify-center">
                  <Inbox className="h-6 w-6" />
                </div>
                <p className="mt-3 text-sm font-semibold text-zinc-900">
                  No pending services
                </p>
                <p className="text-xs text-zinc-500 max-w-[240px]">
                  The queue is clear for now. New bookings will appear here.
                </p>
              </div>
            ) : (
              pendingServices.map((item) => {
                const isBusy =
                  actionState.id === item.id && actionState.type === "claim";
                return (
                  <div
                    key={item.id}
                    className="rounded-2xl border border-zinc-100 bg-white p-4 shadow-sm hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-start justify-between gap-3">
                      <div className="space-y-2">
                        <div className="flex items-center gap-2 flex-wrap">
                          <h4 className="font-semibold text-zinc-900">
                            {item.service.name}
                          </h4>
                          {item.package && (
                            <Badge
                              variant="secondary"
                              className="bg-zinc-100 text-zinc-700 border border-zinc-200 text-[9px] font-semibold h-4 px-1"
                            >
                              {item.package.name}
                            </Badge>
                          )}
                        </div>
                        <div className="text-xs text-zinc-500 font-medium">
                          {item.booking.customer.name}
                        </div>
                        <div className="flex items-center gap-2 text-[10px] font-semibold text-zinc-400">
                          <span className="flex items-center gap-1 bg-zinc-50 text-zinc-600 px-2 py-0.5 rounded-md">
                            <Clock className="h-3 w-3" />
                            {renderTime(item.scheduled_at)}
                          </span>
                          {item.service.duration && (
                            <span>{item.service.duration} mins</span>
                          )}
                        </div>
                      </div>
                      <div className="flex flex-col items-end gap-2">
                        <span className="text-sm font-bold text-zinc-900 bg-zinc-50 px-2 py-0.5 rounded-md border border-zinc-100">
                          {item.price.toLocaleString()}
                        </span>
                        <Button
                          size="sm"
                          className="h-11 min-h-[44px] rounded-xl bg-emerald-600 hover:bg-emerald-700 text-white shadow-sm shadow-emerald-200"
                          onClick={() => handleClaim(item.id)}
                          disabled={isBusy}
                        >
                          {isBusy ? "Claiming..." : "Claim"}
                        </Button>
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </TabsContent>

          <TabsContent value="claimed" className="mt-4 space-y-3">
            {claimedServices.length === 0 ? (
              <div className="flex flex-col items-center justify-center text-center py-10 border-2 border-dashed border-zinc-100 rounded-2xl bg-zinc-50/60">
                <div className="h-12 w-12 rounded-full bg-white text-zinc-300 flex items-center justify-center">
                  <UserCheck className="h-6 w-6" />
                </div>
                <p className="mt-3 text-sm font-semibold text-zinc-900">
                  No claimed services
                </p>
                <p className="text-xs text-zinc-500 max-w-[240px]">
                  Claim a pending service to track it here.
                </p>
              </div>
            ) : (
              claimedServices.map((item) => {
                const isServing =
                  actionState.id === item.id && actionState.type === "serve";
                const isUnclaiming =
                  actionState.id === item.id && actionState.type === "unclaim";
                const isUnserving =
                  actionState.id === item.id && actionState.type === "unserve";
                return (
                  <div
                    key={item.id}
                    className="rounded-2xl border border-zinc-100 bg-white p-4 shadow-sm hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-start justify-between gap-3">
                      <div className="space-y-2">
                        <div className="flex items-center gap-2 flex-wrap">
                          <h4 className="font-semibold text-zinc-900">
                            {item.service.name}
                          </h4>
                          <Badge
                            variant="outline"
                            className="text-[9px] px-1.5 py-0.5 rounded-md border-emerald-200 text-emerald-700 bg-emerald-50"
                          >
                            {item.status}
                          </Badge>
                          {item.package && (
                            <Badge
                              variant="secondary"
                              className="bg-zinc-100 text-zinc-700 border border-zinc-200 text-[9px] font-semibold h-4 px-1"
                            >
                              {item.package.name}
                            </Badge>
                          )}
                        </div>
                        <div className="text-xs text-zinc-500 font-medium">
                          {item.booking.customer.name}
                        </div>
                        <div className="flex items-center gap-2 text-[10px] font-semibold text-zinc-400">
                          <span className="flex items-center gap-1 bg-zinc-50 text-zinc-600 px-2 py-0.5 rounded-md">
                            <Clock className="h-3 w-3" />
                            {renderTime(item.scheduled_at)}
                          </span>
                          {item.claimed_at && (
                            <span className="text-zinc-400">
                              Claimed {formatPH(item.claimed_at, "h:mm a")}
                            </span>
                          )}
                        </div>
                      </div>
                      <div className="flex flex-col items-end gap-2">
                        <span className="text-sm font-bold text-zinc-900 bg-zinc-50 px-2 py-0.5 rounded-md border border-zinc-100">
                          {item.price.toLocaleString()}
                        </span>
                        <div className="flex items-center gap-2">
                          <Button
                            size="sm"
                            className="h-11 min-h-[44px] rounded-xl bg-emerald-600 hover:bg-emerald-700 text-white shadow-sm shadow-emerald-200"
                            onClick={() => handleServe(item.id)}
                            disabled={isServing}
                          >
                            {isServing ? "Saving..." : "Mark Served"}
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            className="h-11 min-h-[44px] rounded-xl border-zinc-200 text-zinc-600 hover:text-zinc-900"
                            onClick={() =>
                              item.status === "CLAIMED"
                                ? handleUnclaim(item.id)
                                : handleUnserve(item.id)
                            }
                            disabled={isUnclaiming || isUnserving}
                          >
                            {item.status === "CLAIMED" ? (
                              <>
                                <XCircle className="h-3.5 w-3.5 mr-1" />
                                {isUnclaiming ? "Releasing..." : "Unclaim"}
                              </>
                            ) : (
                              <>
                                <CheckCircle2 className="h-3.5 w-3.5 mr-1" />
                                {isUnserving ? "Reverting..." : "Undo Serve"}
                              </>
                            )}
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

"use client";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Employee, User, Payslip } from "@/prisma/generated/prisma/client";
import { formatPH } from "@/lib/date-utils";
import { FileText, Plus } from "lucide-react";
import { useState } from "react";
import { PayslipGenerationDialog } from "./PayslipGenerationDialog";
import { useRouter } from "next/navigation";

// Define the type matching what we fetch in OwnerDashboardDataContainer
type EmployeeWithDetails = Employee & {
  user: User;
  payslips: Payslip[];
};

export function PayrollList({
  employees,
}: {
  employees: EmployeeWithDetails[];
}) {
  const [selectedEmployeeId, setSelectedEmployeeId] = useState<number | null>(
    null,
  );
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const router = useRouter();

  const handleSuccess = () => {
    router.refresh();
  };

  return (
    <>
      <Card className="h-full shadow-sm border-zinc-100 flex flex-col">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="space-y-1">
              <CardTitle className="text-xl font-semibold">Payroll</CardTitle>
              <CardDescription>
                Manage employee salaries and generate payslips
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent className="flex-1 overflow-auto p-0 border-t min-h-[400px]">
          <Table>
            <TableHeader className="bg-zinc-50/50 sticky top-0 z-10">
              <TableRow>
                <TableHead>Employee</TableHead>
                <TableHead>Daily Rate</TableHead>
                <TableHead>Commission</TableHead>
                <TableHead>Last Payout</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {employees.length > 0 ? (
                employees.map((emp) => {
                  const lastPayslip = emp.payslips[0];
                  return (
                    <TableRow key={emp.id} className="hover:bg-zinc-50/50">
                      <TableCell className="font-medium">
                        <div className="flex flex-col">
                          <span>{emp.user.name}</span>
                          <span className="text-xs text-muted-foreground">
                            {emp.user.email}
                          </span>
                        </div>
                      </TableCell>
                      <TableCell>{emp.daily_rate.toLocaleString()}</TableCell>
                      <TableCell>{emp.commission_percentage}%</TableCell>
                      <TableCell>
                        {lastPayslip ? (
                          <div className="flex flex-col gap-1">
                            <span className="text-sm font-medium">
                              {formatPH(lastPayslip.ending_date, "MMM d, yyyy")}
                            </span>
                            <Badge
                              variant="outline"
                              className="w-fit text-[10px] h-5"
                            >
                              {lastPayslip.total_salary.toLocaleString()}
                            </Badge>
                          </div>
                        ) : (
                          <span className="text-muted-foreground text-sm">
                            Never
                          </span>
                        )}
                      </TableCell>
                      <TableCell className="text-right">
                        <Button
                          size="sm"
                          variant="outline"
                          className="gap-2"
                          onClick={() => {
                            setSelectedEmployeeId(emp.id);
                            setIsDialogOpen(true);
                          }}
                        >
                          <Plus className="h-4 w-4" />
                          Generate Payslip
                        </Button>
                      </TableCell>
                    </TableRow>
                  );
                })
              ) : (
                <TableRow>
                  <TableCell
                    colSpan={5}
                    className="h-24 text-center text-muted-foreground"
                  >
                    No employees found.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      <PayslipGenerationDialog
        employeeId={selectedEmployeeId}
        open={isDialogOpen}
        onOpenChange={setIsDialogOpen}
        onSuccess={handleSuccess}
      />
    </>
  );
}

