"use client";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Plus, Loader2, Check, ChevronsUpDown } from "lucide-react";
import { useState, useMemo } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Textarea } from "@/components/ui/textarea";
import { createSaleEvent } from "@/lib/server actions/sale-event";
import { toast } from "sonner";
import { useRouter } from "next/navigation";
import { DiscountType } from "@/prisma/generated/prisma/enums";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { cn } from "@/lib/utils";

const saleEventSchema = z.object({
  title: z.string().min(1, "Title is required"),
  description: z.string().optional(),
  startDate: z
    .string()
    .refine((val) => !isNaN(Date.parse(val)), "Invalid date"),
  endDate: z.string().refine((val) => !isNaN(Date.parse(val)), "Invalid date"),
  discountType: z.enum([DiscountType.PERCENTAGE, DiscountType.FLAT]),
  discountValue: z.coerce.number<number>().min(0, "Value must be positive"),
  serviceIds: z.array(z.number()),
  packageIds: z.array(z.number()),
});

type SaleEventFormValues = z.infer<typeof saleEventSchema>;

interface CreateSaleEventDialogProps {
  businessSlug: string;
  services: { id: number; name: string; category: string }[];
  packages: { id: number; name: string }[];
}

export function CreateSaleEventDialog({
  businessSlug,
  services,
  packages,
}: CreateSaleEventDialogProps) {
  const [open, setOpen] = useState(false);
  const router = useRouter();

  const form = useForm<SaleEventFormValues>({
    resolver: zodResolver(saleEventSchema),
    defaultValues: {
      title: "",
      description: "",
      startDate: new Date().toISOString().slice(0, 16),
      endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)
        .toISOString()
        .slice(0, 16),
      discountType: "PERCENTAGE" as DiscountType,
      discountValue: 10,
      serviceIds: [],
      packageIds: [],
    },
  });

  // Group services by category
  const groupedServices = useMemo(() => {
    const groups: Record<string, typeof services> = {};
    services.forEach((service) => {
      const category = service.category || "Uncategorized";
      if (!groups[category]) {
        groups[category] = [];
      }
      groups[category].push(service);
    });
    return groups;
  }, [services]);

  const onSubmit = async (data: SaleEventFormValues) => {
    try {
      const result = await createSaleEvent({
        businessSlug,
        title: data.title,
        description: data.description,
        startDate: new Date(data.startDate),
        endDate: new Date(data.endDate),
        discountType: data.discountType,
        discountValue: data.discountValue,
        serviceIds: data.serviceIds,
        packageIds: data.packageIds,
      });

      if (result.success) {
        toast.success("Sale event created successfully");
        setOpen(false);
        form.reset();
        router.refresh();
      } else {
        toast.error(result.error || "Failed to create sale event");
      }
    } catch (error) {
      toast.error("An unexpected error occurred");
    }
  };

  const selectedServiceIds = form.watch("serviceIds");
  const selectedPackageIds = form.watch("packageIds");

  // Helper functions for services
  const toggleService = (id: number) => {
    const current = form.getValues("serviceIds");
    if (current.includes(id)) {
      form.setValue(
        "serviceIds",
        current.filter((i) => i !== id),
      );
    } else {
      form.setValue("serviceIds", [...current, id]);
    }
  };

  const toggleCategory = (category: string) => {
    const categoryServiceIds = groupedServices[category].map((s) => s.id);
    const current = form.getValues("serviceIds");
    const allSelected = categoryServiceIds.every((id) => current.includes(id));

    if (allSelected) {
      // Deselect all in category
      form.setValue(
        "serviceIds",
        current.filter((id) => !categoryServiceIds.includes(id)),
      );
    } else {
      // Select all in category
      const uniqueIds = Array.from(
        new Set([...current, ...categoryServiceIds]),
      );
      form.setValue("serviceIds", uniqueIds);
    }
  };

  const toggleAllServices = () => {
    if (selectedServiceIds.length === services.length) {
      form.setValue("serviceIds", []);
    } else {
      form.setValue(
        "serviceIds",
        services.map((s) => s.id),
      );
    }
  };

  // Helper functions for packages
  const togglePackage = (id: number) => {
    const current = form.getValues("packageIds");
    if (current.includes(id)) {
      form.setValue(
        "packageIds",
        current.filter((i) => i !== id),
      );
    } else {
      form.setValue("packageIds", [...current, id]);
    }
  };

  const toggleAllPackages = () => {
    if (selectedPackageIds.length === packages.length) {
      form.setValue("packageIds", []);
    } else {
      form.setValue(
        "packageIds",
        packages.map((p) => p.id),
      );
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button className="bg-emerald-600 hover:bg-emerald-700 text-white rounded-xl shadow-md shadow-emerald-600/10 active:scale-[0.98] transition-all font-medium">
          <Plus className="mr-2 h-4 w-4" />
          Create Event
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[700px] max-h-[90vh] flex flex-col p-0 gap-0 bg-white">
        <DialogHeader className="p-6 pb-2">
          <DialogTitle className="text-xl font-bold text-zinc-900">
            Create Sale Event
          </DialogTitle>
          <DialogDescription className="text-zinc-500">
            Configure a new promotional campaign.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form
            onSubmit={form.handleSubmit(onSubmit)}
            className="flex-1 flex flex-col min-h-0"
          >
            <ScrollArea className="flex-1 overflow-y-auto">
              <div className="p-6 pt-2 space-y-8">
                {/* Basic Info Section */}
                <div className="space-y-4">
                  <h3 className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
                    <span className="w-6 h-6 rounded-full bg-emerald-100 text-emerald-700 flex items-center justify-center text-xs">
                      1
                    </span>
                    Event Details
                  </h3>
                  <div className="grid gap-4 pl-4 sm:pl-8">
                    <FormField
                      control={form.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Event Title</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="e.g. Summer Flash Sale"
                              className="focus-visible:ring-emerald-500"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Description (Optional)</FormLabel>
                          <FormControl>
                            <Textarea
                              placeholder="Internal notes or details about the sale..."
                              className="resize-none h-20 focus-visible:ring-emerald-500"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                      <FormField
                        control={form.control}
                        name="startDate"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Start Date</FormLabel>
                            <FormControl>
                              <Input
                                type="datetime-local"
                                className="focus-visible:ring-emerald-500"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="endDate"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>End Date</FormLabel>
                            <FormControl>
                              <Input
                                type="datetime-local"
                                className="focus-visible:ring-emerald-500"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>
                </div>

                <Separator />

                {/* Discount Section */}
                <div className="space-y-4">
                  <h3 className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
                    <span className="w-6 h-6 rounded-full bg-emerald-100 text-emerald-700 flex items-center justify-center text-xs">
                      2
                    </span>
                    Discount Configuration
                  </h3>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 pl-4 sm:pl-8">
                    <FormField
                      control={form.control}
                      name="discountType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Discount Type</FormLabel>
                          <Select
                            onValueChange={field.onChange}
                            defaultValue={field.value}
                          >
                            <FormControl>
                              <SelectTrigger className="focus:ring-emerald-500">
                                <SelectValue placeholder="Select type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="PERCENTAGE">
                                Percentage (%)
                              </SelectItem>
                              <SelectItem value="FLAT">
                                Flat Amount ()
                              </SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name="discountValue"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Value</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              step="0.01"
                              className="focus-visible:ring-emerald-500"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>
                </div>

                <Separator />

                {/* Scope Section */}
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-sm font-semibold text-zinc-900 flex items-center gap-2">
                      <span className="w-6 h-6 rounded-full bg-emerald-100 text-emerald-700 flex items-center justify-center text-xs">
                        3
                      </span>
                      Applicable Items
                    </h3>
                    <div className="flex gap-2">
                      <Badge
                        variant="outline"
                        className="font-normal text-zinc-500"
                      >
                        {selectedServiceIds.length} Services
                      </Badge>
                      <Badge
                        variant="outline"
                        className="font-normal text-zinc-500"
                      >
                        {selectedPackageIds.length} Packages
                      </Badge>
                    </div>
                  </div>

                  <div className="pl-4 sm:pl-8 space-y-6">
                    {/* Services Grouped by Category */}
                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <Label className="text-xs font-semibold text-zinc-500 uppercase tracking-wider">
                          Services
                        </Label>
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="h-6 text-xs text-emerald-600 hover:text-emerald-700 hover:bg-emerald-50"
                          onClick={toggleAllServices}
                        >
                          {selectedServiceIds.length === services.length
                            ? "Deselect All"
                            : "Select All Services"}
                        </Button>
                      </div>

                      <div className="border rounded-xl divide-y overflow-hidden border-zinc-200">
                        {Object.entries(groupedServices).map(
                          ([category, categoryServices]) => {
                            const isCategoryFullySelected =
                              categoryServices.every((s) =>
                                selectedServiceIds.includes(s.id),
                              );
                            const isCategoryPartiallySelected =
                              categoryServices.some((s) =>
                                selectedServiceIds.includes(s.id),
                              ) && !isCategoryFullySelected;

                            return (
                              <div key={category} className="bg-zinc-50/50">
                                <div className="px-4 py-2 bg-zinc-100/80 flex items-center justify-between">
                                  <div className="flex items-center gap-2">
                                    <Checkbox
                                      id={`cat-${category}`}
                                      checked={
                                        isCategoryFullySelected
                                          ? true
                                          : isCategoryPartiallySelected
                                            ? "indeterminate"
                                            : false
                                      }
                                      onCheckedChange={() =>
                                        toggleCategory(category)
                                      }
                                      className="data-[state=checked]:bg-emerald-600 data-[state=checked]:border-emerald-600 data-[state=indeterminate]:bg-emerald-600 data-[state=indeterminate]:border-emerald-600"
                                    />
                                    <label
                                      htmlFor={`cat-${category}`}
                                      className="text-sm font-semibold text-zinc-700 cursor-pointer select-none"
                                    >
                                      {category}
                                    </label>
                                  </div>
                                  <span className="text-xs text-zinc-500">
                                    {categoryServices.length} items
                                  </span>
                                </div>
                                <div className="p-3 grid grid-cols-1 sm:grid-cols-2 gap-3 bg-white">
                                  {categoryServices.map((service) => (
                                    <div
                                      key={service.id}
                                      className="flex items-start space-x-2 p-2 rounded-lg hover:bg-zinc-50 transition-colors"
                                    >
                                      <Checkbox
                                        id={`service-${service.id}`}
                                        checked={selectedServiceIds.includes(
                                          service.id,
                                        )}
                                        onCheckedChange={() =>
                                          toggleService(service.id)
                                        }
                                        className="mt-0.5"
                                      />
                                      <div className="grid gap-0.5 leading-none">
                                        <label
                                          htmlFor={`service-${service.id}`}
                                          className="text-sm font-medium leading-tight cursor-pointer select-none text-zinc-700"
                                        >
                                          {service.name}
                                        </label>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            );
                          },
                        )}
                        {services.length === 0 && (
                          <div className="p-8 text-center text-sm text-zinc-500">
                            No services available.
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Packages */}
                    {packages.length > 0 && (
                      <div className="space-y-3">
                        <div className="flex items-center justify-between">
                          <Label className="text-xs font-semibold text-zinc-500 uppercase tracking-wider">
                            Packages
                          </Label>
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            className="h-6 text-xs text-emerald-600 hover:text-emerald-700 hover:bg-emerald-50"
                            onClick={toggleAllPackages}
                          >
                            {selectedPackageIds.length === packages.length
                              ? "Deselect All"
                              : "Select All Packages"}
                          </Button>
                        </div>
                        <div className="border rounded-xl p-3 bg-white grid grid-cols-1 sm:grid-cols-2 gap-3">
                          {packages.map((pkg) => (
                            <div
                              key={pkg.id}
                              className="flex items-center space-x-2 p-2 rounded-lg hover:bg-zinc-50 transition-colors"
                            >
                              <Checkbox
                                id={`package-${pkg.id}`}
                                checked={selectedPackageIds.includes(pkg.id)}
                                onCheckedChange={() => togglePackage(pkg.id)}
                              />
                              <label
                                htmlFor={`package-${pkg.id}`}
                                className="text-sm font-medium leading-none cursor-pointer select-none text-zinc-700"
                              >
                                {pkg.name}
                              </label>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </ScrollArea>

            <DialogFooter className="p-4 pt-2 border-t bg-zinc-50/50">
              <Button
                type="button"
                variant="ghost"
                onClick={() => setOpen(false)}
                className="hover:bg-zinc-200/50 text-zinc-600"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                className="bg-emerald-600 hover:bg-emerald-700 text-white min-w-[120px]"
                disabled={form.formState.isSubmitting}
              >
                {form.formState.isSubmitting ? (
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                ) : (
                  <Check className="mr-2 h-4 w-4" />
                )}
                Create Event
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { format } from "date-fns";
import { Trash2, Percent, Calendar, Sparkles } from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { MoreHorizontal } from "lucide-react";
import { DiscountType } from "@/prisma/generated/prisma/enums";

interface SaleEvent {
  id: number;
  title: string;
  description: string | null;
  start_date: Date;
  end_date: Date;
  discount_type: DiscountType;
  discount_value: number;
  applicable_services: any[];
  applicable_packages: any[];
}

interface SaleEventListProps {
  events: SaleEvent[];
  onDelete: (id: number) => void;
}

export function SaleEventList({ events, onDelete }: SaleEventListProps) {
  const getStatusBadge = (event: SaleEvent) => {
    const now = new Date();
    const start = new Date(event.start_date);
    const end = new Date(event.end_date);

    if (now > end) {
      return (
        <Badge
          variant="destructive"
          className="bg-red-100 text-red-700 hover:bg-red-100 border-red-200"
        >
          Expired
        </Badge>
      );
    }
    if (now < start) {
      return (
        <Badge
          variant="secondary"
          className="bg-blue-100 text-blue-700 hover:bg-blue-100 border-blue-200"
        >
          Upcoming
        </Badge>
      );
    }
    return (
      <Badge className="bg-emerald-100 text-emerald-700 hover:bg-emerald-100 border border-emerald-200 shadow-sm">
        Active
      </Badge>
    );
  };

  if (events.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center py-16 px-4 text-center border border-dashed border-zinc-200 rounded-3xl bg-zinc-50/50">
        <div className="bg-white p-4 rounded-2xl shadow-sm border border-zinc-100 mb-4 transition-transform hover:scale-105 duration-300">
          <Percent className="h-8 w-8 text-emerald-600" />
        </div>
        <h3 className="text-lg font-semibold text-zinc-900 mb-2">
          No sale events yet
        </h3>
        <p className="text-zinc-500 max-w-sm text-sm leading-relaxed">
          Create a new sale event to attract more customers and boost your
          revenue.
        </p>
      </div>
    );
  }

  return (
    <>
      {/* Mobile Card View */}
      <div className="md:hidden grid grid-cols-1 gap-4">
        {events.map((event) => (
          <div
            key={event.id}
            className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-3 transition-all active:scale-[0.99] active:bg-zinc-50/50"
          >
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-2">
                  <div className="flex items-center gap-1.5 bg-zinc-50 px-2.5 py-1 rounded-lg border border-zinc-100">
                    <Sparkles className="h-3.5 w-3.5 text-zinc-400" />
                    <span className="font-medium text-sm text-zinc-900 tracking-wide">
                      {event.title}
                    </span>
                  </div>
                </div>
                <div className="flex flex-col gap-1">
                  <span className="text-lg font-bold text-zinc-900 flex items-center gap-1">
                    {event.discount_type === "PERCENTAGE"
                      ? `${event.discount_value}% OFF`
                      : `${event.discount_value.toFixed(2)} OFF`}
                  </span>
                  <span className="text-xs text-zinc-500">
                    {event.applicable_services.length} Services,{" "}
                    {event.applicable_packages.length} Packages
                  </span>
                </div>
              </div>

              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="-mr-2 h-8 w-8 text-zinc-400 hover:text-zinc-600"
                  >
                    <MoreHorizontal className="h-4 w-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent
                  align="end"
                  className="w-[160px] rounded-xl"
                >
                  <DropdownMenuItem
                    className="text-red-600 focus:text-red-700 focus:bg-red-50"
                    onClick={() => onDelete(event.id)}
                  >
                    <Trash2 className="mr-2 h-3.5 w-3.5" />
                    Delete
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>

            <div className="flex items-center justify-between pt-3 border-t border-zinc-50 mt-1">
              <div className="flex items-center gap-1.5 text-zinc-500">
                <Calendar className="h-3.5 w-3.5" />
                <span className="text-xs font-medium">
                  {format(new Date(event.start_date), "MMM d")} -{" "}
                  {format(new Date(event.end_date), "MMM d, yyyy")}
                </span>
              </div>
              {getStatusBadge(event)}
            </div>
          </div>
        ))}
      </div>

      {/* Desktop Table View */}
      <div className="hidden md:block rounded-3xl border border-zinc-100 overflow-hidden shadow-sm bg-white">
        <Table>
          <TableHeader className="bg-zinc-50/80 sticky top-0 z-10 backdrop-blur-sm">
            <TableRow className="hover:bg-transparent border-zinc-100">
              <TableHead className="w-[200px] font-semibold text-zinc-500 pl-6 h-12">
                Event Name
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Discount
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Scope
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Duration
              </TableHead>
              <TableHead className="font-semibold text-zinc-500 h-12">
                Status
              </TableHead>
              <TableHead className="text-right w-[100px] font-semibold text-zinc-500 pr-6 h-12">
                Actions
              </TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {events.map((event) => (
              <TableRow
                key={event.id}
                className="hover:bg-zinc-50/50 transition-colors border-zinc-100 group"
              >
                <TableCell className="pl-6 py-4">
                  <div className="flex items-center gap-2">
                    <div className="flex items-center gap-1.5 px-2.5 py-1 rounded-lg border border-zinc-100 bg-zinc-50/50 group-hover:bg-white transition-colors">
                      <Sparkles className="h-3.5 w-3.5 text-zinc-400 group-hover:text-emerald-500 transition-colors" />
                      <span className="font-medium text-sm text-zinc-700">
                        {event.title}
                      </span>
                    </div>
                  </div>
                </TableCell>
                <TableCell className="py-4">
                  <div className="flex flex-col">
                    <span className="font-semibold text-zinc-900">
                      {event.discount_type === "PERCENTAGE"
                        ? `${event.discount_value}%`
                        : `${event.discount_value.toFixed(2)}`}
                    </span>
                    <span className="text-xs text-zinc-500">
                      {event.discount_type === "PERCENTAGE"
                        ? "Percentage"
                        : "Flat Amount"}
                    </span>
                  </div>
                </TableCell>
                <TableCell className="py-4 text-zinc-600 font-medium">
                  {event.applicable_services.length +
                    event.applicable_packages.length}{" "}
                  Items
                </TableCell>
                <TableCell className="py-4 text-zinc-600">
                  <div className="flex flex-col text-xs">
                    <span>
                      {format(new Date(event.start_date), "MMM d, yyyy")}
                    </span>
                    <span className="text-zinc-400">to</span>
                    <span>
                      {format(new Date(event.end_date), "MMM d, yyyy")}
                    </span>
                  </div>
                </TableCell>
                <TableCell className="py-4">{getStatusBadge(event)}</TableCell>
                <TableCell className="text-right pr-6 py-4">
                  <Button
                    variant="ghost"
                    size="icon"
                    className="h-8 w-8 text-zinc-400 hover:text-red-600 hover:bg-red-50 transition-colors"
                    onClick={() => onDelete(event.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </>
  );
}

"use client";

import { useState } from "react";
import { CreateSaleEventDialog } from "./CreateSaleEventDialog";
import { SaleEventList } from "./SaleEventList";
import { deleteSaleEvent } from "@/lib/server actions/sale-event";
import { toast } from "sonner";
import { useRouter } from "next/navigation";
import { Percent } from "lucide-react";

interface SaleEventsPageClientProps {
  businessSlug: string;
  initialEvents: any[]; // Type already defined in List, keeping generic here or match
  services: { id: number; name: string; category: string }[];
  packages: { id: number; name: string }[];
}

export function SaleEventsPageClient({
  businessSlug,
  initialEvents,
  services,
  packages,
}: SaleEventsPageClientProps) {
  const router = useRouter();

  const handleDelete = async (id: number) => {
    try {
      const result = await deleteSaleEvent(id, businessSlug);
      if (result.success) {
        toast.success("Sale event deleted");
        router.refresh();
      } else {
        toast.error(result.error || "Failed to delete sale event");
      }
    } catch (error) {
      toast.error("An unexpected error occurred");
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold tracking-tight text-zinc-900 flex items-center gap-2">
            <div className="p-2 bg-emerald-100 rounded-xl">
              <Percent className="h-6 w-6 text-emerald-700" />
            </div>
            Sale Events
          </h2>
          <p className="text-zinc-500 mt-1">
            Manage your promotional campaigns and discounts.
          </p>
        </div>
        <CreateSaleEventDialog
          businessSlug={businessSlug}
          services={services}
          packages={packages}
        />
      </div>

      <SaleEventList events={initialEvents} onDelete={handleDelete} />
    </div>
  );
}

"use client";

import { useTransition } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import { Loader2, Wand2 } from "lucide-react";
import { toast } from "sonner";
import { format } from "date-fns";

import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";

import {
  createVoucherAction,
  generateVoucherCodeAction,
} from "@/lib/server actions/vouchers";

const formSchema = z.object({
  suffix: z.string().min(4, "Code must be at least 4 characters").max(20),
  type: z.enum(["PERCENTAGE", "FLAT"]),
  value: z.coerce.number<number>().min(0.01, "Value must be greater than 0"),
  minimum_amount: z.coerce.number<number>().min(0),
  expires_at: z.date({
    error: "Expiry date is required",
  }),
});

interface VoucherFormProps {
  onSuccess: () => void;
  initials: string;
}

export function VoucherForm({ onSuccess, initials }: VoucherFormProps) {
  const [isPending, startTransition] = useTransition();
  const [isGenerating, startGenerating] = useTransition();

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      suffix: "",
      type: "PERCENTAGE",
      value: 0,
      minimum_amount: 0,
    },
  });

  async function onSubmit(values: z.infer<typeof formSchema>) {
    startTransition(async () => {
      try {
        const fullCode = `${initials}-${values.suffix}`;
        const payload = {
          ...values,
          code: fullCode,
        };
        // removing suffix from payload passed to action is not strictly necessary if action ignores unknown props,
        // but TS might complain if I pass "suffix" to createVoucherAction which expects "code".
        // I need to map it.

        const res = await createVoucherAction({
          code: fullCode,
          type: values.type,
          value: values.value,
          minimum_amount: values.minimum_amount,
          expires_at: values.expires_at,
        });

        if (res.success) {
          toast.success("Voucher created successfully");
          onSuccess();
        } else {
          toast.error(res.error || "Failed to create voucher");
        }
      } catch (error) {
        toast.error("An error occurred");
      }
    });
  }

  const handleGenerateCode = async (e: React.MouseEvent) => {
    e.preventDefault();
    startGenerating(async () => {
      try {
        const res = await generateVoucherCodeAction();
        if (res.success && res.code) {
          // The action returns "PREFIX-SUFFIX"
          // We want just the suffix.
          // We assume the prefix matches. safely split.
          const parts = res.code.split("-");
          const suffix = parts.length > 1 ? parts[1] : parts[0];
          // fallback if no hyphen, though we enforced hyphen in backend now.

          form.setValue("suffix", suffix);
        } else {
          toast.error(res.error || "Failed to generate code");
        }
      } catch (error) {
        toast.error("Error generating code");
      }
    });
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <FormField
          control={form.control}
          name="suffix"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Voucher Code</FormLabel>
              <div className="flex gap-2">
                <div className="relative flex-1">
                  <div className="absolute left-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground font-medium select-none flex items-center">
                    {initials}-
                  </div>
                  <FormControl>
                    <Input
                      placeholder="SUMMER2026"
                      {...field}
                      className="pl-10 h-10 rounded-xl" // Add padding to make room for prefix
                      style={{ paddingLeft: `${initials.length + 3}ch` }} // dynamic padding roughly
                    />
                  </FormControl>
                </div>
                <Button
                  type="button"
                  variant="outline"
                  size="icon"
                  className="rounded-xl border-dashed"
                  onClick={handleGenerateCode}
                  disabled={isGenerating}
                >
                  {isGenerating ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Wand2 className="h-4 w-4" />
                  )}
                </Button>
              </div>
              <FormDescription>
                Unique code for customers to redeem.
              </FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="type"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Discount Type</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  defaultValue={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="h-10 rounded-xl">
                      <SelectValue placeholder="Select type" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent className="rounded-xl">
                    <SelectItem value="PERCENTAGE">Percentage (%)</SelectItem>
                    <SelectItem value="FLAT">Flat Amount ()</SelectItem>
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="value"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Value</FormLabel>
                <FormControl>
                  <Input
                    type="number"
                    step="0.01"
                    {...field}
                    className="h-10 rounded-xl"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="minimum_amount"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Minimum Spend (Optional)</FormLabel>
              <FormControl>
                <Input
                  type="number"
                  step="0.01"
                  {...field}
                  className="h-10 rounded-xl"
                />
              </FormControl>
              <FormDescription>
                Minimum booking amount required.
              </FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="expires_at"
          render={({ field }) => (
            <FormItem className="flex flex-col">
              <FormLabel>Expiration Date</FormLabel>
              <Popover>
                <PopoverTrigger asChild>
                  <FormControl>
                    <Button
                      variant={"outline"}
                      className={cn(
                        "w-full pl-3 text-left font-normal h-10 rounded-xl",
                        !field.value && "text-muted-foreground",
                      )}
                    >
                      {field.value ? (
                        format(field.value, "PPP")
                      ) : (
                        <span>Pick a date</span>
                      )}
                    </Button>
                  </FormControl>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0 rounded-xl" align="start">
                  <Calendar
                    mode="single"
                    selected={field.value}
                    onSelect={field.onChange}
                    disabled={(date) =>
                      date < new Date(new Date().setHours(0, 0, 0, 0))
                    }
                    initialFocus
                  />
                </PopoverContent>
              </Popover>
              <FormMessage />
            </FormItem>
          )}
        />

        <Button
          type="submit"
          className="w-full h-11 rounded-xl bg-emerald-600 hover:bg-emerald-700 text-white shadow-lg shadow-emerald-500/20 font-semibold"
          disabled={isPending}
        >
          {isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
          Create Voucher
        </Button>
      </form>
    </Form>
  );
}

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { format } from "date-fns";
import { Check, Copy, Trash2, Ticket, Calendar, Banknote } from "lucide-react";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { MoreHorizontal } from "lucide-react";

interface Voucher {
  id: number;
  code: string;
  type: "PERCENTAGE" | "FLAT";
  value: number;
  minimum_amount: number;
  expires_at: Date;
  is_active: boolean;
  used_by: { id: number } | null;
}

interface VoucherListProps {
  vouchers: Voucher[];
  onDelete: (id: number) => void;
  onCopy: (code: string, id: number) => void;
  copiedId: number | null;
}

export function VoucherList({
  vouchers,
  onDelete,
  onCopy,
  copiedId,
}: VoucherListProps) {
  const getStatusBadge = (voucher: Voucher) => {
    if (voucher.used_by) {
      return (
        <Badge variant="secondary" className="bg-zinc-100 text-zinc-600">
          Used
        </Badge>
      );
    }
    if (!voucher.is_active) {
      return (
        <Badge variant="outline" className="text-zinc-500 border-zinc-200">
          Inactive
        </Badge>
      );
    }
    if (new Date(voucher.expires_at) < new Date()) {
      return (
        <Badge
          variant="destructive"
          className="bg-red-100 text-red-700 hover:bg-red-100 border-red-200"
        >
          Expired
        </Badge>
      );
    }
    return (
      <Badge className="bg-emerald-100 text-emerald-700 hover:bg-emerald-100 border border-emerald-200 shadow-sm">
        Active
      </Badge>
    );
  };

  return (
    <>
      {/* Mobile Card View */}
      <div className="md:hidden grid grid-cols-1 gap-4">
        {vouchers.map((voucher) => (
          <div
            key={voucher.id}
            className="bg-white rounded-2xl p-4 shadow-sm border border-zinc-100 flex flex-col gap-3 transition-all active:scale-[0.99] active:bg-zinc-50/50"
          >
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-2">
                  <div className="flex items-center gap-1.5 bg-zinc-50 px-2.5 py-1 rounded-lg border border-zinc-100">
                    <Ticket className="h-3.5 w-3.5 text-zinc-400" />
                    <span className="font-mono text-sm font-semibold text-zinc-900 tracking-wide">
                      {voucher.code}
                    </span>
                  </div>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="h-7 w-7 text-zinc-400 hover:text-zinc-600 hover:bg-zinc-100 rounded-full"
                    onClick={() => onCopy(voucher.code, voucher.id)}
                  >
                    {copiedId === voucher.id ? (
                      <Check className="h-3.5 w-3.5 text-emerald-600" />
                    ) : (
                      <Copy className="h-3.5 w-3.5" />
                    )}
                  </Button>
                </div>
                <div className="flex flex-col gap-1">
                  <span className="text-lg font-bold text-zinc-900 flex items-center gap-1">
                    {voucher.type === "PERCENTAGE"
                      ? `${voucher.value}% OFF`
                      : `${voucher.value.toFixed(2)} OFF`}
                  </span>
                  <span className="text-xs text-zinc-500">
