import { prisma } from "@/prisma/prisma";

/**
 * Gets the count of flow reminder emails sent this week (PH timezone)
 * Week runs from Monday 00:00 to Sunday 23:59:59
 */
export async function getThisWeeksFlowRemindersCount(
  businessId: string,
): Promise<number> {
  const now = new Date();

  // Calculate Monday of current week (PH time)
  const startOfWeek = getStartOfWeekPH(now);

  // Calculate Sunday end of current week (PH time)
  const endOfWeek = getEndOfWeekPH(now);

  const count = await prisma.outboxMessage.count({
    where: {
      business_id: businessId,
      event_type: "FLOW_REMINDER_SENT",
      processed: true, // Only count successfully sent reminders
      created_at: {
        gte: startOfWeek,
        lte: endOfWeek,
      },
    },
  });

  return count;
}

/**
 * Helper: Get Monday 00:00:00 of current week in PH timezone
 */
function getStartOfWeekPH(date: Date): Date {
  const formatter = new Intl.DateTimeFormat("en-US", {
    timeZone: "Asia/Manila",
    year: "numeric",
    month: "numeric",
    day: "numeric",
    weekday: "short",
  });

  const parts = formatter.formatToParts(date);
  const year = parseInt(parts.find((p) => p.type === "year")?.value || "0");
  const month =
    parseInt(parts.find((p) => p.type === "month")?.value || "0") - 1;
  const day = parseInt(parts.find((p) => p.type === "day")?.value || "0");
  const weekday = parts.find((p) => p.type === "weekday")?.value;

  // Get day of week (0 = Sunday, 1 = Monday, ..., 6 = Saturday)
  const dayMap: Record<string, number> = {
    Sun: 0,
    Mon: 1,
    Tue: 2,
    Wed: 3,
    Thu: 4,
    Fri: 5,
    Sat: 6,
  };
  const currentDay = dayMap[weekday || "Mon"] || 1;

  // Calculate days to subtract to get to Monday
  const daysToMonday = currentDay === 0 ? 6 : currentDay - 1;

  // Create date for Monday in PH timezone
  const mondayDate = new Date(date);
  mondayDate.setDate(mondayDate.getDate() - daysToMonday);

  const mondayParts = new Intl.DateTimeFormat("en-US", {
    timeZone: "Asia/Manila",
    year: "numeric",
    month: "numeric",
    day: "numeric",
  }).formatToParts(mondayDate);

  const mondayYear = parseInt(
    mondayParts.find((p) => p.type === "year")?.value || "0",
  );
  const mondayMonth = parseInt(
    mondayParts.find((p) => p.type === "month")?.value || "0",
  );
  const mondayDay = parseInt(
    mondayParts.find((p) => p.type === "day")?.value || "0",
  );

  const isoString = `${mondayYear}-${String(mondayMonth).padStart(2, "0")}-${String(mondayDay).padStart(2, "0")}T00:00:00+08:00`;
  return new Date(isoString);
}

/**
 * Helper: Get Sunday 23:59:59 of current week in PH timezone
 */
function getEndOfWeekPH(date: Date): Date {
  const startOfWeek = getStartOfWeekPH(date);
  // Add 7 days minus 1ms to get end of Sunday
  return new Date(startOfWeek.getTime() + 7 * 24 * 60 * 60 * 1000 - 1);
}

import { expect, test, describe, beforeAll, afterAll } from "vitest";
import { getStartOfDayPH, getEndOfDayPH, formatPH } from "@/lib/date-utils";

describe("Date Utils (PH Timezone)", () => {
  // We can't easily change process.env.TZ in the middle of a test file in some environments,
  // but we can trust that the utility functions use the hardcoded "Asia/Manila".

  test("getStartOfDayPH returns 00:00:00+08:00", () => {
    // Create a date that is definitely noon UTC
    const date = new Date("2024-01-01T12:00:00Z");

    // In PH (UTC+8), 12:00 UTC is 20:00 PH.
    // So "Start of Day" for this date should be 2024-01-01 00:00:00 PH time.
    // PH Offset is +08:00.
    // 2024-01-01T00:00:00+08:00 is 2023-12-31T16:00:00Z in UTC.

    const startOfDay = getStartOfDayPH(date);

    // Check ISO string to verify accurate instant
    expect(startOfDay.toISOString()).toBe("2023-12-31T16:00:00.000Z");
  });

  test("getEndOfDayPH returns 23:59:59.999+08:00", () => {
    const date = new Date("2024-01-01T12:00:00Z");
    const endOfDay = getEndOfDayPH(date);

    // 2024-01-01 23:59:59.999 PH time
    // - 8 hours = 15:59:59.999 UTC
    expect(endOfDay.toISOString()).toBe("2024-01-01T15:59:59.999Z");
  });

  test("formatPH formats correctly for PH timezone", () => {
    // 2024-01-01 00:00 UTC = 08:00 AM PH
    const date = new Date("2024-01-01T00:00:00Z");

    const formatted = formatPH(date, "h:mm a");
    expect(formatted).toBe("8:00 AM");
  });

  test("formatPH handles null/undefined", () => {
    expect(formatPH(null, "h:mm a")).toBe("Unscheduled");
    expect(formatPH(undefined, "h:mm a")).toBe("Unscheduled");
  });
});

"use server";

import { prisma } from "@/prisma/prisma";
import { revalidatePath } from "next/cache";
import { requireAuth } from "@/lib/auth/guards";

export async function searchCustomer(name: string, businessSlug: string) {
  const auth = await requireAuth();
  if (!auth.success) return { success: false, error: "Unauthorized" };

  try {
    const customers = await prisma.customer.findMany({
      where: {
        name: {
          startsWith: name,
          mode: "insensitive",
        },
        business: {
          slug: businessSlug,
        },
      },
      take: 5,
    });

    return { success: true, data: customers };
  } catch (err) {
    return { success: false, error: "Failed to search customers" };
  }
}

export async function createCustomer(data: {
  businessSlug: string;
  name: string;
  email?: string;
  phone?: string;
}) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const business = await prisma.business.findUnique({
      where: { slug: businessSlug }, // ensuring we use the session slug
    });

    if (!business) {
      return { success: false, error: "Business not found" };
    }

    const customer = await prisma.customer.create({
      data: {
        name: data.name,
        email: data.email,
        phone: data.phone,
        business_id: business.id,
      },
    });

    revalidatePath(`/app/${businessSlug}/customers`);
    return { success: true, data: customer };
  } catch (error) {
    console.error("Create customer error:", error);
    return { success: false, error: "Failed to create customer" };
  }
}

export async function updateCustomer(
  id: string,
  data: {
    name: string;
    email?: string;
    phone?: string;
    businessSlug: string;
  },
) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const customer = await prisma.customer.update({
      where: { id },
      data: {
        name: data.name,
        email: data.email,
        phone: data.phone,
      },
    });

    revalidatePath(`/app/${businessSlug}/customers`);
    return { success: true, data: customer };
  } catch (error) {
    console.error("Update customer error:", error);
    return { success: false, error: "Failed to update customer" };
  }
}

export async function deleteCustomer(id: string) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    await prisma.customer.delete({
      where: { id },
    });

    revalidatePath(`/app/${businessSlug}/customers`);
    return { success: true };
  } catch (error) {
    console.error("Delete customer error:", error);
    return { success: false, error: "Failed to delete customer" };
  }
}

type FlowDelayUnit = "DAYS" | "WEEKS" | "MONTHS";
type FlowType = "REQUIRED" | "SUGGESTED";

export type CustomerFlowStatus = {
  triggerServiceId: number;
  triggerServiceName: string;
  suggestedServiceId: number;
  suggestedServiceName: string;
  flowType: FlowType;
  status: "PENDING" | "COMPLETED";
  dueDate: Date;
  completedAt?: Date | null;
  lastServiceDate: Date;
  delayDuration: number;
  delayUnit: FlowDelayUnit;
};

export type CustomerBookingHistoryItem = {
  id: number;
  status: string;
  payment_method: string;
  scheduled_at: Date | null;
  estimated_end: Date | null;
  created_at: Date;
  grand_total: number;
  total_discount: number;
  downpayment: number | null;
  availed_services: {
    id: number;
    status: string;
    scheduled_at: Date | null;
    estimated_end: Date | null;
    final_price: number;
    service: { name: string } | null;
  }[];
};

export type CustomerHistoryData = {
  bookings: CustomerBookingHistoryItem[];
  nextAppointment: {
    bookingId: number;
    scheduledAt: Date;
    estimatedEnd: Date | null;
    services: string[];
  } | null;
  flowStatus: CustomerFlowStatus[];
};

const addFlowDelay = (
  base: Date,
  duration: number,
  unit: FlowDelayUnit,
) => {
  const dueDate = new Date(base);
  if (unit === "DAYS") {
    dueDate.setDate(dueDate.getDate() + duration);
  } else if (unit === "WEEKS") {
    dueDate.setDate(dueDate.getDate() + duration * 7);
  } else if (unit === "MONTHS") {
    dueDate.setMonth(dueDate.getMonth() + duration);
  }
  return dueDate;
};

export async function getCustomerHistory(
  customerId: string,
): Promise<
  | { success: true; data: CustomerHistoryData }
  | { success: false; error: string }
> {
  const auth = await requireAuth();
  if (!auth.success) return auth;

  const { businessSlug } = auth;

  try {
    const business = await prisma.business.findUnique({
      where: { slug: businessSlug },
      select: { id: true },
    });

    if (!business) {
      return { success: false, error: "Business not found" };
    }

    const customer = await prisma.customer.findFirst({
      where: { id: customerId, business_id: business.id },
      select: { id: true },
    });

    if (!customer) {
      return { success: false, error: "Customer not found" };
    }

    const bookings = await prisma.booking.findMany({
      where: {
        customer_id: customerId,
        business_id: business.id,
      },
      orderBy: [{ scheduled_at: "desc" }, { created_at: "desc" }],
      select: {
        id: true,
        status: true,
        payment_method: true,
        scheduled_at: true,
        estimated_end: true,
        created_at: true,
        grand_total: true,
        total_discount: true,
        downpayment: true,
        availed_services: {
          orderBy: { scheduled_at: "asc" },
          select: {
            id: true,
            status: true,
            scheduled_at: true,
            estimated_end: true,
            final_price: true,
            service: { select: { name: true } },
          },
        },
      },
    });

    const nextBooking = await prisma.booking.findFirst({
      where: {
        customer_id: customerId,
        business_id: business.id,
        status: { not: "CANCELLED" },
        scheduled_at: { gt: new Date() },
      },
      orderBy: { scheduled_at: "asc" },
      select: {
        id: true,
        scheduled_at: true,
        estimated_end: true,
        availed_services: {
          select: { service: { select: { name: true } } },
        },
      },
    });

    const nextAppointment = nextBooking
      ? {
          bookingId: nextBooking.id,
          scheduledAt: nextBooking.scheduled_at!,
          estimatedEnd: nextBooking.estimated_end,
          services:
            nextBooking.availed_services
              ?.map((item) => item.service?.name)
              .filter(Boolean) || [],
        }
      : null;

    const availedServices = await prisma.availedService.findMany({
      where: {
        booking: {
          customer_id: customerId,
          business_id: business.id,
          status: { not: "CANCELLED" },
        },
      },
      orderBy: { created_at: "desc" },
      include: {
        service: {
          include: {
            flow_triggers: {
              include: {
                suggested_service: true,
              },
            },
          },
        },
      },
    });

    const serviceDatesMap = new Map<number, Date[]>();
    for (const availed of availedServices) {
      const date = availed.served_at || availed.created_at;
      const list = serviceDatesMap.get(availed.service_id) || [];
      list.push(date);
      serviceDatesMap.set(availed.service_id, list);
    }

    for (const list of serviceDatesMap.values()) {
      list.sort((a, b) => a.getTime() - b.getTime());
    }

    const flowStatus: CustomerFlowStatus[] = [];
    const seenFlows = new Set<string>();

    for (const availed of availedServices) {
      const triggers = availed.service.flow_triggers || [];
      if (triggers.length === 0) continue;

      for (const flow of triggers) {
        const key = `${flow.trigger_service_id}:${flow.suggested_service_id}`;
        if (seenFlows.has(key)) continue;
        seenFlows.add(key);

        const lastServiceDate = availed.served_at || availed.created_at;
        const dueDate = addFlowDelay(
          lastServiceDate,
          flow.delay_duration,
          flow.delay_unit as FlowDelayUnit,
        );

        const suggestedDates =
          serviceDatesMap.get(flow.suggested_service_id) || [];
        const completedAt =
          suggestedDates.find((d) => d >= lastServiceDate) || null;

        flowStatus.push({
          triggerServiceId: flow.trigger_service_id,
          triggerServiceName: availed.service.name,
          suggestedServiceId: flow.suggested_service_id,
          suggestedServiceName: flow.suggested_service.name,
          flowType: flow.type as FlowType,
          status: completedAt ? "COMPLETED" : "PENDING",
          dueDate,
          completedAt,
          lastServiceDate,
          delayDuration: flow.delay_duration,
          delayUnit: flow.delay_unit as FlowDelayUnit,
        });
      }
    }

    return {
      success: true,
      data: {
        bookings: bookings as CustomerBookingHistoryItem[],
        nextAppointment,
        flowStatus,
      },
    };
  } catch (error) {
    console.error("Get customer history error:", error);
    return { success: false, error: "Failed to fetch customer history" };
  }
}

"use server";

import { prisma } from "@/prisma/prisma";
import {
  AvailedServiceStatus,
  BookingStatus,
  ServiceProviderType,
} from "@/prisma/generated/prisma/client";
import { revalidatePath } from "next/cache";
import { requireAuth } from "@/lib/auth/guards";
import { getActiveSaleEvents } from "@/lib/server actions/sale-event";
import { getApplicableDiscount } from "@/lib/utils/pricing";
import {
  claimServiceSchema,
  serviceIdSchema,
  markServedSchema,
} from "@/lib/zod-schemas/dashboard";

export async function claimServiceAction(
  serviceId: number,
  employeeId: number,
) {
  const validation = claimServiceSchema.safeParse({ serviceId, employeeId });
  if (!validation.success) {
    return { success: false, error: "Invalid input" };
  }

  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    // Fetch service details for discount calculation
    const serviceToClaim = await prisma.availedService.findUnique({
      where: { id: serviceId },
      select: {
        price: true,
        service_id: true,
        package_id: true,
      },
    });

    if (!serviceToClaim) {
      return { success: false, error: "Service not found." };
    }

    const saleEventsResult = await getActiveSaleEvents(businessSlug);
    const saleEvents =
      (saleEventsResult.success && saleEventsResult.data) || [];

    const discountInfo = getApplicableDiscount(
      serviceToClaim.service_id,
      serviceToClaim.package_id ?? undefined,
      serviceToClaim.price,
      saleEvents,
    );

    const updateData: any = {
      status: AvailedServiceStatus.CLAIMED,
      served_by_id: employeeId,
      served_by_type: ServiceProviderType.EMPLOYEE,
      claimed_at: new Date(),
    };

    if (discountInfo) {
      updateData.final_price = discountInfo.finalPrice;
      updateData.discount = discountInfo.discount;
      updateData.discount_reason = discountInfo.reason;
      updateData.commission_base = discountInfo.finalPrice;
    } else {
      updateData.final_price = serviceToClaim.price;
      updateData.discount = 0;
      updateData.discount_reason = null;
      updateData.commission_base = serviceToClaim.price;
    }

    const result = await prisma.availedService.update({
      where: {
        id: serviceId,
        status: AvailedServiceStatus.PENDING,
        booking: { business: { slug: businessSlug } },
      },
      data: updateData,
    });

    revalidatePath(`/app/${businessSlug}`);
    return { success: true, data: result };
  } catch (error) {
    const e = error as { code?: string };
    if (e.code === "P2025") {
      return { success: false, error: "Service already claimed or not found." };
    }
    console.error("Error claiming service:", error);
    return { success: false, error: "Service already claimed or not found." };
  }
}

export async function unclaimServiceAction(serviceId: number) {
  const validation = serviceIdSchema.safeParse({ serviceId });
  if (!validation.success) {
    return { success: false, error: "Invalid input" };
  }

  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    // Get original price to reset fields
    const service = await prisma.availedService.findUnique({
      where: { id: serviceId },
      select: { price: true },
    });

    if (!service) return { success: false, error: "Service not found" };

    const result = await prisma.availedService.update({
      where: {
        id: serviceId,
        booking: { business: { slug: businessSlug } },
      },
      data: {
        status: AvailedServiceStatus.PENDING,
        served_by_id: null,
        served_by_type: null,
        claimed_at: null,
        final_price: service.price,
        discount: 0,
        discount_reason: null,
        commission_base: service.price,
      },
    });

    revalidatePath(`/app/${businessSlug}`);
    return { success: true, data: result };
  } catch (error) {
    const e = error as { code?: string };
    if (e.code === "P2025") {
      return { success: false, error: "Unable to unclaim service." };
    }
    console.error("Error unclaiming service:", error);
    return { success: false, error: "Unable to unclaim service." };
  }
}

export async function markServiceServedAction(
  serviceId: number,
  employeeId: number,
) {
  const validation = markServedSchema.safeParse({ serviceId, employeeId });
  if (!validation.success) {
    return { success: false, error: "Invalid input" };
  }

  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const result = await prisma.$transaction(async (tx) => {
      const service = await tx.availedService.findUnique({
        where: {
          id: serviceId,
          booking: { business: { slug: businessSlug } },
        },
        select: {
          commission_base: true,
          price: true,
          booking_id: true,
        },
      });

      if (!service) throw new Error("Service not found");

      const employee = await tx.employee.findUnique({
        where: { id: employeeId },
        select: { commission_percentage: true },
      });

      if (!employee) throw new Error("Employee not found");

      const baseAmount = service.commission_base ?? service.price;
      const commissionAmount =
        (baseAmount * employee.commission_percentage) / 100;

      await tx.employee.update({
        where: { id: employeeId },
        data: {
          salary: { increment: commissionAmount },
        },
      });

      const updatedService = await tx.availedService.update({
        where: {
          id: serviceId,
          served_by_id: employeeId,
        },
        data: {
          status: AvailedServiceStatus.COMPLETED,
          served_at: new Date(),
          completed_at: new Date(),
        },
      });

      const remainingUnserved = await tx.availedService.count({
        where: {
          booking_id: service.booking_id,
          status: {
            notIn: [
              AvailedServiceStatus.COMPLETED,
              AvailedServiceStatus.CANCELLED,
            ],
          },
        },
      });

      if (remainingUnserved === 0) {
        await tx.booking.update({
          where: { id: service.booking_id },
          data: { status: BookingStatus.COMPLETED },
        });
      }

      return updatedService;
    });

    revalidatePath(`/app/${businessSlug}`);
    return { success: true, data: result };
  } catch (error) {
    const e = error as { code?: string };
    if (e.code === "P2025") {
      return { success: false, error: "Unable to mark as served." };
    }
    console.error("Error marking service as served:", error);
    return { success: false, error: "Unable to mark as served." };
  }
}

export async function unserveServiceAction(serviceId: number) {
  const validation = serviceIdSchema.safeParse({ serviceId });
  if (!validation.success) {
    return { success: false, error: "Invalid input" };
  }

  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const result = await prisma.$transaction(async (tx) => {
      const service = await tx.availedService.findUnique({
        where: {
          id: serviceId,
          booking: { business: { slug: businessSlug } },
        },
        select: {
          commission_base: true,
          price: true,
          served_by_id: true,
          booking_id: true,
          booking: { select: { status: true } },
        },
      });

      if (!service || !service.served_by_id)
        throw new Error("Service not found or not served");

      const employee = await tx.employee.findUnique({
        where: { id: service.served_by_id },
        select: { commission_percentage: true },
      });

      if (!employee) throw new Error("Employee not found");

      const baseAmount = service.commission_base ?? service.price;
      const commissionAmount =
        (baseAmount * employee.commission_percentage) / 100;

      await tx.employee.update({
        where: { id: service.served_by_id },
        data: {
          salary: { decrement: commissionAmount },
        },
      });

      const updatedService = await tx.availedService.update({
        where: { id: serviceId },
        data: {
          status: AvailedServiceStatus.CLAIMED,
          served_at: null,
          completed_at: null,
        },
      });

      if (service.booking.status === BookingStatus.COMPLETED) {
        await tx.booking.update({
          where: { id: service.booking_id },
          data: { status: BookingStatus.ACCEPTED },
        });
      }

      return updatedService;
    });

    revalidatePath(`/app/${businessSlug}`);
    return { success: true, data: result };
  } catch (error) {
    const e = error as { code?: string };
    if (e.code === "P2025") {
      return { success: false, error: "Unable to unserve." };
    }
    console.error("Error unserving service:", error);
    return { success: false, error: "Unable to unserve." };
  }
}

