"use server";

import { prisma } from "@/prisma/prisma";
import { Prisma } from "@/prisma/generated/prisma/client";
import { requireAuth } from "@/lib/auth/guards";
import { revalidatePath } from "next/cache";
import { hash } from "bcryptjs";
import crypto from "crypto";
import { headers } from "next/headers";
import { sendEmployeeInviteEmail } from "@/lib/services/employee-invite";

// Get all employees for a business
export async function getEmployeesAction() {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const employees = await prisma.employee.findMany({
      where: {
        business: {
          slug: businessSlug,
        },
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
            role: true,
            created_at: true,
            updated_at: true,
          },
        },
        attendance: {
          orderBy: { date: "desc" },
          take: 7,
        },
        payslips: {
          orderBy: { ending_date: "desc" },
          take: 1,
        },
        _count: {
          select: {
            served_services: true,
          },
        },
      },
      orderBy: {
        user: {
          name: "asc",
        },
      },
    });

    return { success: true, data: employees };
  } catch (error) {
    console.error("Failed to get employees:", error);
    return { success: false, error: "Failed to fetch employees" };
  }
}

// Create a new employee (also creates a user account)
export async function createEmployeeAction(data: {
  name: string;
  email: string;
  daily_rate: number;
  commission_percentage: number;
  specialties?: string[];
}) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    // Check if email already exists
    const existingUser = await prisma.user.findUnique({
      where: { email: data.email },
    });

    if (existingUser) {
      return { success: false, error: "Email already in use" };
    }

    const business = await prisma.business.findUnique({
      where: { slug: businessSlug },
    });

    if (!business) {
      return { success: false, error: "Business not found" };
    }

    const tempPassword = crypto
      .randomBytes(12)
      .toString("base64")
      .replace(/[^a-zA-Z0-9]/g, "")
      .slice(0, 12);
    const hashedPassword = await hash(tempPassword, 12);
    const tempPasswordExpiresAt = new Date(
      Date.now() + 24 * 60 * 60 * 1000,
    );

    // Create user and employee in a transaction
    const result = await prisma.$transaction(
      async (tx: Prisma.TransactionClient) => {
        const user = await tx.user.create({
          data: {
            name: data.name,
            email: data.email,
            hashed_password: hashedPassword,
            role: "EMPLOYEE",
            must_change_password: true,
            temp_password_expires_at: tempPasswordExpiresAt,
          },
        });

        const employee = await tx.employee.create({
          data: {
            user_id: user.id,
            business_id: business.id,
            salary: 0, // Legacy field
            daily_rate: data.daily_rate,
            commission_percentage: data.commission_percentage,
            specialties: data.specialties ?? [],
          },
          include: {
            user: true,
          },
        });

        return employee;
      },
    );

    const headersList = await headers();
    const host = headersList.get("host") || "localhost:3000";
    const protocol = headersList.get("x-forwarded-proto") || "http";
    const baseUrl = `${protocol}://${host}`;
    const changePasswordUrl = `${baseUrl}/app/${businessSlug}/change-password`;

    const emailResult = await sendEmployeeInviteEmail({
      to: data.email,
      employeeName: data.name,
      businessName: business.name,
      tempPassword,
      expiresAt: tempPasswordExpiresAt,
      changePasswordUrl,
    });

    revalidatePath(`/app/${businessSlug}/employees`);

    if (!emailResult.success) {
      console.error("Failed to send employee invite email:", emailResult.error);
      return {
        success: true,
        data: result,
        warning: "Employee created but email failed to send.",
      };
    }

    return { success: true, data: result };
  } catch (error) {
    console.error("Failed to create employee:", error);
    return { success: false, error: "Failed to create employee" };
  }
}

// Update an employee
export async function updateEmployeeAction(
  employeeId: number,
  data: {
    name?: string;
    email?: string;
    daily_rate?: number;
    commission_percentage?: number;
    specialties?: string[];
  },
) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const employee = await prisma.employee.findUnique({
      where: { id: employeeId },
      include: { user: true },
    });

    if (!employee) {
      return { success: false, error: "Employee not found" };
    }

    // Update user and employee
    await prisma.$transaction(async (tx: Prisma.TransactionClient) => {
      if (data.name || data.email) {
        await tx.user.update({
          where: { id: employee.user_id },
          data: {
            ...(data.name && { name: data.name }),
            ...(data.email && { email: data.email }),
          },
        });
      }

      await tx.employee.update({
        where: { id: employeeId },
        data: {
          ...(data.daily_rate !== undefined && { daily_rate: data.daily_rate }),
          ...(data.commission_percentage !== undefined && {
            commission_percentage: data.commission_percentage,
          }),
          ...(data.specialties !== undefined && {
            specialties: data.specialties,
          }),
        },
      });
    });

    revalidatePath(`/app/${businessSlug}/employees`);
    return { success: true };
  } catch (error) {
    console.error("Failed to update employee:", error);
    return { success: false, error: "Failed to update employee" };
  }
}

// Delete an employee
export async function deleteEmployeeAction(employeeId: number) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const employee = await prisma.employee.findUnique({
      where: { id: employeeId },
    });

    if (!employee) {
      return { success: false, error: "Employee not found" };
    }

    // Delete employee (user is linked, decide if you want to delete user too)
    // For now, we'll delete the employee record only
    await prisma.employee.delete({
      where: { id: employeeId },
    });

    revalidatePath(`/app/${businessSlug}/employees`);
    return { success: true };
  } catch (error) {
    console.error("Failed to delete employee:", error);
    return { success: false, error: "Failed to delete employee" };
  }
}

// Reset employee password
export async function resetEmployeePasswordAction(
  employeeId: number,
  newPassword: string,
) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { businessSlug } = auth;

  try {
    const employee = await prisma.employee.findUnique({
      where: { id: employeeId },
    });

    if (!employee) {
      return { success: false, error: "Employee not found" };
    }

    const hashedPassword = await hash(newPassword, 12);

    await prisma.user.update({
      where: { id: employee.user_id },
      data: { hashed_password: hashedPassword },
    });

    return { success: true };
  } catch (error) {
    console.error("Failed to reset password:", error);
    return { success: false, error: "Failed to reset password" };
  }
}

"use server";

import { prisma } from "@/prisma/prisma";
import {
  AttendanceStatus,
  AvailedServiceStatus,
  BookingStatus,
  PayslipStatus,
} from "@/prisma/generated/prisma/client";
import { getStartOfDayPH } from "@/lib/date-utils";
import { revalidatePath } from "next/cache";
import { requireAuth } from "@/lib/auth/guards";
import { Role } from "@/prisma/generated/prisma/enums";

export async function getPayslipDataAction(employeeId: number) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { session, businessSlug } = auth;

  // 1. Employee Self-Check
  if (session.user.role === Role.EMPLOYEE) {
    const userEmployee = await prisma.employee.findUnique({
      where: { user_id: session.user.id },
    });
    if (!userEmployee || userEmployee.id !== employeeId) {
      return {
        success: false,
        error: "Unauthorized: You can only view your own payslips.",
      };
    }
  }

  try {
    const employee = await prisma.employee.findUnique({
      where: { id: employeeId },
      include: {
        user: true,
        business: { select: { slug: true } },
      },
    });

    if (!employee) throw new Error("Employee not found");

    // 2. Owner Business Check
    if (employee.business.slug !== businessSlug) {
      return {
        success: false,
        error: "Unauthorized: Employee belongs to another business.",
      };
    }

    const lastPayslip = await prisma.payslip.findFirst({
      where: { employee_id: employeeId },
      orderBy: { ending_date: "desc" },
    });

    const startingDate = lastPayslip
      ? lastPayslip.ending_date
      : new Date(getStartOfDayPH(employee.user.created_at).getTime() - 1);

    const { getEndOfDayPH } = await import("@/lib/date-utils");
    const endingDate = getEndOfDayPH(new Date());

    const attendanceRecords = await prisma.employeeAttendance.findMany({
      where: {
        employee_id: employeeId,
        date: {
          gt: startingDate,
          lte: endingDate,
        },
        OR: [
          { status: AttendanceStatus.PRESENT },
          { status: AttendanceStatus.LEAVE, is_paid_leave: true },
        ],
      },
    });

    const daysPresent = attendanceRecords.filter(
      (r) => r.status === AttendanceStatus.PRESENT,
    ).length;
    const paidLeaveDays = attendanceRecords.filter(
      (r) => r.status === AttendanceStatus.LEAVE && r.is_paid_leave,
    ).length;

    const basicSalary = (daysPresent + paidLeaveDays) * employee.daily_rate;

    const commissionServices = await prisma.availedService.findMany({
      where: {
        served_by_id: employeeId,
        status: AvailedServiceStatus.COMPLETED,
        completed_at: {
          gt: startingDate,
          lte: endingDate,
        },
      },
      select: {
        id: true,
        commission_base: true,
        price: true,
        package_id: true,
        package: {
          select: {
            name: true,
          },
        },
        service: {
          select: {
            name: true,
          },
        },
      },
    });

    let commissionTotal = 0;
    commissionServices.forEach((service) => {
      const base = service.commission_base ?? service.price;
      commissionTotal += (base * employee.commission_percentage) / 100;
    });

    const totalSalary = basicSalary + commissionTotal;

    return {
      success: true,
      data: {
        employee: {
          id: employee.id,
          name: employee.user.name,
          daily_rate: employee.daily_rate,
          commission_percentage: employee.commission_percentage,
        },
        period: {
          start: startingDate,
          end: endingDate,
        },
        breakdown: {
          days_present: daysPresent,
          paid_leave_days: paidLeaveDays,
          attendance_dates: attendanceRecords.map((r) => r.date),
          basic_salary: basicSalary,
          commission_services_count: commissionServices.length,
          commission_services: commissionServices,
          commission_total: commissionTotal,
        },
        total_salary: totalSalary,
      },
    };
  } catch (error) {
    console.error("Error calculating payslip:", error);
    return { success: false, error: "Failed to calculate payslip" };
  }
}

export async function createPayslipAction(data: {
  employeeId: number;
  startingDate: Date;
  endingDate: Date;
  daysPresent: number;
  totalSalary: number;
  deduction?: number;
  comment?: string;
}) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { session, businessSlug } = auth;

  // STRICT: Only Owners can generate payslips
  if (session.user.role !== Role.OWNER) {
    return {
      success: false,
      error: "Unauthorized: Only owners can create payslips.",
    };
  }

  try {
    // Verify employee belongs to business
    const employee = await prisma.employee.findUnique({
      where: { id: data.employeeId },
      select: { business: { select: { slug: true } } },
    });

    if (!employee || employee.business.slug !== businessSlug) {
      return {
        success: false,
        error: "Unauthorized operation on employee from another business.",
      };
    }
    const payslip = await prisma.payslip.create({
      data: {
        employee_id: data.employeeId,
        starting_date: data.startingDate,
        ending_date: data.endingDate,
        total_salary: data.totalSalary,
        deduction: data.deduction || 0,
        comment: data.comment,
        status: PayslipStatus.PAID,
      },
    });

    revalidatePath("/app/[businessSlug]/payroll");
    return { success: true, data: payslip };
  } catch (error) {
    console.error("Error creating payslip:", error);
    return { success: false, error: "Failed to create payslip" };
  }
}

export async function getPayslipHistoryAction(employeeId: number) {
  const auth = await requireAuth();
  if (!auth.success) return auth;
  const { session, businessSlug } = auth;

  if (session.user.role === Role.EMPLOYEE) {
    const userEmployee = await prisma.employee.findUnique({
      where: { user_id: session.user.id },
    });
    if (!userEmployee || userEmployee.id !== employeeId) {
      return { success: false, error: "Unauthorized" };
    }
  }

  // Cross-tenant check
  const employee = await prisma.employee.findUnique({
    where: { id: employeeId },
    select: { business: { select: { slug: true } } },
  });
  if (!employee || employee.business.slug !== businessSlug) {
    return { success: false, error: "Unauthorized" };
  }

  try {
    const payslips = await prisma.payslip.findMany({
      where: { employee_id: employeeId },
      orderBy: { ending_date: "desc" },
    });
    return { success: true, data: payslips };
  } catch (error) {
    console.error("Error fetching payslip history:", error);
    return { success: false, error: "Failed to fetch payslip history" };
  }
}

import { prisma } from "@/prisma/prisma";
import { Resend } from "resend";
import { getStartOfDayPH, getEndOfDayPH } from "@/lib/date-utils";
import { logger } from "@/lib/logger";

export async function sendFlowReminders() {
  const resend = new Resend(process.env.RESEND_API_KEY);

  try {
    // Use Philippine timezone for "today"
    const today = new Date();

    // 1. Get all active service flows
    const activeFlows = await prisma.serviceFlow.findMany({
      include: {
        trigger_service: true,
        suggested_service: true,
      },
    });

    logger.info(
      `[Flow Reminders] Found ${activeFlows.length} configured flows.`,
    );

    let sentCount = 0;

    // 2. For each flow, find bookings that trigger it today
    for (const flow of activeFlows) {
      // Calculate the "completed_at" date we are looking for.
      // If we want to send reminder TODAY, the booking must have happened X days ago.
      // So target_completion_date = today - delay
      // BUT calculating exact date subtraction is tricky with variable units.
      // Instead, let's fetch recent completed bookings and filter in memory or find a range.
      // A better approach for scalability: Find bookings completed roughly X time ago.

      // Let's use a wide range query for bookings of the trigger service
      // Better Query: Find "AvailedService" items where service_id == trigger_service_id
      // and status == COMPLETED.

      // Optimization: Calculate the specific date range based on the unit
      let targetDateStart: Date;
      let targetDateEnd: Date;

      // We want: completed_at + delay == today
      // So: completed_at == today - delay
      const lookbackDate = today;

      // Helper to subtract time (now timezone-aware)
      const subtractDelay = (
        date: Date,
        amount: number,
        unit: "DAYS" | "WEEKS" | "MONTHS",
      ) => {
        const d = new Date(date);
        if (unit === "DAYS") d.setDate(d.getDate() - amount);
        if (unit === "WEEKS") d.setDate(d.getDate() - amount * 7);
        if (unit === "MONTHS") d.setMonth(d.getMonth() - amount);
        return d;
      };

      const checkDate = subtractDelay(
        lookbackDate,
        flow.delay_duration,
        flow.delay_unit,
      );

      // Use Philippine timezone for start/end of day
      const startOfCheck = getStartOfDayPH(checkDate);
      const endOfCheck = getEndOfDayPH(checkDate);

      const qualifyingServices = await prisma.availedService.findMany({
        where: {
          service_id: flow.trigger_service_id,
          // Use 'completed_at' if available, otherwise 'served_at' or 'created_at' as fallback
          // Assuming 'completed_at' is populated for completed services
          OR: [
            { completed_at: { gte: startOfCheck, lte: endOfCheck } },
            // Fallback: if served_at matches date
            {
              completed_at: null,
              served_at: { gte: startOfCheck, lte: endOfCheck },
            },
          ],
          status: "COMPLETED",
          booking: {
            customer: {
              email: { not: null },
            },
          },
        },
        include: {
          booking: {
            include: {
              customer: true,
              business: true,
            },
          },
        },
      });

      logger.info(
        `[Flow Reminders] Flow ${flow.id}: Found ${qualifyingServices.length} qualifying services from ${checkDate.toDateString()}`,
      );

      for (const item of qualifyingServices) {
        if (!item.booking.customer.email) continue;

        const customerName = item.booking.customer.name;
        const businessName = item.booking.business.name;
        const serviceName = flow.trigger_service.name;
        const nextServiceName = flow.suggested_service.name;

        // Create a slug-like booking link (or use your actual booking URL logic here)
        const bookingLink = `https://serviceflow.store/${businessName
          .toLowerCase()
          .replace(/\s+/g, "-")}`;

        const isRequired = flow.type === "REQUIRED";

        // 1. Dynamic Subject Line (No Emojis)
        const subject = isRequired
          ? `Action Required: Next step for your ${serviceName}`
          : `Recommendation: Try ${nextServiceName} at ${businessName}`;

        // 2. Dynamic Body Text
        // We keep this clean text, letting the HTML handle the bolding/layout
        const introText = isRequired
          ? `It is time for the next step in your treatment process. To ensure the best results, please book your follow-up service soon.`
          : `Based on your recent visit for ${serviceName}, we highly recommend trying this follow-up service. It is the perfect addition to your routine!`;

        // 3. Dynamic Labels for the Green Box
        const boxLabel = isRequired ? "ACTION REQUIRED" : "RECOMMENDED FOR YOU";
        const boxSubText = isRequired ? "Due for booking" : "perfect follow-up";

        // 4. The HTML Template (Matches the "BeautyFeel" Screenshot)
        const emailHtml = `
            <!DOCTYPE html>
            <html lang="en">
            <head>
              <meta charset="utf-8">
              <meta name="viewport" content="width=device-width, initial-scale=1.0">
              <title>${subject}</title>
              <style>
                /* Reset & Base */
                body { margin: 0; padding: 0; background-color: #ffffff; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif; -webkit-font-smoothing: antialiased; color: #1f2937; }
                table { border-spacing: 0; width: 100%; }
                td { padding: 0; }
                img { border: 0; }
                a { text-decoration: none; color: inherit; }
                
                /* Layout */
                .wrapper { width: 100%; background-color: #ffffff; padding-bottom: 40px; }
                .main-container { max-width: 600px; margin: 0 auto; background-color: #ffffff; border: 1px solid #f3f4f6; border-radius: 8px; overflow: hidden; }
                
                /* Header (Navy Blue) */
                .header { background-color: #111827; padding: 40px 20px; text-align: center; }
                .business-name { color: #ffffff; font-size: 24px; font-weight: 700; margin: 0; letter-spacing: -0.5px; }
                
                /* Content */
                .content-body { padding: 40px 30px; }
                .greeting { font-size: 16px; color: #111827; margin: 0 0 16px 0; font-weight: 700; }
                .greeting-name { color: #10b981; } 
                .text-paragraph { color: #4b5563; font-size: 15px; line-height: 1.6; margin-bottom: 30px; margin-top: 0; }
                
                /* The Green "Info" Box */
                .highlight-box { background-color: #ecfdf5; border-radius: 8px; padding: 30px 20px; text-align: center; margin-bottom: 40px; border: 1px solid #d1fae5; }
                .box-label { color: #059669; font-size: 11px; letter-spacing: 1.5px; font-weight: 700; text-transform: uppercase; margin-bottom: 10px; display: block; }
                .box-main-text { color: #111827; font-size: 24px; font-weight: 800; margin: 0; letter-spacing: -0.5px; line-height: 1.2; }
                .box-sub-text { color: #059669; font-size: 13px; font-weight: 600; margin-top: 8px; display: block; }
                
                /* List Section */
                .section-divider { border-top: 1px dashed #e5e7eb; margin-bottom: 25px; }
                .section-label { color: #6b7280; font-size: 11px; letter-spacing: 1px; font-weight: 700; text-transform: uppercase; margin-bottom: 15px; display: block; }
                
                .action-item { display: block; margin-bottom: 10px; }
                .dot { height: 8px; width: 8px; background-color: #10b981; border-radius: 50%; display: inline-block; margin-right: 12px; vertical-align: middle; }
                .action-text { color: #111827; font-size: 14px; font-weight: 600; vertical-align: middle; }
                
                /* Footer */
                .footer { background-color: #f9fafb; padding: 24px; text-align: center; border-top: 1px solid #f3f4f6; }
                .footer-text { color: #9ca3af; font-size: 11px; margin-bottom: 6px; line-height: 1.5; }
                .brand-link { color: #2563eb; text-decoration: none; font-weight: 600; }
              </style>
            </head>
            <body>
              <div class="wrapper">
                <div class="main-container">
                  
                  <!-- Dark Header -->
                  <div class="header">
                    <h1 class="business-name">${businessName}</h1>
                  </div>

                  <!-- Main Body -->
                  <div class="content-body">
                    
                    <!-- Greeting -->
                    <div class="greeting">
                      Hello, <span class="greeting-name">${customerName}</span>
                    </div>

                    <p class="text-paragraph">
                      ${introText}
                    </p>

                    <!-- The Green Highlight Box (Clickable) -->
                    <a href="${bookingLink}" style="text-decoration: none; display: block;">
                      <div class="highlight-box">
                        <span class="box-label">${boxLabel}</span>
                        <div class="box-main-text">${nextServiceName}</div>
                        <span class="box-sub-text">Click to Book Now</span>
                      </div>
                    </a>

                    <!-- Divider -->
                    <div class="section-divider"></div>

                    <!-- Previous Context Section -->
                    <span class="section-label">BASED ON PREVIOUS VISIT</span>
                    
                    <div class="action-item">
                      <span class="dot"></span>
                      <span class="action-text">${serviceName}</span>
                    </div>
                    <div style="font-size: 12px; color: #6b7280; padding-left: 20px;">
                      Completed on ${item.completed_at?.toLocaleDateString() ?? "recently"}
                    </div>
                    
                  </div>

                  <!-- Footer -->
                  <div class="footer">
                    <div class="footer-text">
                      Need help? Contact ${businessName} directly.
                    </div>
                    <div class="footer-text">
                      &copy; ${new Date().getFullYear()} ${businessName} &middot; Powered by <a href="https://www.serviceflow.store" class="brand-link">ServiceFlow</a>
                    </div>
                  </div>

                </div>
              </div>
            </body>
            </html>
         `;

        const { error } = await resend.emails.send({
          from: "ServiceFlow <reminders@serviceflow.store>",
          to: [item.booking.customer.email],
          subject: subject,
          html: emailHtml,
        });

        if (error) {
          logger.error(
            `[Flow Reminders] Failed to send to ${item.booking.customer.email}`,
            { error },
          );
        } else {
          sentCount++;

          // Track this reminder in OutboxMessage for dashboard visibility
          await prisma.outboxMessage.create({
            data: {
              event_type: "FLOW_REMINDER_SENT",
              aggregate_type: "ServiceFlow",
              aggregate_id: flow.id,
              business_id: item.booking.business_id,
              payload: {
                customerId: item.booking.customer_id,
                customerName,
                customerEmail: item.booking.customer.email,
                triggerServiceName: serviceName,
                suggestedServiceName: nextServiceName,
                flowType: flow.type,
                sentAt: new Date().toISOString(),
              },
              processed: true,
              processed_at: new Date(),
            },
          });
        }
      }
    }

    return {
      success: true,
      flows_checked: activeFlows.length,
      emails_sent: sentCount,
    };
  } catch (error) {
    logger.error("[Flow Reminders] Error:", { error });
    throw error;
  }
}

import { prisma } from "@/prisma/prisma";
import { Resend } from "resend";
import { getStartOfDayPH } from "@/lib/date-utils";
import { logger } from "@/lib/logger";

export async function sendReEngagementEmails() {
  const resend = new Resend(process.env.RESEND_API_KEY);

  try {
    // Get current date in Philippine timezone
    const nowPH = getStartOfDayPH(new Date());
    // Calculate 60 days ago in Philippine timezone
    const sixtyDaysAgo = new Date(nowPH.getTime() - 60 * 24 * 60 * 60 * 1000);

    // Get all businesses
    const businesses = await prisma.business.findMany({
      select: {
        id: true,
        name: true,
        slug: true,
      },
    });

    logger.info(`[Re-engagement] Processing ${businesses.length} businesses`);
    logger.info(
      `[Re-engagement] Cutoff date (60 days ago PH time): ${sixtyDaysAgo.toISOString()}`,
    );

    const allResults = [];

    for (const business of businesses) {
      // Find customers who:
      // 1. Have at least one COMPLETED booking
      // 2. Their most recent booking was more than 60 days ago
      // 3. Have a valid email address
      const inactiveCustomers = await prisma.customer.findMany({
        where: {
          business_id: business.id,
          email: {
            not: null,
          },
          bookings: {
            some: {
              status: "COMPLETED", // Must have at least one completed booking
            },
          },
        },
        include: {
          bookings: {
            where: {
              status: "COMPLETED",
            },
            orderBy: {
              created_at: "desc",
            },
            take: 1,
          },
        },
      });

      logger.info(
        `[Re-engagement] Business "${business.name}": Found ${inactiveCustomers.length} customers with completed bookings`,
      );

      // Filter customers whose last booking was 60+ days ago
      const customersToEmail = inactiveCustomers.filter((customer) => {
        if (customer.bookings.length === 0) return false;
        const lastBooking = customer.bookings[0];
        const isInactive = lastBooking.created_at <= sixtyDaysAgo;

        if (process.env.NODE_ENV !== "production") {
          logger.debug(
            `[Re-engagement] Customer ${customer.name} (${customer.email}): Last booking ${lastBooking.created_at.toISOString()}, 60 days ago: ${sixtyDaysAgo.toISOString()}, Inactive: ${isInactive}`,
          );
        }

        return isInactive;
      });

      logger.info(
        `[Re-engagement] Business "${business.name}": ${customersToEmail.length} customers inactive for 60+ days`,
      );

      // Send emails
      for (const customer of customersToEmail) {
        if (!customer.email) continue;

        const lastBookingDate = customer.bookings[0]?.created_at;
        const emailHtml = generateReEngagementEmail(
          business.name,
          customer.name,
        );

        try {
          const { data, error } = await resend.emails.send({
            from: "ServiceFlow <reminders@serviceflow.store>",
            to: [customer.email],
            subject: `We Miss You at ${business.name}!`,
            html: emailHtml,
          });

          if (error) {
            logger.error(
              `[Re-engagement] Failed to send email to ${customer.email}:`,
              { error },
            );
            allResults.push({
              business: business.name,
              customerId: customer.id,
              customerEmail: customer.email,
              success: false,
              error,
            });
          } else {
            logger.info(
              `[Re-engagement] Email sent to ${customer.email} (last booking: ${lastBookingDate?.toISOString()})`,
            );
            allResults.push({
              business: business.name,
              customerId: customer.id,
              customerEmail: customer.email,
              success: true,
            });
          }
        } catch (emailError) {
          logger.error(`[Re-engagement] Error sending to ${customer.email}:`, {
            emailError,
          });
          allResults.push({
            business: business.name,
            customerId: customer.id,
            customerEmail: customer.email,
            success: false,
            error: emailError,
          });
        }
      }
    }

    const successCount = allResults.filter((r) => r.success).length;
    const failCount = allResults.filter((r) => !r.success).length;

    logger.info(
      `[Re-engagement] Summary: ${successCount} sent, ${failCount} failed`,
    );

    return {
      success: true,
      totalProcessed: allResults.length,
      sent: successCount,
      failed: failCount,
      details: allResults,
    };
  } catch (error) {
    logger.error("[Re-engagement] Service error:", { error });
    throw error;
  }
}

